import React from 'react';

import NavigationHeader from '@src/routes/Settings/components/NavigationHeader';
// tslint:disable-next-line:max-line-length
import IntegrationCell from '@src/routes/Settings/routes/Integrations/routes/List/components/IntegrationCell';

import SalesforceLogo from '@src/components/Icons/SalesforceLogo.svg';
import SlackLogo from '@src/components/Icons/Slack.svg';
import HubspotLogo from '@src/components/Icons/Hubspot.svg';
import ZoomLogo from '@src/components/Icons/Zoom.svg';
import AsanaLogo from '@src/assets/Asana.png';

const salesforceIcon = <SalesforceLogo />;
const asanaIcon = <img className="asana-icon" src={AsanaLogo} />;

import './List.scss';

export interface Props {
  salesforceEnabled: boolean;
  asanaEnabled: boolean;
  slackEnabled: boolean;
  zoomEnabled: boolean;
  hubspotEnabled: boolean;
  isMobile?: boolean;
  collapsedMobile?: boolean;
}

export interface DispatchProps {
  collapseLsb: (collapse: boolean) => void;
}

const List: React.SFC<Props & DispatchProps> = (props: Props & DispatchProps) => (
  <div className="settings-panel integration-list">
    <NavigationHeader
      title="Integrations"
      isMobile={props.isMobile}
      collapsedMobile={props.collapsedMobile}
      collapseLsb={props.collapseLsb}
    />
    <div className="description">
      Connect Polymail with the tools you already use to
      get more work done without switching between different apps.
    </div>
    <IntegrationCell
      icon={<SlackLogo />}
      name="Slack"
      description="Share conversations from Polymail directly to Slack"
      installed={props.slackEnabled}
      href="/settings/integrations/slack"
    />
    <IntegrationCell
      icon={salesforceIcon}
      name="Salesforce"
      description="Display, create, and update Salesforce records in Polymail"
      installed={props.salesforceEnabled}
      href="/settings/integrations/salesforce"
    />
    <IntegrationCell
      icon={asanaIcon}
      name="Asana"
      description="Create and view Asana tasks from conversations in Polymail"
      installed={props.asanaEnabled}
      href="/settings/integrations/asana"
    />
    <IntegrationCell
      icon={<ZoomLogo className="zoom-icon" width="36px" />}
      name="Zoom"
      description="Generate unique Zoom meeting urls when events are booked on your calendar"
      installed={props.zoomEnabled}
      href="/settings/integrations/zoom"
    />
    <IntegrationCell
      icon={<HubspotLogo width="34px" className="hubspot-icon" />}
      name="Hubspot"
      description="View Hubspot details for a contact in the Right Sidebar"
      installed={props.hubspotEnabled}
      href="/settings/integrations/hubspot"
    />
  </div>
);

export default List;
