export function createHotkeyMiddleware(): Middleware {
  return (api: MiddlewareAPI<any>) =>
    (next: Dispatch<StoreState>) =>
      (action: any): any => {
        const state = api.getState() as StoreState;
        if (state.preferences.hotkeys !== hotkeyPreference) {
          setHotkeyPreference(state.preferences.hotkeys);
        }

        if (state.preferences.sendAndArchive !== sendAndArchivePreference) {
          setSendAndArchivePreference(state.preferences.sendAndArchive);
        }

        if (state.accounts !== accounts) {
          setAccounts(state.accounts);
          setDynamicHotkeys();
        }

        if (state.lists !== lists) {
          setLists(state.lists);
          setDynamicHotkeys();
        }

        if (selectHubspotEnabled(state) && !hubspotHotkeysEnabled) {
          setHubspotHotkeys();
          setDynamicHotkeys();
        }

        return next(action);
      };
}

let hotkeyPreference: Hotkeys = Hotkeys.Default;
let sendAndArchivePreference: boolean = false;
let dynamicHotkeys: Hotkey[] = [];
let accounts: Account[] = [];
let lists: List[] = [];
let hubspotHotkeysEnabled = false;

export function setDynamicHotkeys() {
  const hubspotHotkeySet = hubspotHotkeysEnabled && hubspotHotkeys() || [];
  const accountHotkeySet = accountNavigationHotkeys();
  const listHotkeySet = labelNavigationHotkeys();
  dynamicHotkeys = accountHotkeySet
    .concat(listHotkeySet)
    .concat(hubspotHotkeySet)
    .concat(applicationNavigationHotkeys());
  return;
}

export function setHubspotHotkeys() {
  getHotkeySet.cache.clear();
  getHotkeySet.cache.clear();
  hotkeysTypeahead.cache.clear();
  hubspotHotkeysEnabled = true;
}

export function setAccounts(newAccounts: Account[]) {
  if (newAccounts !== accounts) {
    accounts = newAccounts;
    getHotkeySet.cache.clear();
    getHotkey.cache.clear();
    hotkeysTypeahead.cache.clear();
  }
}

export function setLists(newLists: List[]) {
  if (newLists !== lists) {
    lists = newLists;
    getHotkeySet.cache.clear();
    getHotkey.cache.clear();
    hotkeysTypeahead.cache.clear();
  }
}

export function setHotkeyPreference(preference: Hotkeys) {
  if (preference !== hotkeyPreference) {
    hotkeyPreference = preference;
    getHotkeySet.cache.clear();
    getHotkey.cache.clear();
    hotkeysTypeahead.cache.clear();
  }
}

export function setSendAndArchivePreference(preference: boolean) {
  if (preference !== sendAndArchivePreference) {
    sendAndArchivePreference = preference;
    getHotkeySet.cache.clear();
    getHotkey.cache.clear();
    hotkeysTypeahead.cache.clear();
  }
}

