memoize.Cache = Map;
export const getHotkeySet = memoize(
  (type: Hotkeys): Array<Hotkey & HotkeyCode | Hotkey> => {
    let codes: { [  action: string]: HotkeyCode } = cloneDeep(DEFAULT_CODES);
    if (type === Hotkeys.Gmail) {
      codes = cloneDeep(GMAIL_CODES);
    }

    // Set Send and Archive preferences if needed
    if (sendAndArchivePreference) {
      codes[HotkeyAction.SendAndArchive] = {
        metaKey: true,
        keyCode: KeyCode.Enter,
        text: '⌘ + ↵',
      };

      codes[HotkeyAction.Send] = {
        metaKey: true,
        altKey: true,
        keyCode: KeyCode.Enter,
        text: '⌘ + ⌥ + ↵',
      };
    }

    return HOTKEYS.map((h: Hotkey) => {
      if (!codes[h.name]) {
        return h;
      }

      if (IS_MAC) {
        return { ...h, ...codes[h.name] };
      }
      const code = codes[h.name];

      if (code.metaKey) {
        code.ctrlKey = true;
        delete code.metaKey;
        code.text.replace('⌘', 'ctrl');
      }

      return {
        ...h,
        ...code,
      };
    })
    .concat(dynamicHotkeys);
  },
  (type: Hotkeys) => type,
);

export const getHotkey = memoize(
  (e: KeyboardEvent | React.KeyboardEvent<HTMLElement>):
  Hotkey & HotkeyCode | Hotkey | undefined => {
    let matches = getHotkeySet(hotkeyPreference).filter(
      (value: Hotkey & HotkeyCode) =>
        !!value.keyCode && value.keyCode === e.keyCode,
    );

    if (matches.length) {
      matches = matches.filter((match: Hotkey & HotkeyCode) => {
        if (
          !!match.metaKey === e.metaKey &&
          !!match.ctrlKey === e.ctrlKey &&
          !!match.altKey === e.altKey &&
          !!match.shiftKey === e.shiftKey &&
          (match.keyCode === e.keyCode || match.altKeyCode === e.keyCode)) {
          return true;
        }
        return false;
      });

      if (matches.length) {
        return matches[0];
      }
    }

    return undefined;
  },
  (event: KeyboardEvent | React.KeyboardEvent<HTMLElement>) =>
    (event.metaKey ? 'm' : '') + (event.ctrlKey ? 'c' : '') +
    (event.altKey ? 'a' : '') + (event.shiftKey ? 's' : '') + event.keyCode,
);

export const getHotkeyByName = memoize(
  (name: string): Hotkey | undefined => {
    const matches = getHotkeySet(hotkeyPreference).filter((value: Hotkey) => value.name === name);
    if (matches.length) {
      return matches[0];
    }

    return undefined;
  },
  (name: string) => name,
);

export const hotkeysTypeahead = memoize(
  (partial: string): Array<Hotkey & HotkeyCode | Hotkey> => {
    const query = partial.trim().toLowerCase();
    const regex = typeaheadRegex(query);

    const matches = getHotkeySet(hotkeyPreference).filter((value: Hotkey & HotkeyCode) => {
      if (value.name.toLowerCase().match(regex)) {
        return true;
      }

      if (!!value.text && value.text.toLowerCase().search(regex) >= 0) {
        return true;
      }

      return false;
    });

    return sortHotkeyMatches(matches, query, 'name');
  },
  (partial: string) => partial,
);

export const checkNextHotkeyChange = (
  name: HotkeyAction,
  next: HotkeyActions,
  old: HotkeyActions,
) => {
  const nextValue = next[name] || 0;
  const oldValue = old[name] || 0;
  return nextValue > oldValue;
};

// Check if primariy modifier key for keyboard event
export const isPrimaryModifierKey = (event:
  React.KeyboardEvent<any>|KeyboardEvent|React.MouseEvent<any>,
) => {
  return IS_MAC ? event.metaKey : event.ctrlKey;
};
