import { filter, findIndex, indexOf, cloneDeep, escape, uniqBy } from 'lodash';
import moment from 'moment-timezone';

import { mongoId } from './crypt';
import { computeThread } from '@src/lib/thread';
import { addressToEmail, parseAddress } from '@src/lib/address';
import { now } from '@src/lib/datetime';
import {
  Draft, Thread, Message, DraftStatus,
  Account, MessageBody, MessageAttachment,
  ThreadBox, Template, Preferences, Attachment,
  AccountType, Features, Commit, AttachmentMeta,
} from '@src/types';
import { accountSignature } from './account';
import { extractText, removeTrackingPixels, FakeDOM } from '@src/lib/html';
import { getDefaultWaiting } from '@src/lib/models/draft';
import { getQueryVariable } from '@src/lib/query';
import { truncate } from '@src/lib/attachment';
import { DraftSendMeta, DraftSendOptimisticMeta } from '@src/ducks/drafts';

export enum DraftSpecialError {
  ScheduledCancel = 'Scheduled draft canceled due to new reply.',
  VersionMismatch = 'draft version mismatch',
}

// patchedDraft is used when Draft is in sending/scheduled state
export const patchedDraft = (draft: Draft, cache: Draft): Draft => {
  const endAttachments = cache.attachments.reduce(
    (all, next) => {
      if (next.optimistic && findIndex(all, { id: next.id }) === -1) {
        return all.concat(next);
      }

      return all;
    },
    draft.attachments,
  );

  // NOTE(SHIN): Override body and subject since local updates
  return {
    ...draft,
    body: cache.body,
    subject: cache.subject,
    status: cache.status,
    cancelByTtl: cache.cancelByTtl,
    cancelBy: cache.cancelBy,
    attachments: endAttachments,
  };
};

// generateOptimisticSendMeta creates a send meta with all properties set, and the corresponding
// attachments to remove before sending
export const generateOptimisticSendMeta = (
  meta: DraftSendMeta,
  attachments: Attachment[],
): DraftSendOptimisticMeta => {

  const attachmentsAppliedDraft = attachments.reduce(
    (d, a) => applyAttachmentToDraft(d, a),
    meta.draft,
  );

  const sanitizedDraft = sanitizeDraft(attachmentsAppliedDraft);

  // Handle setting all optimistic properties
  // AttachmentsMetas -> Optimistic Attachments
  // CancelBy -> 30 seconds into future
  // CancelByTTL -> 30 seconds
  // Scheduled properties
  const cancelBy = now().add(30000, 'seconds');
  const stagedDraft: Draft = {
    ...sanitizedDraft,
    status: !!meta.scheduled ? DraftStatus.Scheduled : DraftStatus.Staged,
    scheduled: meta.scheduled ? meta.scheduled : null,
    cancelBy: cancelBy.toDate(),
    cancelByTtl: 30000,
    attachments: sanitizedDraft.attachments.concat(attachments),
  };

  const deleted = attachmentsToDelete(stagedDraft);
  const deletedIds = deleted.map(d => d.attachment);

  // New Send Meta from optimistic draft
  const sendMeta: DraftSendMeta = {
    ...meta,
    draft: {
      ...stagedDraft,
      attachments: stagedDraft.attachments
        .filter(a => deletedIds.indexOf(a.id) === -1),
    },
    toggleArchive: meta.toggleArchive,
    delay: meta.delay,
  };
  return {
    meta: sendMeta,
    deletedAttachments: deleted,
  };
};

// Helper function to determine if draft is in sending state
export const isDraftSending = (draft: Draft): boolean => {
  return [DraftStatus.Staged, DraftStatus.Sending].indexOf(draft.status) >= 0;
};

// Helper function for Staged/Sending/Failed drafts
export const isDraftOutbox = (draft: Draft): boolean => {
  return [DraftStatus.Staged, DraftStatus.Failed].indexOf(draft.status) >= 0;
};

export const failedDraft = (commit: Commit, draft: Draft, error: Error|undefined): Draft => {
  // Create failed draft
  return {
    ...draft,
    error: error && error.message || 'Failed to send draft',
    status: DraftStatus.Failed,
    attachments: draft.attachments.filter(a => !a.optimistic),
  };
};

export function defaultDraft(draft: Draft): Draft {
  const newDraft = {
    created: new Date('1970-01-01T00:00:00.000Z'),
    updated: new Date('1970-01-01T00:00:00.000Z'),
    subject: '',
    body: '',
    split: false,
    track: false,
    trackLinks: false,
    to: [],
    cc: [],
    bcc: [],
    attachments: [],
    version: 0,
    ...draft,
  };

  if (!(newDraft.created instanceof Date)) {
    newDraft.created = new Date(newDraft.created);
  }

  if (!(newDraft.updated instanceof Date)) {
    newDraft.updated = new Date(newDraft.updated);
  }

  if (newDraft.scheduled && !(newDraft.scheduled instanceof Date)) {
    newDraft.scheduled = new Date(newDraft.scheduled);
  }

  if (newDraft.cancelBy && !(newDraft.cancelBy instanceof Date)) {
    newDraft.cancelBy = new Date(newDraft.cancelBy);
  }

  // Apply cancelByTtl if provided
  if (newDraft.cancelByTtl !== undefined) {
    newDraft.cancelBy = moment().add(newDraft.cancelByTtl, 'ms').toDate();
    // NOTE: Draft spec says not to save this into cache
    newDraft.cancelByTtl = undefined;
  }

  if (newDraft.waiting && newDraft.waiting.when && !(newDraft.waiting.when instanceof Date)) {
    newDraft.waiting.when = new Date(newDraft.waiting.when);
  }

  return newDraft;
}

export const enum Configuration {
  New = 'new',
  Reply = 'reply',
  ReplyAll = 'replyAll',
  MoveBcc = 'moveBcc',
  Forward = 'forward',
  Comment = 'comment',
  MailTo = 'mailTo',
}

// Parses mailto body by breaking newlines into string
export const parseMailToBody = (body: string): string => {
  const container = FakeDOM().createElement('div');
  const newLineSeparatedBodyComponents = body.split(/\r?\n/);
  Array.from(newLineSeparatedBodyComponents).forEach((value, i) => {
    const node = FakeDOM().createTextNode(value);
    container.appendChild(node);
    if (i !== newLineSeparatedBodyComponents.length - 1) {
      container.appendChild(FakeDOM().createElement('br'));
    }
  });

  return container.innerHTML;
};

export const createMailToDraft = (
  account: Account, preferences: Preferences, features: Features, params: string,
): Draft => {
  const index = params.indexOf('?');
  // return new draft if no params
  if (index === -1) {
    return createNewDraft(account, preferences, features, [params].filter(c => !!c));
  }

  const search = params.slice(index);
  const id = mongoId();
  const createDate = constantDate && constantDate || new Date();
  const bodyVariable = getQueryVariable('body', search);
  const body = createMainDraftBody(
    id,
    preferences,
    account,
    features,
    bodyVariable && parseMailToBody(bodyVariable) || undefined,
  );
  const subject = getQueryVariable('subject', search) || '';
  const cc = (getQueryVariable('cc', search) || '').split(',').filter(c => !!c);
  const bcc = (getQueryVariable('bcc', search) || '').split(',').filter(c => !!c);
  const to = (params.slice(0, index) || '').split(',').filter(c => !!c)
    .map(c => decodeURIComponent(c));

  return {
    id,
    created: createDate,
    updated: createDate,
    status: DraftStatus.Draft,
    account: account.id,
    subject,
    attachments: [],
    body,
    to,
    cc,
    bcc: account.autoBcc ? bcc.concat([account.autoBcc]) : bcc,
    split: false,
    track: !preferences.disableTrackByDefault,
    trackLinks: preferences.linkTracking,
    waiting: preferences.followUp ? getDefaultWaiting(preferences.followUp) : undefined,
    version: 0,
    cancelOnReply: false,
  };
};

// Constructor for new draft (either new or mailto)
export const createNewDraft = (
  account: Account, preferences: Preferences, features: Features, forceRecipients?: string[],
): Draft => {

  // Close body and set signature
  const id = mongoId();
  const body = createMainDraftBody(id, preferences, account, features);
  const createDate = constantDate && constantDate || new Date();

  return {
    id,
    created: createDate,
    updated: createDate,
    status: DraftStatus.Draft,
    account: account.id,
    subject: '',
    attachments: [],
    body,
    to: forceRecipients && forceRecipients || [],
    cc: [],
    bcc: account.autoBcc && [account.autoBcc] || [],
    split: false,
    track: !preferences.disableTrackByDefault,
    trackLinks: preferences.linkTracking,
    waiting: preferences.followUp ? getDefaultWaiting(preferences.followUp) : undefined,
    version: 0,
    cancelOnReply: false,
  };
};

// TODO (Cindy): Clean this up
let constantDate: Date | undefined;
export const enableConstantDate = (date: Date) => {
  constantDate = date;
};

export const updateDraft = (draft: Draft) => {
  return {
    ...draft,
    waiting: draft.waiting && draft.waiting.whenRelative ?
        getDefaultWaiting(draft.waiting.whenRelative) :
        draft.waiting,
  };
};

export const generateSentFromPolymailHtmlDiv = (): HTMLDivElement => {
  const sentFromPolymailDiv = FakeDOM().createElement('div');

  // tslint:disable-next-line:max-line-length
  const anchorValue = `<a href="https://polymail.io/?utm_source=polymail&amp;utm_medium=referral&amp;utm_campaign=signature" target="_blank">Polymail</a>`;
  sentFromPolymailDiv.className = 'pm-sfp';
  sentFromPolymailDiv.innerHTML = 'Sent from&nbsp;' + anchorValue;
  sentFromPolymailDiv.style.fontSize = `10px`;
  sentFromPolymailDiv.style.color = '#7e8f9f';
  sentFromPolymailDiv.style.lineHeight = '1.8';
  return sentFromPolymailDiv;
};

//
export const createMainDraftBody = (
  draftId: string, preferences: Preferences,
  account: Account, features: Features, forceBody?: string,
): string => {
  const bodyContainer = FakeDOM().createElement('div');
  // Apply default preferences
  if (preferences.composeFontFamily !== '\\') {
    bodyContainer.style.fontFamily = preferences.composeFontFamily;
  }
  if (preferences.composeFontSize && preferences.composeFontSize !== 0) {
    bodyContainer.style.fontSize = `${preferences.composeFontSize}px`;
  }

  if (forceBody) {
    const innerBody = FakeDOM().createElement('div');
    innerBody.innerHTML = forceBody;
    bodyContainer.innerHTML = innerBody.outerHTML;
  }

  bodyContainer.appendChild(FakeDOM().createElement('br'));
  bodyContainer.appendChild(FakeDOM().createElement('br'));

  // Close body and set signature
  const signature = accountSignature({ account, draftId, preventEdits: true });
  let body = bodyContainer.outerHTML + signature;

  // Add Sent from Polymail
  if (!features.disableSentFromPolymail || preferences.sentFromPolymail === true) {

    // Conditional showing of sent with polymail
    const fakeNode = FakeDOM().createElement('div');
    fakeNode.innerHTML = signature;
    const signatureContent = fakeNode.textContent;
    if (!signatureContent || !signatureContent.toLowerCase().includes('polymail')) {

      // Append sent from Polymail
      body = body + generateSentFromPolymailHtmlDiv().outerHTML;
    }
  }

  return body;
};

export const createDraft = (
  thread: Thread,
  configuration: Configuration,
  messageBody: MessageBody,
  preferences: Preferences,
  accounts: Account[],
  features: Features,
  forceRecipients?: string[],
): Draft => {
  const messageIndex = findIndex(thread.messages, { id: messageBody.id });
  const message = thread.messages[messageIndex];
  const id = mongoId();
  const createDate = constantDate && constantDate || new Date();

  // Configures body
  const prependRePrefix = !message.subject.trim().toLowerCase().startsWith('re:');
  let body: string = '';
  let subject: string = '';

  // Set signature
  let account = accounts.filter(each => each.id === thread.account)[0] || accounts[0];
  const alias = checkForAlias(accounts, message);
  if (alias) {
    account = alias;
  }
  if (account) {
    body = createMainDraftBody(id, preferences, account, features);
  }

  // Set body
  switch (configuration) {
    case Configuration.New:
      body += '';
      subject = '';
      break;
    case Configuration.MoveBcc:
      const givenName = parseAddress(message.from).givenName;
      body = `<div class="pm-bcc-thx">Thanks ${givenName} (BCC)<br/><br /></div>`;
      body += formatReply({
        message,
        body: messageBody.body,
        history: messageBody.history,
        draft: id,
      });
      subject = `${prependRePrefix ? 'Re: ' : ''}${message.subject}`;
      break;
    case Configuration.Reply:
      body += formatReply({
        message,
        body: messageBody.body,
        history: messageBody.history,
        draft: id,
      });
      subject = `${prependRePrefix ? 'Re: ' : ''}${message.subject}`;
      break;
    case Configuration.ReplyAll:
      body += formatReply({
        message,
        body: messageBody.body,
        history: messageBody.history,
        draft: id,
      });
      subject = `${prependRePrefix ? 'Re: ' : ''}${message.subject}`;
      break;
    case Configuration.Forward:
      body += formatForward({
        message,
        body: messageBody.body,
        history: messageBody.history,
        draft: id,
      });
      subject = `Fwd: ${message.subject}`;
      break;
    default:
      body += '';
      subject = message.subject;
      break;
  }

  // Blacklisted recipients
  const aliases = accounts.filter(each => each.account === account.id);
  const blacklist = aliases.concat(account);

  // Recipients for draft
  const recipients = configureRecipients(configuration, message, blacklist, forceRecipients);

  const draft: Draft = {
    id,
    created: createDate,
    updated: createDate,
    status: DraftStatus.Draft,
    account: account.id,
    subject,
    attachments: [],
    thread: configuration !== Configuration.Forward ? thread.apiId : undefined,
    threadV2: configuration !== Configuration.Forward ? thread.id : undefined,
    message: configuration !== Configuration.Forward ? message.id : undefined,
    body,
    ...recipients,
    split: false,
    track: !preferences.disableTrackByDefault,
    trackLinks: preferences.linkTracking,
    bcc: !!account.autoBcc && [account.autoBcc].concat(recipients.bcc) || recipients.bcc,
    waiting: preferences.followUp ? getDefaultWaiting(preferences.followUp) : undefined,
    version: 0,
    cancelOnReply: preferences.cancelOnReply,
  };

  const replyConfigurations = [
    Configuration.Reply,
    Configuration.ReplyAll,
    Configuration.MoveBcc,
  ];
  if (replyConfigurations.indexOf(configuration) > -1) {
    draft.smtp = message.rfcId;
  }
  return draft;
};

interface DraftReplyHistoryMeta {
  draft: string;
  message: Message;
  body: string;
  history?: string;
}

export const formatReply = (meta: DraftReplyHistoryMeta): string => {
  let quote = '<div class="gmail_extra"><br><div class="gmail_quote">';
  const messageDate = moment(meta.message.internal);
  const sentDate = messageDate.format('ddd, MMM Do, YYYY');
  const sentTime = messageDate.format('h:mm A');

  quote += `<div dir="ltr">On ${sentDate} at ${sentTime}, `
    + `${escape(meta.message.from)} wrote:<br></div>`
    + '<blockquote class="gmail_quote"'
    + ' style="margin:0 0 0 .8ex;border-left:1px #ccc solid;padding-left:1ex">'
    + meta.body
    + (meta.history || '');

  const html = quote + '</blockquote></div><br></div>';
  const strippedHtml = removeTrackingPixels(html);
  const container = FakeDOM().createElement('div');
  const historyMarker = meta.draft + '-history-marker';
  container.innerHTML = strippedHtml;
  if (container.firstElementChild) {
    container.firstElementChild.setAttribute('data-pm-history', historyMarker);
  }

  return container.innerHTML;
};

// TODO(SHIN): Handle forward case to show all to recipients
export const formatForward = (meta: DraftReplyHistoryMeta) => {
  let quote = '<br><div class="gmail_quote">';
  const messageDate = moment(meta.message.internal);
  const sentDate = messageDate.format('ddd, MMM Do, YYYY');
  const sentTime = messageDate.format('h:mm A');

  // Identify first recipient for forward
  const firstTo = meta.message.to[0] || meta.message.cc[0] || meta.message.bcc[0] || '';

  quote += '<div dir="ltr">---------- Forwarded message ---------<br>' +
    `From: ${escape(meta.message.from)}<br>` +
    `Date: ${sentDate} at ${sentTime}<br>` +
    `Subject: ${meta.message.subject}<br>` +
    `To: ${escape(firstTo)}<br>` +
    '</div><br><br><u></u>';

  quote += '<blockquote class="gmail_quote"'
    + ' style="margin:0 0 0 .8ex;border-left:1px #ccc solid;padding-left:1ex">'
    + meta.body
    + (meta.history || '');

  const html = quote + '</blockquote></div>';
  const strippedHtml = removeTrackingPixels(html);
  const container = FakeDOM().createElement('div');
  const historyMarker = meta.draft + '-history-marker';
  container.innerHTML = strippedHtml;
  if (container.firstElementChild) {
    container.firstElementChild.setAttribute('data-pm-history', historyMarker);
  }

  return container.innerHTML;
};

export const configureRecipients
= (
  configuration: Configuration,
  message: Message,
  accounts: Account[],
  forceRecipients?: string[],
) => {
  // Lowercase map of accounts to ignore
  const emails = accounts.map(account => addressToEmail(account.email).toLowerCase());
  const replyTo = message.replyTo.length ? message.replyTo : [message.from];
  switch (configuration) {
    case Configuration.Reply: {
      let to = replyTo;
      to = filter(to, (address) => {
        return indexOf(emails, addressToEmail(address).toLowerCase()) === -1;
      });

      // If no results from computed TO, then use previous message.to
      return {
        to: !!to.length ? to : message.to,
        cc: [],
        bcc: [],
      };
    }
    case Configuration.ReplyAll: {
      let to = uniqBy(replyTo.concat(message.to), addressToEmail);
      to = filter(to, (address) => {
        return indexOf(emails, addressToEmail(address).toLowerCase()) === -1;
      });

      const cc = filter(message.cc, (address) => {
        return indexOf(emails, addressToEmail(address).toLowerCase()) === -1;
      });

      return {
        to,
        cc,
        bcc: [],
      };
    }
    case Configuration.MoveBcc: {
      const fromSender = addressToEmail(message.from.toLowerCase());
      let to = filter(message.to, (address) => {
        const lowercaseAddress = addressToEmail(address.toLowerCase());
        if (fromSender === lowercaseAddress) {
          return false;
        }
        return indexOf(emails, lowercaseAddress) === -1;
      });

      let cc = filter(message.cc, (address) => {
        const lowercaseAddress = addressToEmail(address.toLowerCase());
        if (fromSender === lowercaseAddress) {
          return false;
        }
        return indexOf(emails, addressToEmail(address)) === -1;
      });

      if (!message.sent) {
        to = replyTo.concat(to).filter((address) => {
          const lowercaseAddress = addressToEmail(address.toLowerCase());
          if (fromSender === lowercaseAddress) {
            return false;
          }
          return true;
        });
      }

      // If no recipients in TO field, replace with CC
      if (!to.length) {
        to = cc;
        cc = [];
      }

      return {
        to,
        cc,
        bcc: [message.from],
      };
    }
    case Configuration.Forward:
      return {
        subject: `Fwd: ${message.subject}`,
        to: [],
        cc: [],
        bcc: [],
      };
    case Configuration.MailTo:
      return {
        to: forceRecipients ? forceRecipients : [],
        cc: [],
        bcc: [],
      };
    default:
      return {
        to: [],
        cc: [],
        bcc: [],
      };
  }
};

export const attachmentsToDelete = (draft: Draft): AttachmentMeta[] => {
  const deletions: AttachmentMeta[] = [];
  const parser = new DOMParser();
  const doc = parser.parseFromString(draft.body, 'text/html');

  if (draft.attachments) {
    draft.attachments
      .filter(attachment => attachment.cloud)
      .forEach((attachment) => {
        const found = doc.querySelectorAll(`#data-pm-attachment-id-${attachment.id}`);
        if (found.length === 0) {
          // Add request to remove attachment from draft
          const meta = {
            resourceId: draft.id,
            attachment: attachment.id,
            file: undefined,
          };
          deletions.push(meta);
        }
      });

    draft.attachments
      .filter(attachment => attachment.inline)
      .forEach((attachment) => {
        const found = doc.querySelectorAll(`#pm-inline-id-${attachment.id}`);
        if (found.length === 0) {
          // Add request to remove attachment from draft
          const meta = {
            resourceId: draft.id,
            attachment: attachment.id,
            file: undefined,
          };
          deletions.push(meta);
        }
      });
  }
  return deletions;
};

// Convert a draft to a new sent thread
export const toThread = (draft: Draft, accounts: Account[]): Thread => {
  const message = toMessage(draft, accounts);
  return computeThread({
    id: `<${draft.id}@polymail.io>`,
    messages: [message],
    internal: draft.updated,
    sort: draft.updated,
    box: ThreadBox.Archive,
    account: draft.account,
    waiting: draft.waiting ? draft.waiting : undefined,
  } as any);
};

// Append a draft as a sent message to a thread
export const appendThread = (thread: Thread, draft: Draft, accounts: Account[]): Thread =>
  computeThread({
    ...thread,
    lastSent: draft.updated,
    internal: draft.updated,
    waiting: draft.waiting ? draft.waiting : undefined,
    messages: [...thread.messages, toMessage(draft, accounts)],
  });

// Converts draft to message
export const toMessage = (draft: Draft, accounts: Account[]): Message => {
  const { to = [], cc = [], bcc = [], subject = '', track = false } = draft;

  // Calculate from address
  const fromAccount = accounts.filter(account => account.id === draft.account)[0];
  let fromAddress = 'Me';
  if (fromAccount) {
    fromAddress = fromAccount.name !== ''
      ? `${fromAccount.name} <${fromAccount.email}>`
      : fromAccount.email;
  }

  const snippet = extractText(draft.body, 100);
  const id = `<${draft.id}@polymail.io>`;

  const attachments: MessageAttachment[] = draft.attachments
    .filter(a => !a.cloud)
    .map(a => ({
      id: a.id,
      filename: a.filename,
      type: a.type,
      length: a.length,
      url: a.cloudUrl,
      inline: a.inline,
      cid: a.inline ? a.id : undefined,
      message: draft.id,
      thread: draft.threadV2 || draft.thread,
    }));

  return {
    id,
    rfcId: `<${draft.id}@polymail.io>`,
    to,
    cc,
    bcc,
    subject,
    snippet,
    sent: true,
    read: true,
    internal: draft.updated,
    starred: false,
    tracks: 0,
    tracked: track,
    hasAttachments: attachments.filter(a => !a.inline).length > 0,
    attachments,
    from: fromAddress,
    replyTo: [],
    optimistic: draft.status,
  };
};

export const formatAttributeSelector = (attribute: string, value: string): string => {
  return `[${attribute}="${value}"]`;
};

export const toMessageBody = (draft: Draft, threadId: string): MessageBody => {
  const { body } = draft;
  const parser = new DOMParser();
  const doc = parser.parseFromString(body, 'text/html');
  const extra = doc.querySelector('.gmail_extra');
  const historyMarker = draft.id + '-history-marker';
  const history = extra ? extra.innerHTML : '';

  if (extra) {
    extra.innerHTML = '';
    extra.setAttribute('data-pm-history', historyMarker);
    extra.className = '';
  }

  return {
    id: `<${draft.id}@polymail.io>`,
    thread: threadId,
    body: doc.body.innerHTML,
    history,
    historyMarker,
    unsubscribeLink: '',
    updated: now().toDate(),
  };
};

export const removeTemplateRecipientsFromDraft =
  (draft: Draft, template: Template): Draft => {
    const updated = { ...draft, template: undefined };

    if (template.to) {
      const toEmails = template.to.map(r => addressToEmail(r).toLowerCase());
      updated.to = draft.to
        .filter(r => toEmails.indexOf(addressToEmail(r).toLowerCase()) === -1);
    }

    if (template.cc) {
      const ccEmails = template.cc.map(r => addressToEmail(r).toLowerCase());
      updated.cc = draft.cc
        .filter(r => ccEmails.indexOf(addressToEmail(r).toLowerCase()) === -1);
    }

    if (template.bcc) {
      const bccEmails = template.bcc.map(r => addressToEmail(r).toLowerCase());
      updated.bcc = draft.bcc
        .filter(r => bccEmails.indexOf(addressToEmail(r).toLowerCase()) === -1);
    }

    return updated;
  };

export const applyTemplateToDraft =
  (draft: Draft, template: Template): Draft => {
    const updated = { ...draft, template: template.id };

    // preserve signature
    const container = document.createElement('div');
    container.innerHTML = draft.body;
    const signature = container.querySelector('#psignature');
    const history = container.querySelector(formatAttributeSelector(
      'data-pm-history',
      `${draft.id}-history-marker`,
    ));

    const wrapper = document.createElement('div');
    wrapper.innerHTML = template.body;
    if (signature) {
      wrapper.appendChild(document.createElement('br'));
      wrapper.appendChild(document.createElement('br'));
      wrapper.appendChild(signature);
    }

    // Add history if found
    if (history) {
      wrapper.appendChild(history);
    }

    // Handle adding to field
    if (template.to) {
      updated.to = draft.to.concat(template.to);
      updated.to = uniqBy(updated.to, addressToEmail);
    }

    if (template.cc) {
      updated.cc = draft.cc.concat(template.cc);
      updated.cc = uniqBy(updated.cc, addressToEmail);
    }

    if (template.bcc) {
      updated.bcc = draft.bcc.concat(template.bcc);
      updated.bcc = uniqBy(updated.bcc, addressToEmail);
    }

    updated.body = wrapper.innerHTML;

    if (template.subject && !draft.threadV2) {
      updated.subject = template.subject;
    }

    return updated;
  };

const checkForAlias = (accounts: Account[], message: Message): Account|undefined => {
  const match = accounts
    .filter((account) => {
      const isAlias = account.type === AccountType.Alias;
      return isAlias && [message.from]
        .concat(message.to)
        .map(email => addressToEmail(email))
        .join(',')
        .toLowerCase()
        .includes(account.email.toLowerCase());
    })[0];
  return match;
};

// Replaces recipient information (sender/account/bcc recipients)
export const replaceDraftSender = (draft: Draft, accounts: Account[], email: string): Draft => {
  const strippedEmail = addressToEmail(email);
  const oldAccount = accounts
    .filter(account => draft && account.id === draft.account)[0];
  const newAccount = accounts
    .filter(account => addressToEmail(account.email) === strippedEmail)[0];

  // Create base draft with updated account.
  const updated: Draft = {
    ...draft,
    account: newAccount.id,
  };

  // Strip out old bcc from email if previously existed
  if (oldAccount && oldAccount.autoBcc) {
    const oldBccAddress = addressToEmail(oldAccount.autoBcc);
    updated.bcc = updated.bcc.filter(bcc => addressToEmail(bcc) !== oldBccAddress);
  }

  // Add bcc for new email account if is not already inserted
  if (newAccount && newAccount.autoBcc) {
    const newBccAddressString = addressToEmail(newAccount.autoBcc);
    if (updated.bcc.findIndex(bcc => addressToEmail(bcc) === newBccAddressString) === -1) {
      updated.bcc = updated.bcc.concat(newAccount.autoBcc);
    }
  }

  return updated;
};

export const sanitizeDraft = (draft: Draft): Draft => {
  // Removes any attachment remove buttons
  let replace = false;
  const updated = cloneDeep(draft);
  const container = FakeDOM().createElement('div');
  container.innerHTML = draft.body;
  const removeElements = container.querySelectorAll('.attachment-remove-button');
  Array.from(removeElements).forEach((element) => {
    if (element && element.parentElement) {
      element.parentElement.removeChild(element);
      replace = true;
    }
  });

  // If anything was removed then set to modified html body
  // Remove all link preview elements
  const linkPreviewRemoveElements = container.querySelectorAll('.pm-lp-close-buttons');
  Array.from(linkPreviewRemoveElements).forEach((element) => {
    if (element && element.parentElement) {
      element.parentElement.removeChild(element);
      replace = true;
    }
  });

  // Removes calendar invite remove buttons
  const calendarCloseElements = container.querySelectorAll('.pm-cal-invite-close-container');
  Array.from(calendarCloseElements).forEach((element) => {
    if (element && element.parentElement) {
      element.parentElement.removeChild(element);
      replace = true;
    }
  });

  if (!replace) {
    return draft;
  }

  // Updates body and returns sanitized draft
  updated.body = container.innerHTML;
  return updated;
};

// Extracts body from draft and removes history/signature
export const bodyWithoutSignatureAndHistory = (body: string, draftId: string): string => {
  const fakeBody = FakeDOM().createElement('div');
  fakeBody.innerHTML = body;

  // Remove History
  const history = fakeBody.querySelector(`[data-pm-history="${draftId}-history-marker"]`);
  if (history && history.parentElement) {
    history.parentElement.removeChild(history);
  }

  // Remove Signature
  const signatureElement = fakeBody.querySelector('#psignature');
  if (signatureElement && signatureElement.parentElement) {
    signatureElement.parentElement.removeChild(signatureElement);
  }

  return fakeBody.innerHTML;
};

