// Handles replacing attachment stuff
export const applyAttachmentToDraft = (draft: Draft, attachment: Attachment): Draft => {
  if (!attachment.cloud && !attachment.inline) {
    return draft;
  }

  // Create draft to update and Document container for the body
  const updatedDraft = cloneDeep(draft);
  const container = FakeDOM().createElement('div');
  container.innerHTML = draft.body;

  // Makes necessary replacements for cloud attachments
  if (attachment.cloud) {
    const id = `data-pm-attachment-id-${attachment.id}`;
    const foundCloud = container.querySelector(`#${id}`);
    if (foundCloud) {

      // Remove attachment progress element
      const progressElement = foundCloud.querySelector('.attachment-progress');
      if (progressElement && progressElement.parentElement) {
        progressElement.parentElement.removeChild(progressElement);
      }

      // Set new filesize
      const filesizeElement = foundCloud.querySelector('.attachment-filesize');
      if (filesizeElement) {
        filesizeElement.setAttribute('data-pm-complete', '1');
        filesizeElement.innerHTML = truncate(attachment.length);
      }

      // Set new href tags
      const hrefTags = foundCloud.querySelectorAll('[href]');
      if (hrefTags.length) {
        Array.from(hrefTags).forEach((hrefTag) => {
          hrefTag.setAttribute('href', attachment.cloudUrl);
        });
      }
    }
    updatedDraft.body = container.innerHTML || draft.body;
  }

  // Makes necessary replacements for inline images
  if (attachment.inline) {
    const id = `pm-inline-id-${attachment.id}`;
    const foundInline = container.querySelector(`#${id}`);
    if (foundInline) {
      foundInline.removeAttribute('class');
      foundInline.setAttribute('src', attachment.cloudUrl);
    }
    updatedDraft.body = container.innerHTML || draft.body;
  }

  return updatedDraft;
};
