import React from 'react';
import classnames from 'classnames';

import Dropdown from '@src/components/Form/Dropdown';
import ExclamationIcon from '@src/components/Icons/ExclamationIcon.svg';

import './DropdownCell.scss';
import HoverTip from '@src/components/Base/HoverTip';

interface Props {
  className?: string;
  title: string;
  subtitle?: string | JSX.Element;
  onSelect: (value: string, index: number, id?: string) => void;
  values: string[];
  dropdownId?: string;
  startIndex?: number;
  beta?: boolean;
  error?: string;
  errorHandler?: () => void;
  errorCopy?: string;
  typeahead?: boolean;
  readOnly?: boolean;
}

const DropdownCell = (props: Props) => {
  const {
    title,
    subtitle,
    onSelect,
    values,
    dropdownId,
    startIndex,
    className,
    beta,
    error,
    errorHandler,
    errorCopy,
    readOnly,
    typeahead,
  } = props;

  const renderError = () => {
    const errorJSX = errorHandler && (
      <div className="error-handler-section">
        <div className="copy">
          {error}
        </div>
        <button
          className="error-button"
          onClick={errorHandler}
        >
          {errorCopy || 'Fix me'}
        </button>
      </div>
    );

    return (
        <HoverTip
          className="error-disclaimer"
          copy={error}
          multiline
          maxWidth="160px"
          left="3px"
          hoverChild={errorJSX}
        >
          <div className="error-icon">
            <ExclamationIcon />
          </div>
        </HoverTip>
    );
  };

  return (
    <div className={classnames('dropdown-cell', className)}>
      <div className="left-column">
        <div className="top">
          <h4 className="title">{title}</h4>
          {beta && (
            <div className="beta-disclaimer">
              BETA
            </div>
          )}
          {error && renderError()}
        </div>
        {subtitle && <h5 className="subtitle">{subtitle}</h5>}
      </div>
      <Dropdown
        values={values}
        onSelect={onSelect}
        id={dropdownId}
        startIndex={startIndex}
        readOnly={readOnly}
        typeahead={typeahead}
      />
    </div>
  );
};

export default DropdownCell;
