import React from 'react';
import classnames from 'classnames';
import { SequenceStageInstance, SequenceStage } from '@src/types/sequences';
import ContentEditable from '@src/components/ContentEditable/ContentEditable';

import './SequenceStageMessage.scss';
import {
  Resource,
  FileType,
  Attachment,
  AttachmentMeta,
  AttachmentMetaStatus,
  CommandControlType,
} from '@src/types';
import SubjectPane from '@src/components/Compose/SubjectPane/SubjectPane';
import Checkbox from '@src/components/Form/Checkbox/Checkbox';
import { size, filter, findIndex, uniq, compact, values, indexOf } from 'lodash';
import Dropdown from '@src/components/Form/Dropdown';
import { SequenceStageInstanceAttachmentMeta } from '@src/ducks/sequenceStageInstances';
import FileDropZone from '@src/components/Compose/FileDropZone';
import { fileFormat, filenameIconUrl, AttachmentUploadOptions } from '@src/lib/attachment';
import { mongoId } from '@src/lib/crypt';
import AttachmentCell from '@src/components/Compose/Attachments/AttachmentCell';
import UploadButton from '@src/components/Base/UploadButton';
import { Prompt } from 'react-router';
import CommandControl from '@src/components/CommandControl';
import browserHistory from '@src/lib/browserHistory';
import { HotkeyAction } from '@src/lib/hotkeys';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import { BlobDescriptor, uploadErrorMessage } from '@src/lib/files';

export interface Props {
  stage: SequenceStage;
  instances?: {
    [instanceId: string]: Resource<SequenceStageInstance>,
  };
  edit: boolean;
  placeholders?: string[];
  sender: string;
  readOnly?: boolean;

  onEdit: (stage: SequenceStageInstance) => void;
  onStageUpdate: (stage: SequenceStage) => void;
  onStageEdit: (stage: SequenceStage) => void;
  onTestEmailClick: () => void;
  enableEdit: () => void;
}

export interface StateProps {
  attachments: Attachment[];
  attachmentMetas: Array<Resource<AttachmentMeta>>;
  cloudAttachments: boolean;
  commandControlType?: CommandControlType;
  schedulingUrl?: string;

  useLinkPreview?: boolean;
  noBranding?: boolean;
}

export interface DispatchProps {
  showCommandControl: (type: CommandControlType) => void;
  addAttachment: (meta: SequenceStageInstanceAttachmentMeta) => void;
  removeAttachment: (meta: SequenceStageInstanceAttachmentMeta) => void;
  hotkeyUpdate: (meta: HotkeyAction) => void;
}

interface State {
  instance?: SequenceStageInstance;
  stage?: SequenceStage;
  dropzoneActive: boolean;
  confirmProps?: ConfirmProps;
}

const SEND_TYPE = ['New Thread', 'Reply'];
class SequenceStageMessage extends React.PureComponent<Props & DispatchProps & StateProps, State> {
  contentEditable: ContentEditable;
  private intervalDayValues: string[];
  private subjectPane: SubjectPane;
  private focusContentEditable: boolean;

  constructor(props: Props & DispatchProps & StateProps) {
    super();

    this.intervalDayValues = [];
    for (let i = 0; i < 30; i += 1) {
      this.intervalDayValues.push(`${i + 1} days`);
    }

    this.state = {
      dropzoneActive: false,
    };
  }

  componentDidMount() {
    const { instances, stage } = this.props;

    // TODO(SHIN): Determine method between loading vs. empty state
    const stillLoading = filter(instances, i => !i.data);
    if (!instances || !stillLoading || !size(instances)) {
      return;
    }
    const firstInstance = instances[Object.keys(instances)[0]].data;
    this.setState(
      {
        instance: firstInstance,
        stage,
      },
      () => {
        if (this.props.edit && stage.newThread) {
          this.subjectPane.focus();
        } else if (this.props.edit) {
          this.contentEditable.focus();
        }
      },
    );
  }

  UNSAFE_componentWillReceiveProps(nextProps: Props & StateProps) {
    // Handle updating attachments
    const nextAttachments = nextProps.attachments;
    const oldAttachments = this.props.attachments;
    if (nextAttachments && oldAttachments) {
      nextAttachments.
        filter(attachment => findIndex(oldAttachments, { id: attachment.id }) === -1)
        .forEach((attachment) => {
          if (attachment.inline) {
            this.contentEditable.replaceAttachment(attachment);
          }

          if (attachment.cloud) {
            this.contentEditable.replaceAttachment(attachment);
          }
        },
      );
    }

    // Injection points for metas
    const oldMetas = this.props.attachmentMetas;
    const newMetas = nextProps.attachmentMetas;
    const oldMetaIds = compact(values(oldMetas.map(meta => meta.data && meta.data.attachment)));
    if (oldMetas !== newMetas) {
      // Inject metas
      nextProps.attachmentMetas
        .forEach((meta) => {
          if (!meta.data) {
            return;
          }

          // Replace if available
          this.contentEditable.replaceAttachmentMeta(meta.data, meta.error);

          // Check if attachment meta is new
          if (indexOf(oldMetaIds, meta.data && meta.data.attachment) !== -1) {
            return;
          }

          // Do not inject if already exists in body
          if (meta.data && this.contentEditable.attachmentExistsInBody(meta.data.attachment)) {
            return;
          }

          if (meta.data.inline && !meta.data.template) {
            this.contentEditable.injectInlineAttachmentMeta(meta.data);
          }

          if (meta.data.cloud && !meta.data.template) {
            this.contentEditable.injectCloudAttachmentMeta(meta.data, meta.error);
          }
        });
    }

    let firstInstance;
    if (nextProps.instances && Object.keys(nextProps.instances).length) {
      firstInstance = nextProps.instances[Object.keys(nextProps.instances)[0]].data;
    }

    if (this.props.edit === nextProps.edit &&
      ((this.state.instance && this.state.instance === firstInstance)
      || nextProps.stage !== this.props.stage)) {
      return;
    }

    if (this.props.edit !== nextProps.edit && nextProps.edit) {
      if (nextProps.stage.newThread && !this.focusContentEditable) {
        this.subjectPane.focus();
      } else {
        this.contentEditable.focus();
        this.focusContentEditable = false;
      }
    }

    if (this.props.edit !== nextProps.edit && !nextProps.edit) {
      this.setState({
        stage: nextProps.stage,
        instance: firstInstance,
      });
    }

    // TODO(SHIN): Determine method between loading vs. empty state
    const { instances, stage, onEdit } = nextProps;
    const stillLoading = filter(instances, i => !i.data);
    if (!instances || !stillLoading || !size(instances)) {
      return;
    }

    // Reset state if template has been applied
    const templateChanged = this.state.instance && firstInstance &&
      this.state.instance.template !== firstInstance.template;
    if ((!this.state.instance && firstInstance) || templateChanged) {
      this.setState(
        {
          instance: firstInstance,
        },
      );

      // Immediately show edit for stage
      if (firstInstance) {
        onEdit(firstInstance);
      }
    }

    if (!this.state.stage && stage) {
      this.setState(
        {
          stage,
        },
      );
    }
  }

  handleContentEditableRef = (ref: ContentEditable) => {
    this.contentEditable = ref;
  }

  handleSubjectChange = (subject: string) => {
    const { instance } = this.state;
    const { onEdit } = this.props;
    if (!instance || !onEdit) {
      return;
    }

    const updatedStageInstance = {
      ...instance,
      subject,
    };

    this.setState(
      {
        instance: updatedStageInstance,
      },
      () => onEdit(updatedStageInstance),
    );
  }

  handleBodyChange = (body: string) => {
    const { instance } = this.state;
    const { onEdit } = this.props;
    if (!instance || !onEdit) {
      return;
    }

    const updatedStageInstance = {
      ...instance,
      body,
    };

    this.setState(
      {
        instance: updatedStageInstance,
      },
      () => onEdit(updatedStageInstance),
    );
  }

  handleUnsubscribeChange = () => {
    const { instance } = this.state;
    const { onEdit } = this.props;
    if (!instance || !onEdit) {
      return;
    }

    const updatedStageInstance = {
      ...instance,
      unsubscribeLink: !instance.unsubscribeLink,
    };

    this.setState(
      {
        instance: updatedStageInstance,
      },
      () => onEdit(updatedStageInstance),
    );
  }

  handleIntervalDayClick = (value: string, index: number) => {
    const { onStageUpdate, stage } = this.props;

    onStageUpdate({
      ...stage,
      intervalDays: index + 1,
    });
  }

  handleSendTypeClick = (value: string, index: number) => {
    const { onStageEdit, onEdit } = this.props;
    const { stage, instance } = this.state;

    if (!stage || !onStageEdit || !instance) {
      return;
    }

    const updatedStage = {
      ...stage,
      newThread: index === 0,
    };

    let updatedStageInstance: SequenceStageInstance | undefined;
    if (index === 0) {
      updatedStageInstance = {
        ...instance,
        subject: '',
      };
    }

    this.setState(
      {
        stage: updatedStage,
        instance: updatedStageInstance ? updatedStageInstance : instance,
      },
      () => {
        onStageEdit(updatedStage);
        if (updatedStageInstance) {
          onEdit(updatedStageInstance);
        }
      },
    );
  }

  handleSubjectPaneRef = (ref: SubjectPane) => {
    this.subjectPane = ref;
  }

  // DropZone handlers
  handleDragEnter = (event: React.DragEvent<HTMLDivElement>) => {
    if (event.dataTransfer.types.indexOf('Files') !== -1) {
      this.setState({ dropzoneActive: true });
    }
  }

  handleDragLeave = () => {
    this.setState({ dropzoneActive: false });
  }

  handleAttachmentRemove = (id: string) => {
    const { attachments, attachmentMetas, removeAttachment, onEdit } = this.props;
    const { instance } = this.state;

    // Handle meta delete
    const attachmentMetaIds = compact(values(
      attachmentMetas.map(resource => resource.data && resource.data.attachment)),
    );
    const metaIndex = indexOf(attachmentMetaIds, id);
    const meta = attachmentMetas[metaIndex];
    if (meta && meta.data && instance) {
      removeAttachment({
        ...meta.data,
        stage: instance.stage,
        sequence: instance.sequence,
        status: AttachmentMetaStatus.Delete,
      });
    }

    // Handle attachment delete
    const attachmentIndex = findIndex(attachments, { id });
    const attachment = attachments[attachmentIndex];
    if (attachment && instance) {
      removeAttachment({
        attachment: attachment.id,
        resourceId: instance.id,
        stage: instance.stage,
        sequence: instance.sequence,
        status: AttachmentMetaStatus.Delete,
      });
    }

    if (instance) {
      onEdit(instance);
    }
  }

  // TODO(SHIN): Handle Cloud attachments handling
  handleUpload = (files: File[], options: AttachmentUploadOptions) => {
    const { addAttachment, onEdit, cloudAttachments } = this.props;
    const { instance } = this.state;

    // Block ability to attach files larger than 25 MB.
    if (files.filter(file => file.size > 25000000).length) {
      const confirmProps: ConfirmProps = {
        title: 'Error Uploading Attachment',
        message: 'You cannot upload an attachment greater than 25 MB.',
        confirmCopy: 'Ok',
        onConfirm: () => {
          this.setState({
            confirmProps: undefined,
            dropzoneActive: false,
          });
        },
      };

      this.setState({ confirmProps });
      return;
    }

    if (addAttachment && instance) {
      files.forEach((file) => {
        // Create meta
        const inline = fileFormat(file.type) === FileType.Image;
        const attachment = {
          file,
          resourceId: instance.id,
          attachment: mongoId(),
          inline: inline && !options.shiftKey && !options.ignoreInline,
          cloud: !options.shiftKey && !inline && cloudAttachments,
          stage: instance.stage,
          sequence: instance.sequence,
          status: AttachmentMetaStatus.Uploading,
        };

        addAttachment(attachment);
      });
    }

    // Handle dropping to specific position
    this.setState({ dropzoneActive: false }, () => {
      if (options.position && this.contentEditable) {
        this.contentEditable.moveCursorToPosition(options.position);
      }
    });

    if (instance) {
      onEdit(instance);
    }
  }

  renderAttachmentCells = () => (
    this.props.attachments &&
    this.props.attachments
      .filter(attachment => !(attachment.cloud || attachment.inline))
      .map(attachment => (
        <AttachmentCell
          key={attachment.id}
          id={attachment.id}
          filename={attachment.filename}
          iconUrl={filenameIconUrl(attachment.filename)}
          length={attachment.length}
          progress={1}
          onDelete={this.handleAttachmentRemove}
          fileUrl={attachment.cloudUrl}
        />
      ),
    )
  )

  handleTemplateShow = () => {
    this.props.showCommandControl(CommandControlType.Templates);
  }

  // TODO(SHIN): Implement progress when available
  renderAttachmentMetaCells = () => (
    this.props.attachmentMetas && this.props.attachmentMetas
      .filter((meta) => {
        const deleteStatus = meta.data && meta.data.status === AttachmentMetaStatus.Delete;
        const cloud = meta.data && meta.data.cloud;
        const inline = meta.data && meta.data.inline;
        return (!deleteStatus && !(cloud || inline));
      })
      .map((meta) => {
        const data = meta.data;
        if (!data) {
          return null;
        }
        return (
          <AttachmentCell
            key={data.attachment}
            id={data.attachment}
            filename={data.filename || data.file && data.file.name || 'attachment'}
            iconUrl={filenameIconUrl((data.filename || data.file && data.file.name) || '')}
            length={data.length || data.file && data.file.size || 1000}
            onDelete={this.handleAttachmentRemove}
            progress={data.progress || 0}
            fileUrl={data.cloudUrl}
            isMeta
          />
        );
      })
  )

  handleSchedulingUrl = () => {
    const { schedulingUrl, hotkeyUpdate } = this.props;
    if (schedulingUrl) {
      hotkeyUpdate(HotkeyAction.CopySchedulingUrl);
    } else {
      browserHistory.push('/settings/calendar');
    }
  }

  handleEnableEdit = () => {
    this.focusContentEditable = true;
    this.props.enableEdit();
  }

  handleDirectoryDropError = (b: BlobDescriptor) => {
    const confirmProps: ConfirmProps = {
      title: 'Error attaching file',
      message: uploadErrorMessage(b),
      confirmCopy: 'Ok',
      onConfirm: () => {
        this.setState({ confirmProps: undefined });
      },
    };

    this.setState({ confirmProps, dropzoneActive: false });
  }

  render() {
    const {
      edit,
      placeholders,
      onTestEmailClick,
      sender,
      readOnly,
      commandControlType,
      enableEdit,
      useLinkPreview,
      noBranding,
    } = this.props;
    const { instance, dropzoneActive, stage, confirmProps } = this.state;
    const enhancedPlaceholders = uniq(
      ['email', 'first name', 'last name'].concat(placeholders || []),
    );

    if (!instance || !stage) {
      return <div />;
    }

    const firstInstance = instance;
    const leaveWarningPrompt = 'You have unsaved changes on this message. '
    + 'Are you sure you want to leave this page?';

    return (
      <div className={classnames('sequence-stage-message', { edit })}>
        {confirmProps &&
          <Confirm
            {...confirmProps}
          />
        }
        {stage.order > 1 && !readOnly && <div className="send-interval">
          <span>Send</span>
          <Dropdown
            id="intervalDays-dropdown"
            onSelect={this.handleIntervalDayClick}
            values={this.intervalDayValues}
            startIndex={stage.intervalDays - 1}
          />
          <span>after previous stage as</span>
          <Dropdown
            id="newThread-dropdown"
            onSelect={this.handleSendTypeClick}
            values={SEND_TYPE}
            value={SEND_TYPE[stage.newThread ? 0 : 1]}
            startIndex={stage.newThread ? 0 : 1}
          />
        </div>}
        {stage.order > 1 && !!readOnly && <div className="send-interval">
          <span>
            Send {stage.intervalDays} days after previous stage as a {
              stage.newThread ? 'new thread' : 'reply'
            }
          </span>
        </div>}
        {stage.order === 1 && <div className="send-interval">
          <span>Sends as soon as recipients enter this sequence.</span>
        </div>}
        <div className="message-container">
          <div className="edit-options">
            <button className="select-template" onClick={this.handleTemplateShow}>
               Message Templates
            </button>
            <UploadButton
              description="Add Attachment"
              className="upload-attachment-button"
              upload={this.handleUpload}
            />
            <button className="select-scheduling" onClick={this.handleSchedulingUrl}>
              Share Available Times
            </button>
            <div className="unsubscribe-container">
              <Checkbox
                onChange={this.handleUnsubscribeChange}
                checked={firstInstance.unsubscribeLink}
              />
              <span className="description">Add unsubscribe link</span>
            </div>
            <button className="send-test" onClick={onTestEmailClick}>
              Send a test email
            </button>
          </div>
          <div
            className="message"
            onDragEnter={this.handleDragEnter}
            onDragExit={this.handleDragLeave}
          >
            {dropzoneActive && edit &&
              <FileDropZone
                className="compose-drop-zone"
                onDrop={this.handleUpload}
                onLeave={this.handleDragLeave}
                title={'Drop here to attach files'}
                description={'Polymail will automatically track '
                  + 'when your attachments are opened.'}
                altCopy={'Hold SHIFT while dropping to upload without tracking'}
                onDirectoryDropError={this.handleDirectoryDropError}
              />
            }
            <SubjectPane
              ref={this.handleSubjectPaneRef}
              subject={stage.newThread ?
                firstInstance.subject || (edit ? '' : '(No subject)') :
                `Reply to Stage ${stage.order - 1}`}
              onChange={this.handleSubjectChange}
              readOnly={!stage.newThread || !edit}
              variables={enhancedPlaceholders}
              onClick={enableEdit}
            />
            <div className="from">{`from: ${sender}`}</div>
            <div
              className="compose-box"
              onClick={this.handleEnableEdit}
            >
              <ContentEditable
                ref={this.handleContentEditableRef}
                html={firstInstance.body}
                onChange={this.handleBodyChange}
                disabled={!edit}
                variables={enhancedPlaceholders}
                template={firstInstance.template}
                useLinkPreview={useLinkPreview}
                noBranding={noBranding}
              />
            </div>
            {firstInstance.unsubscribeLink &&
              <div>
                <a
                  className="mock-unsubscribe"
                  style={{ fontSize: '10px' }}
                >
                  Unsubscribe
                </a>
              </div>
          }
          {this.renderAttachmentCells()}
          {this.renderAttachmentMetaCells()}
          </div>
        </div>
        <Prompt
          message={leaveWarningPrompt}
          when={edit}
        />
        {commandControlType && commandControlType === CommandControlType.Templates &&
          <CommandControl
            default={CommandControlType.Templates}
          />
        }
      </div>
    );
  }
}

export default SequenceStageMessage;
