import React from 'react';

import './ZoomMeeting.scss';
import { Me } from '@src/types';
import Zoom from '@src/components/Icons/Zoom.svg';
import { ZoomMeeting as ZoomMeetingType } from '@src/types/zoom';
import Popover from '@src/components/Popover/Popover';
import ClipboardJS from 'clipboard';
import ToolTip from '@src/components/Base/Tooltip';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface StateProps {
  me?: Me;
  zoomMeeting?: ZoomMeetingType;
}

interface Props {
  className?: string;
}

export interface DispatchProps {
  dismiss: () => void;
}

interface State {
  showMeetingCopiedTooltip?: boolean;
  showPasswordTooltip?: boolean;
}

class ZoomMeeting extends React.PureComponent<Props & DispatchProps & StateProps, State> {
  meetingUrlClipboard = new ClipboardJS('#zoom-meeting-url-clipboard');

  constructor() {
    super();
    this.state = {};
  }

  handleClick = (e: React.MouseEvent<any>) => {
    const { zoomMeeting } = this.props;
    if (!zoomMeeting) { return; }

    window.open(zoomMeeting.startUrl);
    e.preventDefault();
    e.stopPropagation();
  }

  handleDismiss = () => {
    this.props.dismiss();
  }

  handleMeetingUrlCopy = (e: React.MouseEvent<any>) => {
    logAmplitudeEvent(AmplitudeEvents.CopyZoomUrl);
    this.setState({ showMeetingCopiedTooltip: true });
    e.stopPropagation();
    e.preventDefault();
  }

  handlePasswordCopy = (e: React.MouseEvent<any>) => {
    this.setState({ showPasswordTooltip: true });
    e.stopPropagation();
    e.preventDefault();
  }

  handleMeetingTooltipDismiss = () => {
    this.setState({ showMeetingCopiedTooltip: undefined });
  }

  handlePaswordTooltipDismiss = () => {
    this.setState({ showPasswordTooltip: undefined });
  }

  render() {
    const { zoomMeeting } = this.props;

    const { showPasswordTooltip, showMeetingCopiedTooltip } = this.state;
    if (!zoomMeeting) {
      return null;
    }

    const paswordExists = zoomMeeting.password && zoomMeeting.password !== '';

    const title = 'Your Zoom meeting link has been created';
    return (
      <Popover
        darkOverlay
        showOnMount
        onHide={this.handleDismiss}
        className="zoom-meeting-modal"
        fadesOut
        overlay
      >
        <div className="main">
          <div className="header">
            <div className="logo">
              <Zoom />
            </div>
            <div className="title">
              {title}
            </div>
          </div>
          <div className="info-section">
            <div className="section-header">
              MEETING URL
            </div>
            <div className="meeting-url-section">
              <div
                className="meeting-url"
              >
                {zoomMeeting?.joinUrl}
              </div>
              <button
                className="copy-meeting-url-button"
                id="zoom-meeting-url-clipboard"
                data-clipboard-text={zoomMeeting.joinUrl}
                onClick={this.handleMeetingUrlCopy}
                onMouseLeave={this.handleMeetingTooltipDismiss}
              >
                Copy Url
              </button>
              {showMeetingCopiedTooltip &&
                <ToolTip
                  className="tooltip"
                  top="34px"
                  right="5px"
                  copy="Copied!"
                />
              }
            </div>
            {paswordExists && (
              <div className="section-header">
                MEETING PASSWORD
              </div>
            )}
            {paswordExists && (
            <div className="password-section">
              <div
                className="password-input"
              >
                {zoomMeeting?.password}
              </div>
              <button
                className="copy-password-button"
                id="password-clipboard"
                data-clipboard-text={zoomMeeting.password}
                onClick={this.handlePasswordCopy}
                onMouseLeave={this.handlePaswordTooltipDismiss}
              >
                Copy
              </button>
              {showPasswordTooltip &&
                <ToolTip
                  className="tooltip"
                  top="34px"
                  right="10px"
                  copy="Copied!"
                />
              }
            </div>
          )}
          </div>
          <button
            onClick={this.handleClick}
            className="cta-button"
          >
            Start meeting
          </button>
        </div>
      </Popover>
    );
  }
}

export default ZoomMeeting;
