import React from 'react';
import {
  Editor,
  EditorState,
  convertFromRaw,
  CompositeDecorator,
  ContentBlock,
  ContentState,
  getDefaultKeyBinding,
  SelectionState,
  RawDraftContentState,
  DraftHandleValue,
} from 'draft-js';

import { debounce, isEqual, last, difference } from 'lodash';
import { parseOneAddress } from 'email-addresses';
import { addressToEmail, parseAddress } from '@src/lib/address';
import { Contact, KeyCode, ContactGroup } from '@src/types';

import EmailToken from './components/EmailToken';
import ContactTypeaheadPopover from './components/ContactTypeaheadPopover';

import './RecipientsEditor.scss';

export const enum EditorType {
  To = 'to',
  Bcc = 'bcc',
  Cc = 'cc',
  Event = 'event',
}

export interface Props {
  recipients: string[];
  id: EditorType;
  typeaheadContacts?: Array<Contact|ContactGroup>;
  focused: boolean;

  onRecipientsChange: (list: string, recipients: string[]) => void;
  onContactGroupAdd: (list: string, contactGroup: ContactGroup) => void;
  getContact?: (email: string) => Contact;
  onSearch?: (id: EditorType) => void;
  onTabPress?: () => void;
  onShiftTabPress?: () => void;
  onFocus?: (type: EditorType) => void;
  onBlur?: () => void;
}

interface State {
  editorState: EditorState;
  startRecipient?: number;
  popoverShown: boolean;
  selectedPopoverIndex: number;
}

export interface DispatchProps {
  searchContactTypeahead: (query: string) => void;
  clearContactTypeahead: () => void;
  selectContact: (email: string) => void;
}

class RecipientsEditor extends React.PureComponent<Props & DispatchProps, State>  {
  static defaultProps = {
    recipients: [],
  };
  private compositeDecorator: CompositeDecorator;
  private debounced: any;
  private popover: ContactTypeaheadPopover;
  private timeout: NodeJS.Timer;
  private editor: Editor;
  private potentialRecipient?: string;

  constructor() {
    super();

    this.compositeDecorator = new CompositeDecorator([
      {
        strategy: this.handleStrategy,
        component: EmailToken,
      },
    ]);

    this.debounced = debounce(this.contactTypeahead, 300);

    this.state = {
      editorState: EditorState.createEmpty(),
      popoverShown: false,
      selectedPopoverIndex: 0,
    };
  }

  UNSAFE_componentWillReceiveProps(nextProps: Props) {
    const { recipients } = this.props;

    if (difference(nextProps.recipients, recipients).length > 0) {
      const block = convertFromRaw(this.createRawContentState(nextProps.recipients));
      const editor = EditorState.createWithContent(block, this.compositeDecorator);
      this.setState({
        editorState: nextProps.focused ? EditorState.moveFocusToEnd(editor) : editor,
      });
    }

    const editorText = this.state.editorState.getCurrentContent().getPlainText();
    if (!nextProps.typeaheadContacts || nextProps.typeaheadContacts.length === 0 ||
      !nextProps.focused
    ) {
      this.popover.hide();
    } else if (nextProps.focused && !!editorText) {
      this.popover.show();
    }
  }

  componentDidMount() {
    const { recipients, focused } = this.props;

    if (recipients.length) {
      const block = convertFromRaw(this.createRawContentState(recipients));
      const editor = EditorState.createWithContent(block, this.compositeDecorator);
      this.setState({ editorState: focused ? EditorState.moveFocusToEnd(editor) : editor });
    }
  }

  hasFocus = () => {
    return this.state.editorState.getSelection().getHasFocus();
  }

  handleStrategy = (contentBlock: ContentBlock, callback: any, contentState: ContentState) => {
    contentBlock.findEntityRanges(
      (character) => {
        const entityKey = character.getEntity();
        if (entityKey === null) {
          return false;
        }
        return contentState.getEntity(entityKey).getType() === 'TOKEN';
      },
      callback,
    );
  }

  createRawContentState = (addresses: string[]): RawDraftContentState => {
    const { getContact } = this.props;
    let text = '';
    const entityRanges: any[] = [];
    let length;
    let offset;
    let key;
    let contact;
    let data;
    const entityMap = {};

    addresses.map((address: string, index: number) => {
      length = address.length;
      offset = text.length;
      text += address;
      key = index;

      if (getContact) {
        contact = getContact(addressToEmail(address));
        data = {
          email: contact.email,
          photoUrl: contact.photoUrl,
          fullName: contact.fullName,
          index,
        };
      } else {
        data = { email: addressToEmail(address) };
      }

      entityMap[key] = {
        type: 'TOKEN',
        mutability: 'IMMUTABLE',
        data,
      };
      entityRanges.push({ key, offset, length });
    });

    return {
      blocks: [{
        key: '',
        depth: 0,
        inlineStyleRanges: [],
        text,
        type: 'unstyled',
        entityRanges,
      }],
      entityMap,
    };
  }

  keyBinding = (e: React.KeyboardEvent<HTMLElement>): string => {
    if (e.keyCode === KeyCode.Enter && this.state.popoverShown) {
      return 'new-email-from-popover';
    }

    if (e.keyCode === KeyCode.Enter) {
      return 'new-email';
    }

    if (e.keyCode === KeyCode.Comma && !e.shiftKey) {
      this.setPotentialRecipient();
      if (this.potentialRecipient && parseOneAddress(this.potentialRecipient)) {
        return 'new-email';
      }
    }

    if (e.keyCode === KeyCode.Up) {
      const handled = this.handleUpArrow(e);
      if (handled) {
        return 'pm-up-arrow';
      }
    }

    if (e.keyCode === KeyCode.Down) {
      const handled = this.handleDownArrow(e);
      if (handled) {
        return 'pm-down-arrow';
      }
    }

    if (e.keyCode === KeyCode.Tab) {
      const handled = this.handleTabPress(e);
      if (handled) {
        return 'pm-down-arrow';
      }
    }

    return getDefaultKeyBinding(e) || '';
  }

  setPotentialRecipient = () => {
    const { editorState } = this.state;
    const contentState = editorState.getCurrentContent();
    const text = contentState.getPlainText();
    const block = contentState.getLastBlock();
    const cursorPosition = editorState.getSelection().getFocusOffset() - 1;

    let i;
    let entity;
    for (i = cursorPosition; i >= 0; i -= 1) {
      entity = block.getEntityAt(i);
      if (entity) {
        break;
      }
    }

    const start = i + 1;
    const end = cursorPosition + 1;
    this.potentialRecipient = cursorPosition >= start ? text.slice(start, end) : undefined;
  }

  addRecipientByCursorPosition = () => {
    const { getContact } = this.props;

    if (!this.potentialRecipient) {
      this.setPotentialRecipient();
    }

    if (this.potentialRecipient && getContact) {
      this.addRecipient(getContact(this.potentialRecipient));
    } else {
      this.updateRecipients();
    }

    this.potentialRecipient = '';
    return 'handled';
  }

  addRecipient = (contact: Contact) => {
    const { id, onRecipientsChange } = this.props;
    const { startRecipient } = this.state;
    let newRecipients;
    const recipients: string[] = this.getCurrentEntities();

    if (startRecipient !== undefined) {
      newRecipients = recipients
        .slice(0, startRecipient + 1)
        .concat([`${contact.fullName} <${contact.email}>`]);

      if (startRecipient !== recipients.length - 1) {
        newRecipients = newRecipients.concat(recipients.slice(startRecipient + 1));
      }
    } else {
      newRecipients = [`${contact.fullName} <${contact.email}>`].concat(recipients);
    }

    onRecipientsChange(id, newRecipients);
    this.setState({ startRecipient: undefined });
  }

  addContactGroup = (contactGroup: ContactGroup) => {
    const { id, onContactGroupAdd } = this.props;
    onContactGroupAdd(id, contactGroup);
    this.setState({ startRecipient: undefined });
  }

  getCurrentEntities = () => {
    const { editorState } = this.state;
    const entities: string[] = [];

    const contentState = editorState.getCurrentContent();
    contentState.getLastBlock().findEntityRanges(
      (character) => {
        const entityKey = character.getEntity();
        if (entityKey === null) {
          return false;
        }

        return contentState.getEntity(entityKey).getType() === 'TOKEN';
      },
      (start: number, end: number) => {
        entities.push(contentState.getPlainText().slice(start, end));
      },
    );

    return entities;
  }

  updateRecipients = () => {
    const { id, onRecipientsChange, recipients } = this.props;
    const currentRecipients = this.getCurrentEntities();

    if (!isEqual(recipients, currentRecipients)) {
      onRecipientsChange(id, currentRecipients);
    }
  }

  handleKeyCommand = (command: string) => {
    const { typeaheadContacts } = this.props;
    const { selectedPopoverIndex } = this.state;

    switch (command)  {
      case 'new-email-from-popover': {
        if (typeaheadContacts && typeaheadContacts.length > selectedPopoverIndex) {
          this.handleContactSelect(typeaheadContacts[selectedPopoverIndex]);
          return 'handled';
        }
        break;
      }
      case 'new-email': {
        this.addRecipientByCursorPosition();
        return 'handled';
      }
      case 'pm-up-arrow':
      case 'pm-down-arrow':
      case 'pm-tab':
        return 'handled';
      default:
        return 'not-handled';
    }

    return 'not-handled';
  }

  contactTypeahead = (editorState: EditorState) => {
    const contentState = editorState.getCurrentContent();
    const text = contentState.getPlainText();
    const block = contentState.getLastBlock();
    const cursorPosition = editorState.getSelection().getFocusOffset() - 1;

    let i;
    let entity;
    let startRecipient;
    for (i = cursorPosition; i >= 0; i -= 1) {
      entity = block.getEntityAt(i);
      if (entity) {
        startRecipient = contentState.getEntity(entity).getData().index;
        break;
      }
    }

    let k;
    for (k = cursorPosition; k < text.length; k += 1) {
      entity = block.getEntityAt(k);
      if (entity) {
        break;
      }
    }

    const start = i + 1;
    const end = k === text.length - 1 ? k + 1 : k;
    const query = end > start ? text.slice(start, end) : undefined;
    if (query) {
      this.setState(
        {
          startRecipient,
        },
        () => {
          this.props.searchContactTypeahead(query);
          if (this.props.onSearch) {
            this.props.onSearch(this.props.id);
          }
        },
      );
    } else {
      this.debounced.cancel();
      this.props.clearContactTypeahead();
    }
  }

  selectEntity = (entityKey: string, callback: any, contentState: ContentState) => {
    contentState.getLastBlock().findEntityRanges(
      (character) => {
        const key = character.getEntity();
        if (entityKey.length && key === entityKey) {
          contentState.mergeEntityData(key, { selected: true });
          return true;
        }

        if (key) {
          contentState.mergeEntityData(key, { selected: false });
        }

        return false;
      },
      callback,
    );

    return contentState;
  }

  // onChange gets called after onBlur as well
  handleEditorChange = (editorState: EditorState) => {
    const oldEditorState = this.state.editorState;
    const focused = editorState.getSelection().getHasFocus();
    const prevCursorPosition = oldEditorState.getSelection().getAnchorOffset();
    const cursorPosition = editorState.getSelection().getAnchorOffset();
    const contentState = editorState.getCurrentContent();
    const entityKey = contentState.getLastBlock().getEntityAt(
      prevCursorPosition >= cursorPosition && cursorPosition ?
        cursorPosition - 1 : cursorPosition,
    );
    const blockKey = contentState.getBlockMap().last().getKey();
    const firstFocus = !this.state.editorState.getSelection().getHasFocus() &&
      editorState.getSelection().getHasFocus();
    let newContentState: ContentState = this.selectEntity(
      '',
      undefined,
      contentState,
    );
    let newEditorState: EditorState = editorState;
    const deleteKey = editorState.getLastChangeType() === 'backspace-character';

    if (focused || this.state.popoverShown) {
      let selection = editorState.getSelection();

      if (entityKey && !firstFocus && !deleteKey) {
        newContentState = this.selectEntity(
          entityKey,
          (start: number, end: number) => {
            selection = new SelectionState({
              anchorKey: blockKey,
              anchorOffset: start,
              focusKey: blockKey,
              focusOffset: end,
            });
          },
          contentState,
        );
      }

      this.debounced(editorState);
      newEditorState = EditorState.forceSelection(
        EditorState
          .createWithContent(newContentState, this.compositeDecorator),
        selection,
      );

      if (firstFocus) {
        newEditorState = EditorState.moveFocusToEnd(newEditorState);
      }
    } else if (!focused) {
      newEditorState = EditorState.createWithContent(newContentState, this.compositeDecorator);
      this.popover.hide();
      this.props.clearContactTypeahead();
      this.addRecipientByCursorPosition();

      if (this.props.onBlur) {
        this.props.onBlur();
      }
    }

    this.setState({ editorState: newEditorState }, this.updateRecipients);
  }

  handleEditorPaste = (
    text: string,
  ): DraftHandleValue => {
    const { onRecipientsChange, id, selectContact } = this.props;
    const recipients = text.split(/,|;/);
    const recipient = !!recipients.length && parseAddress(last(recipients)!);

    if (recipient && recipient.email) {
      selectContact(recipient.email);
    }
    onRecipientsChange(id, this.props.recipients.concat(recipients));

    return 'handled';
  }

  handleContactSelect = (contact: Contact|ContactGroup) => {
    const { clearContactTypeahead, selectContact } = this.props;

    // check if contact model
    if ((contact as Contact).email) {
      selectContact((contact as Contact).email);
      this.addRecipient(contact as Contact);
    } else {
      this.addContactGroup(contact as ContactGroup);
    }

    this.debounced.cancel();
    clearContactTypeahead();
  }

  handlePopoverChange = (index: number) => {
    this.setState({ selectedPopoverIndex: index });
  }

  handleRef = (ref: ContactTypeaheadPopover) => {
    this.popover = ref;
  }

  handlePopoverShow = () => {
    this.setState({ popoverShown: true });
  }

  handlePopoverHide = () => {
    this.setState({ popoverShown: false });
  }

  handleUpArrow = (e: React.KeyboardEvent<HTMLElement>) => {
    if (this.state.popoverShown) {
      this.popover.onUpArrow();
      return true;
    }

    return false;
  }

  handleDownArrow = (e: React.KeyboardEvent<HTMLElement>) => {
    if (this.state.popoverShown) {
      this.popover.onDownArrow();
      return true;
    }

    return false;
  }

  handleTabPress = (e: React.KeyboardEvent<HTMLElement>) => {
    const { onTabPress, onShiftTabPress, typeaheadContacts } = this.props;
    const { selectedPopoverIndex } = this.state;
    let handled = false;

    if (this.state.popoverShown) {
      if (typeaheadContacts && typeaheadContacts.length > selectedPopoverIndex) {
        this.handleContactSelect(typeaheadContacts[selectedPopoverIndex]);
        handled = true;
      }
    } else {
      this.addRecipientByCursorPosition();

      if (!e.shiftKey && onTabPress) {
        onTabPress();
        handled = true;
      } else if (onShiftTabPress) {
        onShiftTabPress();
        handled = true;
      }
    }

    return handled;
  }

  focus = () => {
    if (this.editor) {
      clearTimeout(this.timeout);
      this.timeout = global.setTimeout(
        () => {
          let editorState = this.state.editorState;
          editorState = EditorState.moveFocusToEnd(editorState);
          this.setState(
            {
              editorState,
            },
            () => this.handleFocus(),
          );
        },
        0,
      );
    }
  }

  handleFocus = () => {
    const { onFocus, id } = this.props;
    if (onFocus) {
      onFocus(id);
    }
  }

  handleEditorRef = (ref: Editor) => {
    this.editor = ref;
  }

  render() {
    const { typeaheadContacts } = this.props;

    return (
      <div className="recipients-editor">
        <Editor
          ref={this.handleEditorRef}
          editorState={this.state.editorState}
          onChange={this.handleEditorChange}
          handleKeyCommand={this.handleKeyCommand}
          keyBindingFn={this.keyBinding}
          handlePastedText={this.handleEditorPaste}
          onFocus={this.handleFocus}
        />
        <ContactTypeaheadPopover
          contacts={typeaheadContacts || []}
          onSelect={this.handleContactSelect}
          ref={this.handleRef}
          onShow={this.handlePopoverShow}
          onHide={this.handlePopoverHide}
          onChange={this.handlePopoverChange}
        />
      </div>
    );
  }
}

export default RecipientsEditor;
