import { Observable } from 'rxjs';
import { Deps } from '@src/lib/epic';
import { Epic } from 'redux-observable';
import Cache, { Stores } from '@src/lib/db';

import {
  StoreState,
  Action,
  ActionStatus,
  Template,
  Attachment,
  AttachmentMeta,
} from '@src/types';

import {
  TEMPLATE_LIST, templateList,
  TEMPLATE_CREATE, templateCreate,
  TEMPLATE_UPDATE, templateUpdate,
  TEMPLATE_DELETE, templateDelete,
  TEMPLATE_ATTACHMENT_ADD, templateAttachmentAdd,
  TEMPLATE_ATTACHMENT_REMOVE, templateAttachmentRemove,
  TEMPLATE_SHARE, TemplateShareMeta, templateShare,
  TEMPLATE_CONVERT,
  TemplateAttachmentConversionMeta,
  templateAttachmentConvert,
  TEMPLATE_ATTACHMENT_CONVERT,
} from '@src/ducks/templates';
import { DB_READY } from '@src/ducks/db';
import browserHistory from '@src/lib/browserHistory';
import { ApiError } from '@src/api/base';
import { toastSet } from '@src/ducks/toasts';
import { recordEvent, UserEvents } from '@src/lib/analytics';
import { attachmentMetaUpdate } from '@src/ducks/drafts';
import { handleTemplateConversions } from '@src/lib/template';
import { commandControlHide } from '@src/ducks/commandControl';

// DB ready triggers list of templates
export const dbReadyTriggerListTemplate: Epic<Action<string, Template[]>, StoreState> =
  action$ =>
    action$
      .ofType(DB_READY)
      .map(() => templateList(''));

export const listTemplates: Epic<Action<string, Template[]>, StoreState> =
  action$ =>
  action$.ofType(TEMPLATE_LIST)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap(action =>
      Cache.list<Template>(Stores.Templates)
        .map((templates: Template[]) => templateList('', templates)),
    );

export const templateUpdateEpic: Epic<
Action<Template|Error|null, Template|null>, StoreState, Deps
> =
  (action$, store, deps) =>
    action$.ofType(TEMPLATE_UPDATE)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action<Template, Template>) =>
      deps.templates.update(action.meta)
        .map((template: Template) => templateUpdate(action.meta, template))
        .catch((e: Error) => Observable.merge(
          Observable.of(templateUpdate(action.meta, e)),
          Observable.of(toastSet(e)),
        )),
    );

export const templateCreateEpic: Epic<
  Action<Template|Error|null, Template|null>, StoreState, Deps
> =
  (action$, store, deps) =>
    action$.ofType(TEMPLATE_CREATE)
      .filter(action => action.status === ActionStatus.Start)
      .do(action => recordEvent(UserEvents.TemplateCreated))
      .flatMap((action: Action<Template, Template>) =>
        deps.templates.create(action.meta)
          .map(template => templateCreate(action.meta, template))
          .catch(e =>
            Observable.merge(
              Observable.of(templateCreate(action.meta, e)),
              Observable.of(toastSet(e)),
            ),
          ),
      );

// Handles converting a template, and it's attachments, into a new template. Navigates on complete
export const templateConvertEpic: Epic<
  Action<Template|TemplateAttachmentConversionMeta|undefined, Template|Attachment[]|void>,
  StoreState, Deps
> =
  (action$, store, deps) =>
    action$.ofType(TEMPLATE_CONVERT)
      .filter(action => action.status === ActionStatus.Start)
      .do(action => recordEvent(UserEvents.TemplateCreated))
      .flatMap((action: Action<Template, Template>) => {
        const conversions = handleTemplateConversions(action.meta, action.meta.attachments);
        return deps.templates.create(conversions.template)
          .flatMap((template) => {
            const attachmentsConversionMeta: TemplateAttachmentConversionMeta = {
              template: template.id,
              attachments: conversions.attachments,
            };

            return Observable.merge(
              Observable.of(templateCreate(action.meta, template)),
              Observable.of(templateAttachmentConvert(attachmentsConversionMeta)),
              Observable.of(commandControlHide(undefined)),
            );
          })
          .catch(e => Observable.of(templateCreate(action.meta, e)));
      });

export const templateAttachmentConversionEpic: Epic<
  Action<TemplateAttachmentConversionMeta|AttachmentMeta, Attachment[]|Attachment>, StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(TEMPLATE_ATTACHMENT_CONVERT)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<TemplateAttachmentConversionMeta, Attachment>) => {
        const attachmentUploadRequests = action.meta.attachments
          .map(a =>
            deps.templates.addAttachment(
              action.meta.template,
              a.id,
              a.filename,
              a.cloudFile,
              a.inline,
              a.cloud,
            )
            .flatMap((attachment: Attachment) => {
              return Observable.of(
                templateAttachmentAdd(
                  { resourceId: action.meta.template, attachment: attachment.id },
                  attachment,
                ),
              );
            }),
          );
        return Observable.merge(...attachmentUploadRequests);
      });

export const templateCreateSuccessRedirect: Epic<Action<Template, Template>, StoreState, Deps> =
  (action$, store, deps) =>
    action$.ofType(TEMPLATE_CREATE)
      .filter(action => action.status === ActionStatus.Success)
      .do(action => browserHistory.replace(`/templates/${action.payload!.id}`))
      .filter(() => false);

export const templateDeleteEpic: Epic<Action<Template, string>, StoreState, Deps> =
  (action$, store, deps) =>
  action$.ofType(TEMPLATE_DELETE)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap(action =>
      deps.templates.delete(action.meta)
        .map(() => templateDelete(action.meta, action.meta.id))
        .catch((e) => {
          // handle 404 as completion
          if (e as ApiError && e.code === 404) {
            return Observable.of(templateDelete(action.meta, action.meta.id));
          }

          throw e;
        })
        .catch(e => Observable.of(templateDelete(action.meta, e))),
    );

// TODO(SHIN): Handle cloud & inline attachments
export const templateAttachmentAddEpic: Epic<
  Action<AttachmentMeta, Attachment|AttachmentMeta>, StoreState, Deps
> = (action$, store, deps) =>
  action$.ofType(TEMPLATE_ATTACHMENT_ADD)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap(action =>
      deps.templates.uploadAttachment(
        action.meta.resourceId,
        action.meta.attachment,
        action.meta.file!,
        action.meta.inline || false,
        action.meta.cloud || false,
      )
      .flatMap((attachmentOrProgress) => {
        if (typeof attachmentOrProgress === 'number') {
          return Observable.of(attachmentMetaUpdate({
            ...action.meta,
            progress: attachmentOrProgress,
          }));
        }

        return Observable.of(templateAttachmentAdd(action.meta, attachmentOrProgress));
      })
      .catch(e => Observable.of(templateAttachmentAdd(action.meta, e))),
    );

export const templateAttachmentRemoveEpic: Epic<Action<AttachmentMeta, null>, StoreState, Deps> =
  (action$, store, deps) =>
    action$.ofType(TEMPLATE_ATTACHMENT_REMOVE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.templates.removeAttachment(action.meta.resourceId, action.meta.attachment)
          .map(() => templateAttachmentRemove(action.meta, null))
          .catch((e) => {
            // handle 404 as completion
            if (e as ApiError && e.code === 404) {
              return Observable.of(templateAttachmentRemove(action.meta, null));
            }

            throw e;
          })
          .catch(e => Observable.of(templateAttachmentRemove(action.meta, e))),
      );

export const shareTemplate: Epic<
  Action<TemplateShareMeta|Error|null, Template|null>, StoreState, Deps
> =
  (action$, store, deps) =>
    action$.ofType(TEMPLATE_SHARE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<TemplateShareMeta, Template>) =>
        deps.templates
          .share(action.meta.templateId, action.meta.teamId)
          .map(template => templateShare(action.meta, template))
          .catch(e => Observable.of(toastSet(e))),
      );

export default [
  dbReadyTriggerListTemplate,
  listTemplates,
  templateCreateEpic,
  templateCreateSuccessRedirect,
  templateUpdateEpic,
  templateDeleteEpic,
  templateAttachmentAddEpic,
  templateAttachmentRemoveEpic,
  shareTemplate,
  templateConvertEpic,
  templateAttachmentConversionEpic,
];
