import React from 'react';
import { History } from 'history';

import {
  ThreadBox,
  List,
  Mailbox,
  MailboxType,
  Thread,
  Commit,
  KeyCode,
  HotkeyActions,
  Account,
  BannerType,
} from '@src/types';
import Resizable, { NumberSize } from 're-resizable';
import InfiniteThreadList from './components/InfiniteThreadList';
import FilterPopover from './components/FilterPopover';
import SearchInput from '@src/components/Form/SearchInput/SearchInput';
import Disclosure from '@src/components/Icons/Disclosure.svg';

import UndoPopup from '@src/components/Base/UndoPopup';
import AdvancedSearchPopoverConnect from './components/AdvancedSearchPopover';
import AdvancedSearchPopover from './components/AdvancedSearchPopover/AdvancedSearchPopover';
import {
  getHotkey, HotkeyAction, checkNextHotkeyChange, Hotkey, HotkeyCode,
} from '@src/lib/hotkeys';

import './ThreadList.scss';
import { debounce, Cancelable } from 'lodash';
import { ThreadSearchMeta } from '@src/ducks/threads';
import { getSearchDbConfig } from '@src/lib/device';
import { isFocusedOnEditableContent } from '@src/lib/utils';

export interface Props {
  selectedMailbox?: Mailbox;
  lists: List[];
  query?: string;
  threads?: Thread[];
  selectedThread: Thread;
  history?: History;
  loading: boolean;
  hotkeyActions: HotkeyActions;
  width?: number;
  selectedAccount?: Account;
  banner?: BannerType;
  lsbCollapsed?: boolean;
  failedDraftsCount?: number;

  onUndo: () => void;
}

export interface DispatchProps {
  searchThreads: (meta: ThreadSearchMeta) => void;
  updateMailbox: (mailbox: Mailbox) => void;
  updateHotkey: (action: HotkeyAction) => void;
  setWidth: (width: number) => void;
}

interface State {
  loading: boolean;
  listHeight: number;
  title: string;
  query?: string;
  lastMove?: Commit;
}

const THREAD_ACTIONS: ThreadBox[] = [
  ThreadBox.Archive,
  ThreadBox.Trash,
  ThreadBox.List,
  ThreadBox.Later,
  ThreadBox.Inbox,
  ThreadBox.Spam,
];

const THREAD_FILTERS = ['All', 'Unread'];
const SEARCH_HEIGHT = 30;
const HEADER_HEIGHT = 68;

class ThreadList extends
  React.Component<Props & DispatchProps, State> {
  private popover: FilterPopover;
  private searchInput: SearchInput;
  private debounced: ((meta: ThreadSearchMeta) => void) & Cancelable;
  private handleSearchBlurDebounced: any;
  private searchPopover: AdvancedSearchPopover;
  private searchInFocus: boolean;

  constructor(props: Props & DispatchProps) {
    super();

    const mainApp = document.getElementsByClassName('main-app');
    const windowHeight = mainApp[0] ? (mainApp[0] as HTMLElement).offsetHeight : window.innerHeight;

    this.state = {
      loading: false,
      listHeight: windowHeight - SEARCH_HEIGHT - HEADER_HEIGHT,
      title: '',
    };

    this.debounced = debounce(
      props.searchThreads,
      200,
    );

    this.handleSearchBlurDebounced = debounce(
      this.handleSearchBlur,
      200,
    );
  }

  UNSAFE_componentWillMount() {
    this.updateTitle();
  }

  componentDidMount() {
    window.addEventListener('resize', this.handleResize);
    this.handleResize();
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleResize);
    this.debounced.cancel();
    this.handleSearchBlurDebounced.cancel();
  }

  // Custom handling for component update
  shouldComponentUpdate(nextProps: Props, nextState: State) {
    const oldThread = nextProps.selectedThread && nextProps.selectedThread.id || '';
    const newThread = this.props.selectedThread && this.props.selectedThread.id || '';
    if (
      oldThread !== newThread ||
      nextProps.selectedMailbox !== this.props.selectedMailbox ||
      nextState !== this.state ||
      nextProps.loading !== this.props.loading ||
      nextProps.hotkeyActions !== this.props.hotkeyActions ||
      nextProps.query !== this.props.query
    ) {
      return true;
    }
    return false;
  }

  // Handles resetting undo commit when mailbox has changed
  UNSAFE_componentWillUpdate(nextProps: Props) {
    if (this.state.lastMove && nextProps.selectedMailbox !== this.props.selectedMailbox) {
      this.setState({ lastMove: undefined });
    }
  }

  UNSAFE_componentWillReceiveProps(nextProps: Props) {
    // Handle resize for threads list
    if (nextProps.banner !== this.props.banner) {
      // setTimeout(() => , 0);
      this.handleResize();
    }
  }

  // HACK(SHIN): Update focus behavior to not rely on resetting ThreadList on componentDidUpdate
  componentDidUpdate(oldProps: Props & DispatchProps) {
    const { selectedThread, selectedMailbox } = this.props;
    const element = selectedThread && document.getElementById(selectedThread.id);
    this.updateTitle();

    // Handles search hotkey
    const newActions = this.props.hotkeyActions;
    const oldActions = oldProps.hotkeyActions;
    if (newActions !== oldActions) {
      if (checkNextHotkeyChange(HotkeyAction.Search, newActions, oldActions)) {
        this.searchInput.focus(true);
        return;
      }
    }

    // Handle clearing out query
    if (oldProps.selectedMailbox && selectedMailbox) {
      if (!selectedMailbox.query && oldProps.selectedMailbox.query) {
        this.searchInFocus = false;
        this.setState({ query: undefined });
      }
    }

    // HACK(SHIN): Does not remove selection if draft is present or if input already has input
    if (isFocusedOnEditableContent()) {
      return;
    }

    if (element) {
      element.focus();
    } else {
      this.handleFocus();
    }
  }

  handleFocus = (force: boolean = false) => {
    if (!force && this.searchInFocus) {
      return;
    }

    if (document.activeElement && (document.activeElement.tagName.toLowerCase() === 'input' ||
      document.activeElement.className.toLowerCase().includes('drafteditor'))) {
      return;
    }

    this.searchInFocus = false;

    const threadList = document.getElementsByClassName('infinite-thread-list');
    if (threadList && threadList[0]) {
      (threadList[0] as HTMLElement).focus();
    }
  }

  updateTitle = () => {
    const { selectedMailbox } = this.props;
    let newTitle = '';

    switch (selectedMailbox && selectedMailbox.type) {
      case MailboxType.Inbox:
        newTitle = 'Inbox';
        break;
      case MailboxType.Archive:
        newTitle = 'Archive';
        break;
      case MailboxType.Later:
        newTitle = 'Read Later';
        break;
      case MailboxType.Trash:
        newTitle = 'Trash';
        break;
      case MailboxType.Spam:
        newTitle = 'Spam';
        break;
      case MailboxType.Outbox:
        newTitle = 'Outbox';
        break;
      case MailboxType.Drafts:
        newTitle = 'Drafts';
        break;
      case MailboxType.SendLater:
        newTitle = 'Send Later';
        break;
      case MailboxType.Any:
        // starred
        if (selectedMailbox && selectedMailbox.starred === true) {
          newTitle = 'Starred';
          break;
        }

        if (selectedMailbox && selectedMailbox.followUp) {
          newTitle = 'Follow Up';
        }

        if (selectedMailbox && selectedMailbox.sent) {
          newTitle = 'Sent';
        }

        // list name
        if (selectedMailbox && selectedMailbox.list && this.props.lists) {
          const lists = this.props.lists.filter(
            list => list.id === (selectedMailbox && selectedMailbox.list),
          );
          if (lists.length > 0) {
            newTitle = lists[0].name;
          }
        }
        break;
      default:
        break;
    }

    if (selectedMailbox && selectedMailbox.query) {
      newTitle = `Search: ${selectedMailbox.query}`;
    }

    if (newTitle !== this.state.title) {
      this.setState({
        title: newTitle,
      });
    }
  }

  showPopover = () => {
    this.popover.show();
  }

  handleResize = () => {
    const mainApp = document.getElementsByClassName('thread-list');
    const windowHeight = mainApp[0] ? (mainApp[0] as HTMLElement).offsetHeight : window.innerHeight;

    this.setState({ listHeight: windowHeight - SEARCH_HEIGHT - HEADER_HEIGHT });
  }

  handleFilterSelect = (filter: string) => {
    const { selectedMailbox, updateMailbox } = this.props;
    const lowercaseFilter = filter.toLowerCase();

    // Set query parameter according to filter
    // TODO(SHIN & JENDY): Handle query + filter replace vs updating for filter vs query
    if (selectedMailbox) {
      if (lowercaseFilter !== 'all') {
        updateMailbox({ ...selectedMailbox, read: false });
      } else {
        updateMailbox({ ...selectedMailbox, read: undefined });
      }
    }
  }

  handleInfiniteLoad = () => {
    // this.setState({ loading: true });
  }

  handlePopoverRef = (ref: FilterPopover) => {
    this.popover = ref;
  }

  handleSearch = (query: string, complete: boolean) => {
    const { selectedMailbox } = this.props;

    this.setState({ query: complete ? query + ' ' : query }, () => {

      // Start query automatically if searchDb is enabled, otherwise wait for completion event
      if (getSearchDbConfig().enabled || complete) {
        this.debounced({ query, provider: complete });
      }

      if (complete) {
        this.searchPopover.hide();
      }

      // Return focus if completed search with text query param
      if (complete && selectedMailbox &&
        selectedMailbox.queryParams && !!selectedMailbox.queryParams.text) {
        this.searchInFocus = false;
        this.handleFocus(true);
      }

      if (!complete) {
        this.searchInput.focus(true);
      }
    });
  }

  handleMove = (commit: Commit) => {
    if (commit.payload && commit.payload.box) {
      this.setState({ lastMove: commit });
    }
  }

  handleUndo = () => {
    this.setState({ lastMove: undefined });
    this.props.onUndo();
  }

  handleSearchInputRef = (ref: SearchInput) => {
    this.searchInput = ref;
  }

  handleSearchPopoverRef = (ref: any) => {
    if (ref) {
      this.searchPopover = ref.getWrappedInstance();
    }
  }

  handleSearchFocus = () => {
    if (!this.searchInFocus) {
      this.searchPopover.show();
    }

    this.searchInFocus = true;
    this.handleSearchBlurDebounced.cancel();
  }

  handleSearchBlur = () => {
    this.searchInFocus = false;
    this.searchPopover.hide();

    if (!this.state.query) {
      this.handleSearchClear();
    }
  }

  handleKeyDown = (e: React.KeyboardEvent<HTMLElement>) => {
    const { updateHotkey, selectedMailbox, updateMailbox } = this.props;
    const hotkey = getHotkey(e);
    let handled: boolean = false;

    if (hotkey) {

      // Special handling for escape key
      if (e.keyCode === KeyCode.Esc) {
        if (selectedMailbox && selectedMailbox.query) {
          updateMailbox({
            ...selectedMailbox,
            query: undefined,
            queryParams: undefined,
          });
          handled = true;
          this.searchInput?.blur();
          this.searchInFocus = false;
          this.handleFocus(true);
        }

        this.setState({ query: '' }, () => this.searchPopover.hide());
      }

      switch (hotkey.name || (hotkey as Hotkey & HotkeyCode).keyCode) {
        case HotkeyAction.Search:
          handled = true;
          updateHotkey(HotkeyAction.Search);
          break;
        default: return;
      }
    }

    if (handled) {
      e.stopPropagation();
      e.preventDefault();
    }
  }

  handleSearchClear = () => {
    const { updateMailbox, selectedMailbox } = this.props;
    if (selectedMailbox && selectedMailbox.query) {
      updateMailbox({
        ...selectedMailbox,
        query: undefined,
        queryParams: undefined,
      });
    }

    this.handleFocus(true);

    this.setState({ query: '' }, () => this.searchPopover.hide());
  }

  handleSearchKeyDown = (e: React.KeyboardEvent<HTMLElement>) => {
    const { updateMailbox, selectedMailbox } = this.props;
    let handled = false;
    switch (e.keyCode) {
      case KeyCode.Esc:
        if (selectedMailbox && selectedMailbox.query) {
          updateMailbox({
            ...selectedMailbox,
            query: undefined,
            queryParams: undefined,
          });
        }
        this.searchInput?.blur();
        this.searchInFocus = false;
        this.handleFocus(true);
        this.setState({ query: '' }, () => this.searchPopover.hide());
        handled = true;
        break;
      case KeyCode.Down:
        handled = true;
        this.searchPopover.downArrow();
        break;
      case KeyCode.Up:
        handled = true;
        this.searchPopover.upArrow();
        break;
      case KeyCode.Enter:
        handled = true;
        this.searchPopover.enter();
        if (selectedMailbox && selectedMailbox.query) {
          this.searchInput?.blur();
          this.searchInFocus = false;
          this.handleFocus(true);
        }
        break;
      default:
        break;
    }

    if (handled) {
      e.preventDefault();
      e.stopPropagation();
    }
  }

  handleResizeStop = (
    event: MouseEvent,
    direction: string,
    refToElement: HTMLElement,
    delta: NumberSize,
  ) => {
    const { setWidth } = this.props;

    if (refToElement.clientWidth) {
      setWidth(refToElement.clientWidth);
    }
  }

  // HACK(SHIN): Decrease InfiniteThreadList height by 10 due to HEADER_HEIGHT changing.
  // Padding changes for some reason and miscalculates height of table and pushes app content up
  render() {
    const {
      query,
      selectedMailbox,
      loading,
      width,
      selectedAccount,
      lsbCollapsed,
      failedDraftsCount,
    } = this.props;
    const {
      listHeight,
      lastMove,
    } = this.state;

    // Set filter text to unread if mailbox unread filter is set
    const filterText = selectedMailbox && selectedMailbox.read !== undefined
      ? 'Unread'
      : 'All';

    // Determine if we should show the SearchInput loading indicator
    const providerLoading = selectedMailbox &&
      selectedMailbox.providerSearch && !selectedMailbox.providerSearchComplete;
    const showSearchInputLoading = (loading || providerLoading) &&
      !!(selectedMailbox && selectedMailbox.query);

    return (
      <Resizable
        className="thread-list"
        enable={{
          top: false,
          right: true,
          bottom: false,
          left: false,
          topRight: false,
          bottomRight: false,
          bottomLeft: false,
          topLeft: false,
        }}
        defaultSize={{
          width: width || 300,
          height: 'auto',
        }}
        minWidth={200}
        onResizeStop={this.handleResizeStop}
      >
        <div
          className="thread-list-main"
          tabIndex={-1}
          onKeyDown={this.handleKeyDown}
        >
          <SearchInput
            ref={this.handleSearchInputRef}
            onSearch={this.handleSearch}
            onFocus={this.handleSearchFocus}
            onBlur={this.handleSearchBlurDebounced}
            search={query}
            key="search-input"
            height={SEARCH_HEIGHT}
            loadingResults={showSearchInputLoading}
            defaultValue={this.state.query}
            onKeyDown={this.handleSearchKeyDown}
            shouldFocus={this.searchInFocus}
            onClear={this.handleSearchClear}
            placeholder={selectedAccount ? `Search ${selectedAccount.description}` : 'Search'}
          />
          <AdvancedSearchPopoverConnect
            key="advanced-search"
            query={this.state.query || ''}
            onSearch={this.handleSearch}
            ref={this.handleSearchPopoverRef}
          />
          <div className="header" style={{ height: HEADER_HEIGHT }}>
          {selectedAccount && lsbCollapsed && (
              <div className="account-info">
                {selectedAccount.description}
              </div>
            )}
          {selectedMailbox && selectedMailbox.type === MailboxType.Outbox &&
            !!failedDraftsCount && (
              <div className="failed-drafts-disclaimer">
                {failedDraftsCount === 1
                  ? `${failedDraftsCount} draft failed to send`
                  : `${failedDraftsCount} drafts failed to send`
                }
              </div>
            )}
            <h4>{this.state.title}</h4>
            <button onClick={this.showPopover}>
              {filterText}<Disclosure className="disclosure"/>
            </button>
          </div>
          <FilterPopover
            ref={this.handlePopoverRef}
            filters={THREAD_FILTERS}
            onFilterSelect={this.handleFilterSelect}
            selectedFilter={filterText}
          />
          <InfiniteThreadList
            actions={THREAD_ACTIONS}
            listHeight={listHeight}
            key="infinite-thread-list"
            selectedIds={new Map()}
            threads={[]}
            drafts={[]}
            onThreadMove={this.handleMove}
            loading={loading}
          />
          {lastMove &&
            <UndoPopup
              title={`Moved to ${lastMove.payload.box}`}
              onUndoClick={this.handleUndo}
              resourceId={lastMove.resourceId}
              autoDismissDuration={5000}
            />
          }
        </div>
      </Resizable>
    );
  }
}

export default ThreadList;
