import { Observable, Subject, Observer, Subscription } from 'rxjs';
import Cache, { Stores } from '@src/lib/db';
import { patchedNoCompute, computeThread } from '@src/lib/thread';
import { cacheDeleteMessageBodies } from '@src/lib/models/messageBodies';
import { Combiner, rateLimiter } from '@src/lib/rx';
import { applyHistoryDefaults } from '@src/lib/history';
import { History, HistoryObject, RealtimeEvent, Commit, CommitStatus } from '@src/types';
import { HistoryAPI, HistoryClient } from '@src/api/histories';
import { Backoff } from './Backoff';
import { ApiError } from '@src/api/base';
import {
  sendThreadUpdated,
  sendThreadDeleted,
  sendDraftDeleted,
  sendIncrementalSyncStarted,
  sendIncrementalSyncFinished,
  sendTeamMemberUpdated,
  sendSharedThreadDeleted,
  sendSharedThreadUpdated,
  sendFilterDeleted,
  sendFilterUpdated,
  sendMeUpdated,
  sendCalendarsSynced,
} from '@src/worker/bridge/workerEvents';
import bus, { getBus, BusEvent, BusEventName } from '@src/worker/bus';
import { captureException } from '@sentry/browser';
import { compact } from 'lodash';
import { saveThreadsToSearchDb, getSearchDb } from '@src/lib/searchDb';

export class HistorySync {
  // State
  readonly workerId: string;
  team?: string;
  isRunning: boolean;
  backoff: Backoff;
  private cancelSignal: Subject<null>;
  private ratelimit: number;
  private retrigger: boolean;
  private retriggerId: string|undefined;

  // Dependencies
  private events: Observable<RealtimeEvent>;
  private api: HistoryAPI;
  private bus: Subject<BusEvent>;
  private realtimeSub: Subscription;
  private busSub: Subscription;

  constructor(
    events: Observable<RealtimeEvent>,
    team?: string,
    api: HistoryAPI = new HistoryClient(),
  ) {
    this.isRunning = false;
    this.team = team;
    this.cancelSignal = new Subject<null>();
    this.ratelimit = 333; // in milliseconds
    this.workerId = 'HistorySync';
    if (team) {
      this.workerId = 'HistorySync-' + team;
    }
    this.backoff = new Backoff(this.workerId);

    this.events = events;
    this.api = api;
    this.bus = getBus();
    this.retrigger = false;
  }

  init() {
    // Listen for realtime events
    if (!this.realtimeSub) {
      this.realtimeSub = this.events
        .filter((event) => {
          if (event.name !== 'HistoryUpdated') {
            return false;
          }
          if (this.team && this.team !== event.data.team) {
            return false;
          }
          return true;
        })
        .subscribe((event) => {
          if (this.isRunning) {
            this.retrigger = true;
            if (!this.retriggerId || Number(this.retriggerId) > Number(event.data.id)) {
              this.retriggerId = event.data.id;
            }
            return;
          }

          this.run(event.data.id).subscribe();
        });
    }

    // Listen for bus events
    if (!this.busSub) {
      this.busSub = this.bus.subscribe({
        next: (event: BusEvent) => {
          this.handleBusEvent(event);
        },
        error: (e: Error) => {
          captureException(e);
        },
      });
    }
  }

  deinit() {
    if (this.realtimeSub) {
      this.realtimeSub.unsubscribe();
    }
    if (this.busSub) {
      this.busSub.unsubscribe();
    }
  }

  persist(intervalSeconds: number) {
    setInterval(
      () => {
        this.run().subscribe();
      },
      intervalSeconds * 1000,
    );
  }

  // deleteItems does not apply to HistorySync as it does not
  // handle any cache items directly
  clearCache(deleteItems: boolean): Observable<null> {
    if (this.isRunning) {
      this.cancelSignal.next(null);
    }
    return Cache.deleteSingle(Stores.Workers, this.workerId);
  }

  getState(): Observable<string | undefined> {
    return Cache.get(Stores.Workers, this.workerId).map(
      state => (state ? state.offset : undefined),
    );
  }

  saveState(newOffset: string): Observable<any> {
    return Cache.save(Stores.Workers, {
      worker: this.workerId,
      offset: newOffset,
    });
  }

  run(id?: string): Observable<null> {
    return Observable.create((runner: Observer<null>) => {
      if (!this.backoff.isReady()) {
        runner.next(null);
        runner.complete();
        return;
      }
      if (this.isRunning) {
        runner.next(null);
        runner.complete();
        return;
      }
      this.isRunning = true;
      sendIncrementalSyncStarted();

      const limiter = rateLimiter<History[]>(this.ratelimit);

      this.getState().subscribe({
        next: (offset) => {
          // TODO(Jeff): Replace this with int64 library to do this comparison
          // If id is lower than offset, replaced offset with id.
          let adjustedOffset = offset;

          // TODO(SHAHAN): Investigate if we want to fix this
          if (!offset && !id && Number(id) < Number(adjustedOffset)) {
            adjustedOffset = id;
          }

          let initialId = offset === undefined;

          const combiner = new Combiner<History[]>();
          combiner.takeUntil(this.cancelSignal).subscribe({
            next: (histories) => {
              if (histories.length === 0) {
                sendIncrementalSyncFinished();
                combiner.complete();
                return;
              }

              // Checkpoint offset before reversal
              const newOffset = histories[0].id;

              // Merge histories
              const merged = mergeHistories(histories);

              const results: Array<Observable<null>> = [];

              // NOTE(SHAHAN): Want to prevent clear cache loop when fetching the intial id
              if (initialId && histories[0].object === HistoryObject.All && histories[0].clear === 'all') {
                // Don't process any returned data for the initial HistoryId fetch
                // Since that fetch is only needed to get a good HistoryId
                initialId = false;
                results.push(Observable.of(null));
              } else {
                merged.forEach((history) => {
                  switch (history.object) {
                    case HistoryObject.Calendar:
                      results.push(this.handleCalendars(history));
                      break;

                    case HistoryObject.CalendarEvent:
                      results.push(this.handleCalendarEvents(history));
                      break;

                    case HistoryObject.Contact:
                      results.push(this.handleContacts(history));
                      break;

                    case HistoryObject.ContactGroup:
                      results.push(this.handleContactGroups(history));
                      break;

                    case HistoryObject.ContactNote:
                      results.push(this.handleContactNotes(history));
                      break;

                    case HistoryObject.Draft:
                      results.push(this.handleDrafts(history));
                      break;

                    case HistoryObject.Filter:
                      results.push(this.handleFilters(history));
                      break;

                    case HistoryObject.MessageBody:
                      results.push(this.handleMessageBodies(history));
                      break;

                    case HistoryObject.SharedThread:
                      results.push(this.handleSharedThreads(history));
                      break;

                    case HistoryObject.TeamMember:
                      results.push(this.handleTeamMembers(history));
                      break;

                    case HistoryObject.Template:
                      results.push(this.handleTemplates(history));
                      break;

                    case HistoryObject.Thread:
                      results.push(this.handleThreads(history));
                      break;

                    case HistoryObject.All:
                      if (history.clear === 'all') {
                        bus.historyClearCache();
                      }
                      break;

                    default:
                      break;
                  }
                });
              }

              // Resolve and recursively call upon completion
              Observable.zip(...results)
                .subscribe({
                  error: (e) => {
                    combiner.error(e);
                  },
                  complete: () => {
                    this.saveState(newOffset).subscribe({
                      error: (e) => {
                        combiner.error(e);
                      },
                      complete: () => {
                        combiner.run(limiter(this.fetchNextPage(newOffset)));
                      },
                    });
                  },
                });
            },
            error: (e: Error) => {
              this.isRunning = false;
              sendIncrementalSyncFinished();

              // Clear cache with 400
              if ((e as ApiError).code === 400) {
                bus.historyClearCache();
              }

              // Handle 401 error, we send a MeUpdated event to main thread
              // The meFetch epic then correctly handles the neccessary steps
              // to mark the user as logged out and trigger a clear cache
              if ((e as ApiError).code === 401) {
                sendMeUpdated();
              }

              // Otherwise set error and backoff
              this.backoff.setError(e);
              runner.next(null);
              runner.complete();
            },
            complete: () => {
              this.isRunning = false;
              sendIncrementalSyncFinished();
              runner.next(null);
              runner.complete();

              // Handle retrigger
              if (this.retrigger) {
                this.retrigger = false;
                bus.triggerSync(this.workerId);
              }
            },
          });

          // Buffer initial fetch to alleviate archive bounce issue
          combiner.run(
            Observable.of(null)
              .delay(500)
              .flatMap(() => this.fetchNextPage(adjustedOffset)),
          );
        },
        error: (e: Error) => {
          this.isRunning = false;
          this.backoff.setError(e);
          runner.next(null);
          runner.complete();
        },
      });
    });
  }

  private handleCalendars(history: History): Observable<null> {
    if (history.clear === 'all') {
      // HACK(SHIN): Call clear for Calendar Events since Calendars is associated
      // and cleared from CalendarEventSync. If not pointed to CalendarEventSync,
      // this request will stall HistorySync
      return bus.clearResourceRequest(Stores.CalendarEvents, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache
        .deleteBatch(Stores.Calendars, history.deleted)
        .do(() => sendCalendarsSynced());
    }
    if (history.calendars && history.calendars.length > 0) {
      return Cache
        .load(Stores.Calendars, history.calendars)
        .do(() => sendCalendarsSynced());
    }
    return Observable.of(null);
  }

  private handleCalendarEvents(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.CalendarEvents, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache
        .deleteBatch(Stores.CalendarEvents, history.deleted)
        .do(() => sendCalendarsSynced());
    }
    if (history.calendarEvents && history.calendarEvents.length > 0) {
      return Cache
        .load(Stores.CalendarEvents, history.calendarEvents)
        .do(() => sendCalendarsSynced());
    }
    return Observable.of(null);
  }

  private handleContacts(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.Contacts, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.Contacts, history.deleted);
    }
    if (history.contacts && history.contacts.length > 0) {
      return Cache.load(Stores.Contacts, history.contacts);
    }
    return Observable.of(null);
  }

  private handleContactGroups(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.ContactGroups, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.ContactGroups, history.deleted);
    }
    if (history.contactGroups && history.contactGroups.length > 0) {
      return Cache.load(Stores.ContactGroups, history.contactGroups);
    }
    return Observable.of(null);
  }

  private handleContactNotes(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.ContactNotes, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.ContactNotes, history.deleted);
    }
    if (history.contactNotes && history.contactNotes.length > 0) {
      return Cache.load(Stores.ContactNotes, history.contactNotes);
    }
    return Observable.of(null);
  }

  private handleDrafts(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.Drafts, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.Drafts, history.deleted).flatMap(() => {
        history.deleted.forEach(id => sendDraftDeleted(id));
        return Observable.of(null);
      });
    }
    return Observable.of(null);
  }

  private handleFilters(history: History): Observable<null> {
    if (history.deleted && history.deleted.length > 0) {
      history.deleted.forEach(id => sendFilterDeleted(id));
    }

    if (history.filters && history.filters.length > 0) {
      history.filters.forEach(filter => sendFilterUpdated(filter));
    }
    return Observable.of(null);
  }

  private handleMessageBodies(history: History): Observable<null> {
    if (history.clear === 'all') {
      return Cache.clear(Stores.MessageBodies);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.MessageBodies, history.deleted);
    }
    return Observable.of(null);
  }

  private handleSharedThreads(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.SharedThreads, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.SharedThreads, history.deleted)
        .flatMap(() => {
          history.deleted.forEach(id => sendSharedThreadDeleted(id));
          return Observable.of(null);
        });
    }
    if (history.sharedThreads && history.sharedThreads.length > 0) {
      return Cache.load(Stores.SharedThreads, history.sharedThreads)
        .flatMap(() => {
          history.sharedThreads.forEach(sharedThread => sendSharedThreadUpdated(sharedThread));
          return Observable.of(null);
        });
    }
    return Observable.of(null);
  }

  private handleTeamMembers(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.TeamMembers, this.team)
        .do(() => sendTeamMemberUpdated());
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.TeamMembers, history.deleted)
        .do(() => sendTeamMemberUpdated());
    }
    if (history.teamMembers && history.teamMembers.length > 0) {
      return Cache.load(Stores.TeamMembers, history.teamMembers)
        .do(() => sendTeamMemberUpdated());
    }
    return Observable.of(null);
  }

  private handleTemplates(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.Templates, this.team);
    }
    if (history.deleted && history.deleted.length > 0) {
      return Cache.deleteBatch(Stores.Templates, history.deleted);
    }
    return Observable.of(null);
  }

  private handleThreads(history: History): Observable<null> {
    if (history.clear === 'all') {
      return bus.clearResourceRequest(Stores.Threads, this.team);
    }

    if (history.deleted && history.deleted.length > 0) {
      // Clear message bodies cache and delete threads
      return Observable.zip(...history.deleted.map(id => cacheDeleteMessageBodies(id)))
        .flatMap(() => Cache.deleteBatch(Stores.Threads, history.deleted))
        // Deletes Polymail threads
        .flatMap(() => Observable.from(
          getSearchDb().threads.where('id').anyOf(history.deleted).delete(),
        ))
        .map(() => {
          history.deleted.forEach(id => sendThreadDeleted(id));
          // TODO(Shahan): Should call bus.updateMailCount
          // once we figure out how to get the account id
          // for the thread here.
          return null;
        });
    }
    if (history.threads && history.threads.length > 0) {
      // Get unresolved thread commits
      return listUnresolvedCommits(HistoryObject.Thread).flatMap((unresolvedCommits) => {
        return Observable.zip(
          ...history.threads.map((thread) => {
            // Get commits for this thread id
            const commits = unresolvedCommits.filter(commit => commit.resourceId === thread.id);

            // Patch commits
            let patchedThread = thread;
            let deleted = false;
            commits.forEach((commit) => {
              if (commit.method === 'PATCH') {
                patchedThread = patchedNoCompute(patchedThread, commit);
              }
              if (commit.method === 'DELETE') {
                deleted = true;
              }
            });

            // Don't save if we find a delete commit
            if (deleted) {
              return Observable.of(null);
            }

            // Save patched & computed thread to cache and emit out
            patchedThread = computeThread(patchedThread);

            // Update MailCountBot to reset empty list hash if unread and in list
            if (patchedThread.list && !patchedThread.read) {
              bus.updateListUnreadCount(patchedThread.account, patchedThread.list);
            }

            return Observable.of(patchedThread);
          }),
        )
        .flatMap((threads) => {
          // Bulk Save threads into polymailDb
          const compacted = compact(threads);

          // Bulk save threads
          return Cache.load(Stores.Threads, compacted)
            .flatMap(() => saveThreadsToSearchDb(compacted))
            .flatMap(() => {
              compacted.forEach((patchedThread) => {
                sendThreadUpdated(patchedThread);
                bus.updateMailCount(patchedThread.account);
              });
              return Observable.of(null);
            });
        })
        .map(() => null);
      });
    }
    return Observable.of(null);
  }

  private fetchNextPage(offset?: string): Observable<History[]> {
    // NOTE(SHAHAN): Can't no longer turn off retrigger here in case retriggerId
    // is older then offset. Other race conditions exist too with retrigger.
    if (this.team) {
      return this.api.listByTeam(this.team, offset);
    }
    return this.api.list(offset);
  }

  private handleBusEvent(event: BusEvent) {
    switch (event.name) {
      case BusEventName.ClearErrors:
        this.backoff.clearErrors();
        break;

      case BusEventName.CancelSync:
        if (event.data.workerId && event.data.workerId !== this.workerId) {
          break;
        }
        this.cancelSignal.next(null);
        return;

      case BusEventName.TriggerSync:
        if (event.data.workerId && event.data.workerId !== this.workerId) {
          break;
        }
        if (this.isRunning) {
          this.retrigger = true;
          return;
        }
        this.run(this.retriggerId).subscribe();
        this.retriggerId = undefined;
        break;

      case BusEventName.HistoryClearCache:
        this.clearCache(false).subscribe();
        break;

      default:
        break;
    }
  }
}

// mergeHistories takes in histories in reverse chronological order.
export const mergeHistories = (histories: History[]): History[] => {
  // Keep track of clear cache events (key is HistoryObect)
  const clearAllMap: Map<string, boolean> = new Map([]);

  // Keep track of all update and deletion events (key is HistoryObject)
  const mergedMap: Map<string, History> = new Map([]);

  // Keep track of all seen update and deletions (key is HistoryObect + resourceIdd)
  const seen: Map<string, boolean> = new Map([]);
  histories.forEach((history) => {
    const clearAll = clearAllMap.get(history.object) ? true : false;
    if (clearAll) {
      return;
    }
    if (history.clear === 'all') {
      clearAllMap.set(history.object, true);
    }

    let existing = mergedMap.get(history.object);
    if (!existing) {
      existing = applyHistoryDefaults({ id: '', object: history.object });
    }
    mergedMap.set(history.object, mergeHistory(seen, existing, history));
  });

  // Resolve clear caches first
  const mergedHistories: History[] = [];
  clearAllMap.forEach((_, object) => {
    const historyObject = object as HistoryObject;
    mergedHistories.push(applyHistoryDefaults({ id: '', object: historyObject, clear: 'all' }));
  });

  // Then append histories for each resource
  // NOTE(Jeff): Need to split deletion and update histories because
  // history handler function expect only one or the other array, never both.
  mergedMap.forEach((history) => {
    if (history.deleted.length > 0) {
      mergedHistories.push(
        applyHistoryDefaults({
          id: '',
          object: history.object,
          deleted: history.deleted,
        }),
      );
      mergedHistories.push({ ...history, deleted: [] });
    } else {
      mergedHistories.push(history);
    }
  });
  return mergedHistories;
};

const mergeHistory = (seen: Map<string, boolean>, existing: History, history: History): History => {
  const merged = { ...existing };

  // Accumulated deletion ids
  history.deleted.forEach((id) => {
    const key = history.object + id;
    if (seen.has(key)) {
      return;
    }
    merged.deleted.push(id);
    seen.set(key, true);
  });

  // Handle specific object types
  switch (history.object) {
    case HistoryObject.Contact:
      history.contacts.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.contacts.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.ContactGroup:
      history.contactGroups.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.contactGroups.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.ContactNote:
      history.contactNotes.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.contactNotes.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.TeamMember:
      history.teamMembers.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.teamMembers.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.SharedThread:
      history.sharedThreads.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.sharedThreads.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.Thread:
      history.threads.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.threads.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.Filter:
      history.filters.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.filters.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.Calendar:
      history.calendars.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.calendars.push(model);
        seen.set(key, true);
      });
      break;

    case HistoryObject.CalendarEvent:
      history.calendarEvents.forEach((model) => {
        const key = history.object + model.id;
        if (seen.has(key)) {
          return;
        }
        merged.calendarEvents.push(model);
        seen.set(key, true);
      });
      break;

    default:
      break;
  }
  return merged;
};

function listUnresolvedCommits(resource: HistoryObject): Observable<Commit[]> {
  return Cache.list<Commit>(Stores.Commits, (commit) => {
    if (commit.resource !== resource) {
      return false;
    }
    if (commit.status !== CommitStatus.Ready && commit.status !== CommitStatus.Processing) {
      return false;
    }
    return true;
  });
}

export default HistorySync;
