import { Observable } from 'rxjs';

import {
  SequenceRecipient, SequenceRecipientFilter, SequenceRecipientStatus,
} from '@src/types/sequences';
import { jsonRequest, blankRequest, sequenceRecipientBatchRequest } from './base';
import { createSequenceRecipient } from '@src/mocks/sequences';
import { applySequenceRecipientDefaults } from '@src/lib/models/sequenceRecipients';
import {
  SequenceRecipientUploadPayload,
  SequenceRecipientListMeta,
  SequenceRecipientCreatePayload,
  SequenceRecipientCreateRecipient,
} from '@src/ducks/sequenceRecipients';

interface SequenceCreateBody {
  sequence: string;
  recipients: SequenceRecipientCreateRecipient[];
}

interface SequenceRecipientValidateBody {
  email: string;
  sequence: string;
  givenName?: string;
  familyName?: string;
  variables?: { [header: string]: string };
}

export interface SequenceRecipientAPI {
  list(meta: SequenceRecipientListMeta): Observable<SequenceRecipient[]>;
  upload(
    sequence: string,
    cloudFile: string,
    emailHeader: string,
    givenNameHeader?: string,
    familyNameHeader?: string,
  ): Observable<SequenceRecipientUploadPayload>;
  update(recipient: Partial<SequenceRecipient>): Observable<SequenceRecipient>;
  delete(recipient: SequenceRecipient): Observable<null>;
  create(recipient: SequenceCreateBody):
    Observable<SequenceRecipientCreatePayload>;
  validate(recipient: SequenceRecipientValidateBody):
    Observable<SequenceRecipient>;
  reactivate(recipient: SequenceRecipient): Observable<SequenceRecipient>;
}

export class SequenceRecipientClient implements SequenceRecipientAPI {
  list(meta: SequenceRecipientListMeta): Observable<SequenceRecipient[]> {
    const query = new URLSearchParams();

    if (meta.query) {
      query.append('q', meta.query);
    }

    if (meta.filter && meta.filter !== SequenceRecipientFilter.All) {
      query.append('filter', meta.filter.toLowerCase());
    }

    if (meta.sort) {
      query.append('sort', meta.sort);
      if (meta.desc) {
        query.append('desc', 'true');
      }
    }

    if (meta.offset) {
      query.append('offset', meta.offset.toString());
    }

    const queryString = query.toString();
    return jsonRequest({
      method: 'GET',
      route: !!queryString ?
        `/v1/sequences/${meta.sequence}/recipients?${queryString}` :
        `/v1/sequences/${meta.sequence}/recipients`,
    })
    .map(recipients => recipients.map(
      (r: SequenceRecipient) => applySequenceRecipientDefaults(r),
    ));
  }

  upload(
    sequence: string,
    cloudFile: string,
    emailHeader: string,
    givenNameHeader: string,
    familyNameHeader: string,
  ): Observable<SequenceRecipientUploadPayload> {
    return jsonRequest({
      method: 'POST',
      route: `/v5/sequences/${sequence}/recipients/upload`,
      body: {
        cloudFile,
        emailHeader,
        givenNameHeader,
        familyNameHeader,
      },
    })
    .map((results: SequenceRecipientUploadPayload) => ({
      ...results,
      recipients: results.recipients
        .map(applySequenceRecipientDefaults),
      invalidRecipients: results.invalidRecipients
        .map(applySequenceRecipientDefaults),
    }));
  }

  create(meta: SequenceCreateBody): Observable<SequenceRecipientCreatePayload> {
    return sequenceRecipientBatchRequest({
      requests: meta.recipients.map(r => ({
        method: 'POST',
        url: `/v1/sequences/${meta.sequence}/recipients`,
        body: JSON.stringify(r),
      })),
    })
    .map((response) => {
      const recipients: SequenceRecipient[] = [];
      const invalidRecipients: SequenceRecipientCreateRecipient[] = [];

      response.forEach((r, i) => {
        if (r.code === 201) {
          recipients.push(applySequenceRecipientDefaults(r.body));
        } else {
          invalidRecipients.push({
            ...meta.recipients[i],
            status: SequenceRecipientStatus.Invalid,
            invalidReason: r.body.description,
          });
        }
      });

      return ({
        recipients,
        invalidRecipients,
      });
    });
  }

  update(recipient: Partial<SequenceRecipient>): Observable<SequenceRecipient> {
    return jsonRequest({
      method: 'PATCH',
      route: `/v1/sequences/${recipient.sequence}/recipients/${recipient.id}`,
      body: recipient,
    })
      .map(s => applySequenceRecipientDefaults(s));
  }

  delete(recipient: SequenceRecipient): Observable<null> {
    return blankRequest({
      method: 'DELETE',
      route: `/v1/sequences/${recipient.sequence}/recipients/${recipient.id}`,
    });
  }

  validate(recipient: SequenceRecipientValidateBody): Observable<SequenceRecipient> {
    return jsonRequest({
      method: 'POST',
      route: `/v2/sequences/${recipient.sequence}/recipients/validate`,
      body: {
        email: recipient.email,
        givenName: recipient.givenName,
        familyName: recipient.familyName,
        variables: recipient.variables,
      },
    }).map(s => applySequenceRecipientDefaults(s));
  }

  reactivate(recipient: SequenceRecipient): Observable<SequenceRecipient> {
    return jsonRequest({
      method: 'POST',
      route: `/v1/sequences/${recipient.sequence}/recipients/${recipient.id}/reactivate`,
    });
  }
}

export function mockSequenceRecipientClient(): SequenceRecipientAPI {
  return {
    list: () => Observable.of([createSequenceRecipient()]),
    upload: info => Observable.of({
      recipients: [createSequenceRecipient()],
      invalidRecipients: [],
    }),
    update: recipient => Observable.of(createSequenceRecipient(recipient)),
    delete: recipient => Observable.of(null),
    create: recipient => Observable.of({
      recipients: [createSequenceRecipient()],
      invalidRecipients: [],
    }),
    validate: recipient => Observable.of(createSequenceRecipient(recipient)),
    reactivate: recipient => Observable.of({
      ...createSequenceRecipient(recipient),
      status: SequenceRecipientStatus.Active,
    }),
  };
}
