import { IScheduler } from 'rxjs/Scheduler';
import { Epic, combineEpics } from 'redux-observable';

import { MeAPI, meClient, mockMeClient } from '@src/api/me';
import { DraftAPI, DraftClient, mockDraftClient } from '@src/api/drafts';
import { ThreadAPI, ThreadClient, mockThreadClient } from '@src/api/threads';
import { TemplateAPI, TemplateClient, mockTemplateClient } from '@src/api/templates';
import { ListAPI, ListClient, mockListClient } from '@src/api/lists';
import { CloudFileAPI, CloudFileClient, mockCloudFileClient } from '@src/api/cloudFile';
import {
  CalendarEventAPI, CalendarEventClient, mockCalendarEventClient,
} from '@src/api/calendarEvents';

import { StoreState } from '@src/types';
import { DevicesAPI, mockDevicesClient, DevicesClient } from '@src/api/devices';
import { AccountAPI, mockAccountClient, AccountClient } from '@src/api/account';
import { IntegrationClient, mockIntegrationClient, IntegrationAPI } from '@src/api/integrations';
import { mockCalendarClient, CalendarAPI, CalendarClient } from '@src/api/calendars';
import { mockHubspotClient, HubspotAPI, HubspotClient } from '@src/api/hubspot';
import { mockSalesforceClient, SalesforceAPI, SalesforceClient } from '@src/api/salesforce';
import { SequenceAPI, SequenceClient, mockSequenceClient } from '@src/api/sequences';
import {
  SequenceStageAPI, SequenceStageClient, mockSequenceStageClient,
} from '@src/api/sequenceStages';
import {
  SequenceStageInstanceAPI, SequenceStageInstanceClient, mockSequenceStageInstanceClient,
} from '@src/api/sequenceStageInstances';
import {
  SequenceStageRecipientAPI,
  SequenceStageRecipientClient,
  mockSequenceStageRecipientClient,
} from '@src/api/sequenceStageRecipients';
import {
  SequenceRecipientClient, SequenceRecipientAPI, mockSequenceRecipientClient,
} from '@src/api/sequenceRecipients';
import { BillingAPI, BillingClient, mockBillingClient } from '@src/api/billing';
import { SchedulingClient, mockSchedulingClient, SchedulingAPI } from '@src/api/scheduling';
import { TeamMemberAPI, TeamMemberClient, mockTeamMemberClient } from '@src/api/teamMembers';
import { TeamAPI, TeamClient, mockTeamClient } from '@src/api/team';
import { AppAPI, AppClient, mockAppClient } from '@src/api/app';
import {
  SharedThreadAPI, SharedThreadClient, mockSharedThreadClient,
} from '@src/api/sharedThreads';
import { CommentAPI, CommentClient, mockCommentClient } from '@src/api/comment';
import {
  NotificationAPI,
  NotificationClient,
  mockNotificationClient,
} from '@src/api/notifications';
import {
  ReferralInviteAPI, ReferralInviteClient, mockReferralInviteClient,
} from '@src/api/referralInvites';
import { SlackAPI, SlackClient, mockSlackClient } from '@src/api/slack';
import { AsanaAPI, AsanaClient, mockAsanaClient } from '@src/api/asana';
import { ContactClient, ContactAPI, mockContactClient } from '@src/api/contacts';
import {
  MessageBodiesAPI,
  MessageBodiesClient,
  mockMessageBodiesClient,
} from '@src/api/messageBodies';
import { FilterAPI, FilterClient, mockFilterClient } from '@src/api/filters';
import { mockZoomClient, ZoomAPI, ZoomClient } from '@src/api/zoom';
import { AnalyticsAPI, AnalyticsClient, mockAnalyticsClient } from '@src/api/analytics';

export interface Deps {
  accounts: AccountAPI;
  app: AppAPI;
  analytics: AnalyticsAPI;
  asana: AsanaAPI;
  billing: BillingAPI;
  calendarEvents: CalendarEventAPI;
  calendars: CalendarAPI;
  cloudFile: CloudFileAPI;
  comments: CommentAPI;
  contacts: ContactAPI;
  drafts: DraftAPI;
  devices: DevicesAPI;
  filters: FilterAPI;
  integrations: IntegrationAPI;
  hubspot: HubspotAPI;
  lists: ListAPI;
  me: MeAPI;
  now: () => Date;
  notifications: NotificationAPI;
  referralInvites: ReferralInviteAPI;
  salesforce: SalesforceAPI;
  scheduling: SchedulingAPI;
  scheduler?: IScheduler;
  sequences: SequenceAPI;
  sequenceRecipients: SequenceRecipientAPI;
  sequenceStages: SequenceStageAPI;
  sequenceStageInstances: SequenceStageInstanceAPI;
  sequenceStageRecipients: SequenceStageRecipientAPI;
  sharedThreads: SharedThreadAPI;
  slack: SlackAPI;
  team: TeamAPI;
  teamMembers: TeamMemberAPI;
  templates: TemplateAPI;
  threads: ThreadAPI;
  messageBodies: MessageBodiesAPI;
  zoom: ZoomAPI;
}

export function defaultDeps(): Deps {
  return {
    accounts: new AccountClient(),
    app: new AppClient(),
    analytics: new AnalyticsClient(),
    asana: new AsanaClient(),
    billing: new BillingClient(),
    calendarEvents: new CalendarEventClient(),
    calendars: new CalendarClient(),
    cloudFile: new CloudFileClient(),
    comments: new CommentClient(),
    contacts: new ContactClient(),
    devices: new DevicesClient(),
    drafts: new DraftClient(),
    filters: new FilterClient(),
    hubspot: new HubspotClient(),
    integrations: new IntegrationClient(),
    lists: new ListClient(),
    me: meClient,
    notifications: new NotificationClient(),
    now: () => new Date(),
    referralInvites: new ReferralInviteClient(),
    salesforce: new SalesforceClient(),
    scheduling: new SchedulingClient(),
    sequences: new SequenceClient(),
    sequenceRecipients: new SequenceRecipientClient(),
    sequenceStages: new SequenceStageClient(),
    sequenceStageInstances: new SequenceStageInstanceClient(),
    sequenceStageRecipients: new SequenceStageRecipientClient(),
    sharedThreads: new SharedThreadClient(),
    slack: new SlackClient(),
    team: new TeamClient(),
    teamMembers: new TeamMemberClient(),
    templates: new TemplateClient(),
    threads: new ThreadClient(),
    messageBodies: new MessageBodiesClient(),
    zoom: new ZoomClient(),
  };
}

export function mockDeps(): Deps {
  return {
    accounts: mockAccountClient(),
    app: mockAppClient(),
    analytics: mockAnalyticsClient(),
    asana: mockAsanaClient(),
    billing: mockBillingClient(),
    calendarEvents: mockCalendarEventClient(),
    calendars: mockCalendarClient(),
    cloudFile: mockCloudFileClient(),
    comments: mockCommentClient(),
    contacts: mockContactClient(),
    devices: mockDevicesClient(),
    drafts: mockDraftClient(),
    filters: mockFilterClient(),
    hubspot: mockHubspotClient(),
    integrations: mockIntegrationClient(),
    lists: mockListClient(),
    me: mockMeClient(),
    notifications: mockNotificationClient(),
    now: () => new Date(),
    referralInvites: mockReferralInviteClient(),
    salesforce: mockSalesforceClient(),
    scheduling: mockSchedulingClient(),
    sequences: mockSequenceClient(),
    sequenceRecipients: mockSequenceRecipientClient(),
    sequenceStages: mockSequenceStageClient(),
    sequenceStageInstances: mockSequenceStageInstanceClient(),
    sequenceStageRecipients: mockSequenceStageRecipientClient(),
    sharedThreads: mockSharedThreadClient(),
    slack: mockSlackClient(),
    team: mockTeamClient(),
    teamMembers: mockTeamMemberClient(),
    threads: mockThreadClient(),
    templates: mockTemplateClient(),
    messageBodies: mockMessageBodiesClient(),
    zoom: mockZoomClient(),
  };
}

// reference: https://github.com/redux-observable/redux-observable/issues/94#issuecomment-277351312
const wrapEpic = (epic: Epic<any, StoreState, Deps>) => (a: any, s: any, d: any) =>
  epic(a, s, d).catch((error: any, source: any) => {
    // tslint:disable-next-line:no-console
    console.error(error);
    return source;
  });

export const combineWrapEpics = (epics: Array<Epic<any, StoreState, Deps>>) =>
  combineEpics<any>(...epics.map(wrapEpic));
