import { Observable, Observer } from 'rxjs';
import { FileUploadMeta, Commit, CommitStatus, HistoryObject, Draft } from '@src/types';
import { initSearchDb, getSearchDb } from './searchDb';
import { getSearchDbConfig } from './device';
import { AUTH_TOKEN_NONE } from '@src/api/base';
import { getCreateClearCacheStatus, saveClearCacheStatus } from './cache';
import { sendTriggerElectronClearCache } from '@src/worker/bridge/workerEvents';
import { deleteFile, unserializeFile, serializeFile } from './filesystem';
import { fetchStagedDrafts } from './models/draft';
import { fetchRefreshCommits } from './commit';
import { LogLevel } from '@src/api/log';
import { recordLogFunc } from './log_worker';

// Enable debug / logging of IndexDB call times
let userDebug = false;
let userDebugPrefix = '';
let userDebugCb: recordLogFunc | undefined;
let forceDebug = false;

export function enableForceDebug() {
  forceDebug = true;

  // tslint:disable-next-line:no-console
  console.debug('Enabled IDB Logs to Console.');
}

export function enableUserDebug(prefix: string, cb: recordLogFunc) {
  userDebug = true;
  userDebugPrefix = prefix;
  userDebugCb = cb;

  if (userDebugPrefix !== '') {
    userDebugPrefix += ': ';
  }

  // tslint:disable-next-line:no-console
  console.debug('Enabled IDB User Logs.', { userDebug, userDebugPrefix });
}

export function disableUserDebug() {
  if (!userDebug) {
    return;
  }

  userDebug = false;
  userDebugPrefix = '';
  userDebugCb = undefined;

  // tslint:disable-next-line:no-console
  console.debug('Disabled IDB User Logs.');
}

function debugLog(store: Stores, timer: number, method: string, data?: any) {

  if (userDebug && userDebugCb) {
    let message = userDebugPrefix + 'Completed IDB-' + method + '-' + store
      + ' in ' + (performance.now() - timer).toFixed(2) + 'ms';

    if (data && typeof test !== 'function') {
      // Extract index?
      if (data.index) {
        message += '. Index: ' + data.index;
      }

      // Extract range of object
      if (data.range) {
        message += '. Range: ' + JSON.stringify(data.range);
      }

      // Extract count of object
      if (data.length) {
        message += '. Count: ' + data.length;
      }

      // Extract length of object
      const parsed = JSON.stringify(data);
      if (parsed) {
        message += '. Bytes: ' + parsed.length;
      }
    }

    userDebugCb(LogLevel.Debug, message);
  }

  if (forceDebug) {
    // tslint:disable-next-line:no-console
    console.debug(
      'IDB-' + method + '-' + store,
      (performance.now() - timer).toFixed(2) + 'ms',
      data,
    );
  }
}

// Clear Cache Mechanism
export enum ClearCacheMechanism {
  Default,
  ElectronBridge,
  WorkerElectronBridge,
}

let clearCacheMechanism = ClearCacheMechanism.Default;

export function setClearCacheMechanism(mechanism: ClearCacheMechanism) {
  clearCacheMechanism = mechanism;
}

export function getClearCacheMechanism(): ClearCacheMechanism {
  return clearCacheMechanism;
}

// Stores enum for
export enum Stores {
  ActivityFeed = 'activityFeed',
  Calendars = 'calendars',
  CalendarEvents = 'calendarEvents',
  Commits = 'commits',
  ContactGroups = 'contactGroups',
  ContactNotes = 'contactNotes',
  Contacts = 'contacts',
  Drafts = 'drafts',
  FileUploads = 'fileUploads',
  MessageBodies = 'messageBodies',
  SharedThreads = 'sharedThreads',
  TeamMembers = 'teamMembers',
  Templates = 'templates',
  Threads = 'threads',
  Workers = 'workers',
  KeyValue = 'keyValue',
}

// Version needs to be incremented when adding stores or indexes
const DB_NAME = 'polymail';
const DB_VERSION = 29;
// Users who update, and have version below this number will have a clear cache
const DB_MIN_VERSION_NO_CLEAR_CACHE = 29;

// Increment to update db via the migrateDb function below
// This allows migrations without a full clear cache
export const DB_MIGRATION_VERSION = 1;

const COMMITS_RESTORE_FILENAME = 'commits-restore.json';
const DRAFTS_RESTORE_FILENAME = 'drafts-restore.json';

// IMPORTANT: READ WHEN UPGRADING https://dexie.org/docs/Tutorial/Design#database-versioning
export const SEARCH_DB_VERSION = DB_MIN_VERSION_NO_CLEAR_CACHE * 1.0 / 10;

// Holds the indexed db instance
let _db: IDBDatabase|null = null;
let dbOpen = true;
let allowReconnect = true;

const closeEventListener = () => {
  dbOpen = false;
  _db?.removeEventListener('close', closeEventListener);
  _db = null;
};

// isDbReady returns whether the db connection is ready
export const isDbReady = (): boolean =>  {
  return _db !== null && dbOpen;
};

// migrateDb allows upgrading data in a database without a full clear cache
function migrateDb(currentVersion: number): Observable<null> {
  const ops: Observable<null>[] = [];

  if (currentVersion < 1) {
    // Clear out calendars, calendar events, and sync state for calendars
    ops.push(clear(Stores.Calendars));
    ops.push(clear(Stores.CalendarEvents));
    ops.push(
      listRange(Stores.Workers, prefixKeyRange('CalendarEventSync-'))
        .flatMap(
          (workers: any[]) => deleteBatch(Stores.Workers, workers.map(worker => worker.worker),
        )),
    );
  }

  if (ops.length === 0) {
    return Observable.of(null);
  }

  return Observable.zip(...ops).map(() => null);
}

export function prefixKeyRange(prefix: string): IDBKeyRange {
  return IDBKeyRange.bound(
    prefix,
    prefix + '\uffff',
    false,
    false,
  );
}

// initDb must be called by app and worker init once
export function initDb(force: boolean = false): Observable<IDBDatabase> {
  const usualCheck = !force && dbOpen && _db;
  if (usualCheck && _db) {
    return Observable.of(_db);
  }

  if (!force && !allowReconnect) {
    return Observable.throw(new Error('programmer: clear cache in progress'));
  }

  // create new instance and set it
  return createDbInstance()
    .flatMap((instance) => {
      _db = instance;
      dbOpen = true;
      _db.addEventListener('close', closeEventListener);
      return initSearchDb();
    })
    .flatMap(() => getCreateClearCacheStatus())
    .flatMap((status) => {
      // Handle DB Migrations if needed
      if (!status.version || status.version !== DB_MIGRATION_VERSION) {
        // Run DB Migration
        return migrateDb(status.version ? status.version : 0)
          .flatMap(() => {
            status.version = DB_MIGRATION_VERSION;
            return saveClearCacheStatus(status);
          });
      }

      return Observable.of(null);
    })
    .flatMap(() => {
      // These commit restores only apply to electron builds
      if (clearCacheMechanism === ClearCacheMechanism.Default) {
        return Observable.of(null);
      }

      // Handle commits
      const commitsRestore = unserializeFile<Commit[]>(COMMITS_RESTORE_FILENAME)
        .flatMap((commits) => {
          const cleanedCommits = commits.map((c) => {
            delete c.id;

            if (c.resource === HistoryObject.DraftAttachment) {
              const e = new Error('Attachment upload failed. Please try again');
              c.status = CommitStatus.Error;
              c.rehydrateMeta = {
                ...c.rehydrateMeta,
                error: e,
              };
            }

            return c;
          });
          return load(Stores.Commits, cleanedCommits);
        })
        .flatMap(() => deleteFile(COMMITS_RESTORE_FILENAME))
        .catch((e: DOMException) => {
          if (e.code && e.code === e.NOT_FOUND_ERR) {
            return Observable.of(null);
          }
          throw e;
        });

      // Handle Drafts
      const draftsRestore = unserializeFile<Draft[]>(DRAFTS_RESTORE_FILENAME)
        .flatMap((drafts) => {
          return load(Stores.Drafts, drafts);
        })
        .flatMap(() => deleteFile(DRAFTS_RESTORE_FILENAME))
        .catch((e: DOMException) => {
          if (e.code && e.code === e.NOT_FOUND_ERR) {
            return Observable.of(null);
          }
          throw e;
        });

      return Observable.zip(commitsRestore, draftsRestore).map(() => null);
    })
    .map(() => _db!);
}

export function setAllowReconnect(allow: boolean) {
  allowReconnect = allow;
}

export function getAllowReconnect(): boolean {
  return allowReconnect;
}

// createDbInstance creates a db instance
function createDbInstance(): Observable<IDBDatabase> {
  return Observable.create((observer: Observer<any>) => {
    const timer = performance.now();
    debugLog('all' as any, timer, 'createdbstart');

    const open = indexedDB.open(DB_NAME, DB_VERSION);
    open.onsuccess = (event) => {
      debugLog('all' as any, timer, 'createdbsuccess');

      open.result.onversionchange = () => {
        open.result.close();
      };

      observer.next(open.result);
      observer.complete();
    };

    open.onerror = (e: ErrorEvent) => {
      debugLog('all' as any, timer, 'createdberror');

      observer.error(open.error);
    };

    // Handles DB upgrades and index creations
    open.onupgradeneeded = (e) => {
      debugLog('all' as any, timer, 'createdbupgradeneededstart');

      // Clear Stores if really old version
      if (e.oldVersion < DB_MIN_VERSION_NO_CLEAR_CACHE) {
        Object.keys(Stores).forEach((store) => {
          const name = Stores[store];
          if (open.result.objectStoreNames.contains(name)) {
            open.result.deleteObjectStore(name);
          }
        });
      }

      // Create Stores
      const basicStores: Stores[] = [
        Stores.ActivityFeed,
        Stores.Calendars,
        Stores.CalendarEvents,
        Stores.Contacts,
        Stores.ContactGroups,
        Stores.ContactNotes,
        Stores.Drafts,
        Stores.MessageBodies,
        Stores.SharedThreads,
        Stores.TeamMembers,
        Stores.Templates,
      ];
      basicStores.forEach((store) => {
        // create store if it doesn't exist
        if (!open.result.objectStoreNames.contains(store)) {
          open.result.createObjectStore(store, { keyPath: 'id' });
        }
      });

      if (!open.result.objectStoreNames.contains(Stores.KeyValue)) {
        open.result.createObjectStore(Stores.KeyValue);
      }

      if (!open.result.objectStoreNames.contains(Stores.Commits)) {
        open.result.createObjectStore(Stores.Commits, { keyPath: 'id', autoIncrement: true });
      }
      if (!open.result.objectStoreNames.contains(Stores.FileUploads)) {
        open.result.createObjectStore(Stores.FileUploads, { keyPath: 'id' });
      }

      if (!open.result.objectStoreNames.contains(Stores.Workers)) {
        open.result.createObjectStore(Stores.Workers, { keyPath: 'worker' });
      }

      if (!open.result.objectStoreNames.contains(Stores.Threads)) {
        open.result.createObjectStore(Stores.Threads, { keyPath: 'id' });
      }

      // Thread Indexes
      interface IndexArgs {
        name: string;
        key: string|string[];
        options: IDBIndexParameters;
        deleted?: boolean;
      }
      const threadIndexes: IndexArgs[] = [
        // Index for box queries
        {
          name: 'box_sort',
          key: ['box', 'sort'],
          options: { unique: false },
        },
        {
          name: 'account_internal',
          key: ['account', 'internal'],
          options: { unique: false },
        },
        {
          name: 'account_box_sort',
          key: ['account', 'box', 'sort'],
          options: { unique: false },
        },

        // Index for Sent filter queries
        {
          name: 'lastsent',
          key: ['lastSent'],
          options: { unique: false },
        },
        {
          name: 'account_lastsent',
          key: ['account', 'lastSent'],
          options: { unique: false },
        },

        // Index for List box queries
        {
          name: 'list_internal',
          key: ['list', 'internal'],
          options: { unique: false },
        },
        {
          name: 'account_list_internal',
          key: ['account', 'list', 'internal'],
          options: { unique: false },
        },

        // Index for Email Lookup
        {
          name: 'participantemails',
          key: 'participantEmails',
          options: { unique: false, multiEntry: true },
        },

        // Index for starred threads
        {
          name: 'starred',
          key: 'starredIndex',
          options: { unique: false },
          deleted: true,
        },

        {
          name: 'starred_internal',
          key: ['starredIndex', 'internal'],
          options: { unique: false },
        },

        {
          name: 'starred_account_internal',
          key: ['starredIndex', 'account', 'internal'],
          options: { unique: false },
        },

        // Index for threads with follow up reminders
        {
          name: 'waiting',
          key: 'waitingIndex',
          options: { unique: false },
        },

        // Index for unread threads in a list
        {
          name: 'account_list_unread',
          key: ['account', 'listUnreadIndex'],
          options: { unique: false },
        },

        // Index for read/unread threads in inbox
        {
          name: 'account_inbox_unread',
          key: ['account', 'inboxUnreadIndex'],
          options: { unique: false },
        },
      ];
      const threads = open.transaction!.objectStore(Stores.Threads);
      threadIndexes.forEach((arg) => {
        const contains = threads.indexNames.contains(arg.name);
        if (arg.deleted && contains) {
          threads.deleteIndex(arg.name);
        } else if (!contains && !arg.deleted) {
          threads.createIndex(arg.name, arg.key, arg.options);
        }
      });

      // Contact Indexes
      const contactIndexes: IndexArgs[] = [
        // Index for Email Lookup
        {
          name: 'email',
          key: 'email',
          options: { unique: false },
        },

        // Index for Autocomplete
        {
          name: 'search',
          key: 'searchIndex',
          options: { unique: false, multiEntry: true },
        },
      ];
      const contacts = open.transaction!.objectStore(Stores.Contacts);
      contactIndexes.forEach((arg) => {
        if (!contacts.indexNames.contains(arg.name)) {
          contacts.createIndex(arg.name, arg.key, arg.options);
        }
      });

      // Activity Feed Indexes
      const activityFeedIndexes: IndexArgs[] = [
        // Sorted by date
        {
          name: 'date',
          key: ['date'],
          options: { unique: false },
        },
        {
          name: 'type_date',
          key: ['type', 'date'],
          options: { unique: false },
        },
      ];
      const activityFeed = open.transaction!.objectStore(Stores.ActivityFeed);
      activityFeedIndexes.forEach((arg) => {
        if (!activityFeed.indexNames.contains(arg.name)) {
          activityFeed.createIndex(arg.name, arg.key, arg.options);
        }
      });

      // Calendar Event Indexes
      const calendarEventIndexes: IndexArgs[] = [
        {
          name: 'starttime',
          key: 'startTime',
          options: { unique: false },
        },
      ];
      const calendarEvent = open.transaction!.objectStore(Stores.CalendarEvents);
      calendarEventIndexes.forEach((arg) => {
        if (!calendarEvent.indexNames.contains(arg.name)) {
          calendarEvent.createIndex(arg.name, arg.key, arg.options);
        }
      });

      // Shared Thread Indexes
      const sharedThreadIndexes: IndexArgs[] = [
        {
          name: 'thread',
          key: 'thread',
          options: { unique: false },
        },
      ];
      const sharedThread = open.transaction!.objectStore(Stores.SharedThreads);
      sharedThreadIndexes.forEach((arg) => {
        if (!sharedThread.indexNames.contains(arg.name)) {
          sharedThread.createIndex(arg.name, arg.key, arg.options);
        }
      });

      // Message Bodies Index
      const messageBodiesIndexes: IndexArgs[] = [
        {
          name: 'thread',
          key: 'thread',
          options: { unique: false },
        },
        {
          name: 'updated',
          key: 'updated',
          options: { unique: false },
        },
      ];
      const messageBody = open.transaction!.objectStore(Stores.MessageBodies);
      messageBodiesIndexes.forEach((arg) => {
        if (!messageBody.indexNames.contains(arg.name)) {
          messageBody.createIndex(arg.name, arg.key, arg.options);
        }
      });
    };
  });
}

// helper to create a readwrite transaction with custom durability
//   guarantees for browsers that support it
function getReadWriteTransaction(store: Stores): Observable<IDBTransaction> {
  switch (store) {
    // Want to keep safety for Drafts and Commits
    case Stores.Drafts:
    case Stores.Commits:
      return initDb().map(db => db.transaction(store, 'readwrite'));
    default:
      return initDb().map(
        db => (db.transaction as any)(store, 'readwrite', { durability: 'relaxed' }),
      );
  }
}

function get(store: Stores, key: any): Observable<any> {
  return initDb().flatMap((db) => {
    return Observable.create((observer: Observer<any>) => {
      const timer = performance.now();
      const request = db.transaction(store, 'readonly').objectStore(store).get(key);
      request.onsuccess = () => {
        debugLog(store, timer, 'get', key);
        observer.next(request.result);
        observer.complete();
      };
      request.onerror = () => {
        observer.error(request.error);
      };
    });
  });
}

function bulkGet<T>(store: Stores, keys: any[]): Observable<T[]> {
  return initDb().flatMap((db) => {
    return Observable.create((observer: Observer<any>) => {
      const timer = performance.now();
      let completed = false;
      const results: T[] = new Array(keys.length);

      // Handle 0 items
      if (results.length === 0) {
        debugLog(store, timer, 'bulkget', keys);
        observer.next(results);
        observer.complete();
        return;
      }

      // Fetch all keys
      const transaction = db.transaction(store, 'readonly');
      const objectStore = transaction.objectStore(store);

      transaction.oncomplete = () => {
        if (!completed) {
          debugLog(store, timer, 'bulkget', keys);
          observer.next(results);
          observer.complete();
          completed = true;
        }
      };

      transaction.onerror = (e) => {
        if ((e.target as any) && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(transaction.error);
        }
      };

      transaction.onabort = (e) => {
        if ((e.target as any) && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(transaction.error);
        }
      };

      keys.forEach((key, i) => {
        const request = objectStore.get(key);
        request.onsuccess = () => {
          results[i] = request.result;
        };
        request.onerror = () => {
          // Send error and mark as completed
          if (!completed) {
            observer.error(request.error);
          }

          completed = true;

          // Cancel remaining requests
          try {
            transaction.abort();
            // tslint:disable-next-line: no-empty
          } catch (e) {
          }
        };
      });

      return () => {
        if (!completed) {
          try {
            transaction.abort();
            // tslint:disable-next-line: no-empty
          } catch (e) {
          }
        }
      };
    });
  });
}

function save(store: Stores, object: any, key?: any): Observable<null> {
  return getReadWriteTransaction(store).flatMap(txn =>
    Observable.create((observer: Observer<null>) => {
      const timer = performance.now();
      const request = txn.objectStore(store).put(object, key);
      request.onsuccess = () => {
        debugLog(store, timer, 'save', object);
        observer.next(null);
        observer.complete();
        return;
      };
      request.onerror = () => {
        observer.error(request.error);
        return;
      };
    }),
  );
}

function deleteSingle(store: Stores, key: any): Observable<null> {
  return getReadWriteTransaction(store).flatMap(txn =>
    Observable.create((observer: Observer<null>) => {
      const timer = performance.now();
      const request = txn.objectStore(store).delete(key);
      request.onsuccess = () => {
        debugLog(store, timer, 'del', key);
        observer.next(null);
        observer.complete();
        return;
      };
      request.onerror = () => {
        observer.error(request.error);
        return;
      };
    }),
  );
}

function load(store: Stores, objects: any[]): Observable<any> {
  if (objects.length === 0) {
    debugLog(store, 0, 'load', objects);
    return Observable.of(null);
  }

  return getReadWriteTransaction(store).flatMap(txn =>
    Observable.create((observer: Observer<any>) => {
      const timer = performance.now();

      const s = txn.objectStore(store);
      let completed = false;

      txn.oncomplete = () => {
        completed = true;
        debugLog(store, timer, 'load', objects);
        observer.next(null);
        observer.complete();
        return;
      };
      txn.onerror = (e) => {
        if (txn.error == null) {
          // Have to get error off of event since this is a bulk load
          if (e.target && (e.target as any).error) {
            observer.error((e.target as any).error);
            return;
          }

          observer.error(new Error('programmer: null error in db load'));
          return;
        }

        observer.error(txn.error);
        return;
      };

      txn.onabort = (e) => {
        if (e.target && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(txn.error);
        }
      };

      objects.forEach(obj => s.put(obj));

      return () => {
        if (!completed) {
          try {
            txn.abort();
            // tslint:disable-next-line: no-empty
          } catch (e) {
          }
        }
      };
    }),
  );
}

function deleteBatch(store: Stores, keys: any[]): Observable<any> {
  if (keys.length === 0) {
    debugLog(store, 0, 'delbatch', keys);
    return Observable.of(null);
  }

  return getReadWriteTransaction(store).flatMap(txn =>
    Observable.create((observer: Observer<any>) => {
      const timer = performance.now();
      const s = txn.objectStore(store);
      let completed = false;

      txn.oncomplete = () => {
        completed = true;
        debugLog(store, timer, 'delbatch', keys);
        observer.next(null);
        observer.complete();
        return;
      };
      txn.onerror = () => {
        observer.error(txn.error);
        return;
      };

      txn.onabort = (e) => {
        if (e.target && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(txn.error);
        }
      };

      keys.forEach(key => s.delete(key));

      return () => {
        if (!completed) {
          try {
            txn.abort();
            // tslint:disable-next-line: no-empty
          } catch (e) {
          }
        }
      };
    }),
  );
}

function deleteIndexRange(
    store: Stores, index: string, range?: IDBKeyRange, limit?: number,
): Observable<any> {
  return listIndexKeys(store, index, range, limit).flatMap(keys => deleteBatch(store, keys));
}

function clear(store: Stores, ignoreCommits: boolean = false): Observable<any> {

  // Add specialized handling to not delete commits or fileuploads that are in progress
  if (ignoreCommits) {
    switch (store) {
      case Stores.FileUploads:
      case Stores.Commits:
        return list(
          store,
          (model: FileUploadMeta | Commit) =>
            [CommitStatus.Pending, CommitStatus.Ready, CommitStatus.Processing]
              .indexOf(model.status) === -1,
        )
        .flatMap(models => deleteBatch(store, models.map(model => model.id)));
      default:
        break;
    }
  }

  return getReadWriteTransaction(store).flatMap(transaction =>
    Observable.create((observer: Observer<null>) => {
      const timer = performance.now();

      const request = transaction.objectStore(store).clear();
      request.onsuccess = () => {
        debugLog(store, timer, 'clear');
        observer.next(null);
        observer.complete();
        return;
      };
      request.onerror = () => {
        observer.error(request.error);
        return;
      };
    }),
  );
}

// Loop through all models in a given store, optionally with a filter function
function list<Model>(
  store: Stores, filter?: (model: Model) => boolean,
): Observable<Model[]> {
  return initDb().flatMap((db) => {
    return Observable.create((observer: Observer<Model[]>) => {
      const timer = performance.now();
      const txn = db.transaction(store, 'readonly');
      const items: Model[] = [];
      let completed = false;

      txn.oncomplete = (event) => {
        debugLog(store, timer, 'list', filter);
        completed = true;
        observer.next(items);
        observer.complete();
      };

      txn.onerror = (e) => {
        if (e.target && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(txn.error);
        }
      };

      txn.onabort = (e) => {
        if (e.target && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(txn.error);
        }
      };

      const s = txn.objectStore(store).openCursor();

      s.onsuccess = (event) => {
