function listIndex<Model>(
  store: Stores,
  index: string,
  range: IDBKeyRange|undefined,
  desc: boolean,
  limit?: number,
  filter?: (model: Model) => boolean,
): Observable<Model[]> {
  return initDb().flatMap((db) => {
    return Observable.create((observer: Observer<Model[]>) => {
      const timer = performance.now();
      const txn = db.transaction(store, 'readonly');
      const items: Model[] = [];
      let completed = false;
      txn.oncomplete = (event) => {
        debugLog(store, timer, 'listindex', { index, range, desc, limit });
        completed = true;
        observer.next(items);
        observer.complete();
      };

      txn.onerror = (e: ErrorEvent) => {
        if ((e.target as any) && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(txn.error);
        }
      };

      // NOTE(SHIN): Adding abort handler to pass errors to mailcountbot
      txn.onabort = (e) => {
        if ((e.target as any) && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(txn.error);
        }
      };

      const s = desc ?
        txn.objectStore(store).index(index).openCursor(range, 'prev') :
        txn.objectStore(store).index(index).openCursor(range);

      let numItems = 0;
      s.onsuccess = (event) => {
        const cursor = event.target as any;
        if (cursor && cursor.result) {
          if (!filter || (filter && filter(cursor.result.value))) {
            items.push(cursor.result.value);

            // Limit the number of items fetched
            numItems += 1;
            if (limit && numItems >= limit) {
              return;
            }
          }

          // Otherwise keep fetching items
          cursor.result.continue();
        }
      };

      s.onerror = (e: ErrorEvent) => {
        if ((e.target as any) && (e.target as any).error) {
          observer.error((e.target as any).error);
        } else {
          observer.error(s.error);
        }
      };

      return () => {
        if (!completed) {
          try {
            txn.abort();
          // tslint:disable-next-line: no-empty
          } catch (e) {
          }
        }
      };
    });
  });
}

