export function resetDb(ignoreCommits: boolean = false): Observable<any> {
  switch (clearCacheMechanism) {
    case ClearCacheMechanism.ElectronBridge:
      if (!ignoreCommits) {
        return Observable.of(window.PMElectronBridge!.clearCacheV2!());
      }

      return fetchStagedDrafts()
        .flatMap(drafts => serializeFile(DRAFTS_RESTORE_FILENAME, drafts))
        .flatMap(() => fetchRefreshCommits())
        .flatMap(commits => serializeFile(COMMITS_RESTORE_FILENAME, commits))
        .flatMap(() => Observable.of(window.PMElectronBridge!.clearCacheV2!()));

    case ClearCacheMechanism.WorkerElectronBridge:
      if (!ignoreCommits) {
        return Observable.of(sendTriggerElectronClearCache());
      }

      return fetchStagedDrafts()
        .flatMap(drafts => serializeFile(DRAFTS_RESTORE_FILENAME, drafts))
        .flatMap(() => fetchRefreshCommits())
        .flatMap(commits => serializeFile(COMMITS_RESTORE_FILENAME, commits))
        .flatMap(() => Observable.of(sendTriggerElectronClearCache()));

    default:
      const clearAll = Object.keys(Stores).map(name => clear(Stores[name], ignoreCommits));
      let searchDbClears: Array<Observable<any>> = [];
      if (getSearchDbConfig().enabled) {
        searchDbClears = searchDbClears.concat([
          Observable.from(
            getSearchDb().threads.clear(),
          ),
          Observable.from(
            getSearchDb().bodies.clear(),
          ),
        ]);
      }

      const deleteAll = getSearchDbConfig().enabled &&
        [...clearAll, ...searchDbClears] || [...clearAll];

      return Observable.create((observer: Observer<any>) => {
        const timer = performance.now();

        Observable.from(deleteAll).concatAll().subscribe({
          complete: () => {
            debugLog('allstores' as any, timer, 'resetdb');
            observer.next(null);
            observer.complete();
          },
          error: e => observer.error(e),
        });
      });
  }
}

// resetDbIfNeeded resets the db if needed based on the clearCacheStatus
export function resetDbIfNeeded(): Observable<null> {
  return getCreateClearCacheStatus()
    .flatMap((status) => {

      // Clear cache already complete, can skip
      if (status.hash === status.completedHash && status.counter === status.completedCounter) {
        return Observable.of(null);
      }

      // Handle scenario when going from logged out to logged in case
      if (status.completedHash === AUTH_TOKEN_NONE) {
        status.completedHash = status.hash;
        status.completedCounter = status.counter;
        return saveClearCacheStatus(status);
      }

      return resetDb(status.ignoreCommits)
        .flatMap(() => {
          status.completedHash = status.hash;
          status.completedCounter = status.counter;
          return saveClearCacheStatus(status);
        });
    });
}

