import { Resource } from '@src/types';
import { SalesforceSummaryCreateMeta } from '@src/ducks/salesforce';

export interface SalesforceState {
  listViews: {
    [t: string]: Resource<SFDCListView[]>;
  };

  pendingSummaries: {
    [email: string]: SalesforceSummaryCreateMeta;
  };

  summaries: {
    [email: string]: Resource<SalesforceSummary>;
  };

  schemas: {
    [string: string]: SObjectSchema;
  };
}

export interface SalesforceSummary {
  lead?: SObject;
  contact?: SObject;
  account?: SObject;
  tasks: SObject[];
  activityHistory: SObject[];
  openTasksCount: number;
  activityHistoryCount: number;
  opportunities: SObject[];
}

export interface SObjectSchema {
  fields: SObjectField[];
}

export const enum SalesforceTaskSubtype {
  Task = 'Task',
  Phone = 'Phone',
  Email = 'Email',
}

export interface SObject {
  url: string;
  href: string;
  resource: string;
  updatable: boolean;
  deletable: boolean;
  custom: boolean;
  fields: SObjectField[];
}

export interface SObjectField {
  type: SObjectFieldType;
  updatable: boolean;
  creatable: boolean;
  required: boolean;

  custom: boolean;
  name: string;

  value: any;
  displayValue?: any;
  displayName: string;
  // type Reference
  referencedResource?: string;

  // type picklist
  picklistValues?: SObjectPicklistValue[];
}

export const enum SObjectFieldType {
  Id = 'id',
  Url = 'url',
  Textarea = 'textarea',
  Phone = 'phone',
  String = 'string',
  Boolean = 'boolean',
  Date = 'date',
  Datetime = 'datetime',
  Picklist = 'picklist',
  Reference = 'reference',
  Int = 'int',
  Currency = 'currency',
  Email = 'email',
}

export interface SObjectPicklistValue {
  default: boolean;
  value: string;
  displayValue: string;
}

export const enum SalesforceTaskStatus {
  Completed = 'Completed',
  NotStarted = 'Not Started',
}

export enum SObjectType {
  Contact = 'Contact',
  Lead = 'Lead',
}

export interface SFDCListView {
  id: string;
  name: string;
  sobjectType: SObjectType;
}
