import React from 'react';
import classnames from 'classnames';
import {
  Contact, ContactGroup, ContactType, HotkeyActions,
} from '../../types';
import {
  RightSidebarTab, RightSidebarSubTab, Page,
} from '../../types/rightSidebar';

import ContactInfo from './components/ContactInfo/ContactInfo';
import ActivityFeed from './components/ActivityFeed';
import Salesforce from './components/Salesforce';
import Hubspot from './components/Hubspot';
import Calendar from './components/Calendar';

import './RightSidebar.scss';

import Activity from '@src/components/Icons/Activity.svg';
import SilverHelp from '@src/components/Icons/SilverHelp.svg';
import Bio from '@src/components/Icons/Bio.svg';
import RightArrow from '@src/components/Icons/RightArrow.svg';
import SalesforceLogo from '@src/components/Icons/SalesforceLogo.svg';
import HubspotLogo from '@src/components/Icons/Hubspot.svg';
import CalendarIcon from '@src/components/Icons/Calendar.svg';
import Hyperlink from '@src/components/Icons/Hyperlink.svg';
import ThreeDots from '@src/components/Icons/ThreeDots.svg';

import { Configuration } from '@src/lib/draft';
import { HotkeyAction, checkNextHotkeyChange } from '@src/lib/hotkeys';
import KeyboardShortcuts from './components/KeyboardShortcuts';
import HoverTip from '@src/components/Base/HoverTip';
import { ContactUpdateMeta } from '@src/ducks/contacts';
import Onboarding from './components/Onboarding';
import LinkExtractor from './components/LinkExtractor';
import Popover from '../Popover/Popover';
import TabsPopover from './components/TabsPopover/TabsPopover';

export interface StateProps {
  contact?: Contact;
  contactGroups: { [id: string]: ContactGroup; };
  collapsed: boolean;
  selectedTab: RightSidebarTab;
  hotkeyActions: HotkeyActions;
  email?: string;

  // Integrations
  hideSalesforce?: boolean;
  hideHubspot?: boolean;
}

export interface Props {
  hideTopBar?: boolean;
  page: Page;
}

export interface DispatchProps {
  selectRightSidebarTab: (tab: RightSidebarTab, subTab?: RightSidebarSubTab) => void;
  createDraft: (configuration: Configuration, recipients: string[]) => void;
  getContact: (email: string) => void;
  collapseRightSidebar: (collapse: boolean) => void;
  updateContact: (contact: ContactUpdateMeta) => void;
}

export interface RightSidebarTabOrder {
  tab: RightSidebarTab;
  shown: boolean;
  order?: number;
}

interface State {
  showTabsPopover?: boolean;
}

// Max number of tabs to show on top of RSB
const MAX_TABS = 6;
const SHOW_MORE_ELEMENT_ID = 'rsb-show-more';

class RightSidebar extends React.PureComponent<StateProps & DispatchProps & Props, State> {
  tabsPopover: Popover;

  constructor() {
    super();
    this.state = {};
  }

  componentDidMount() {
    const { email, contact, getContact } = this.props;

    if (contact && email && contact.type === ContactType.Default) {
      getContact(email);
    }
  }

  UNSAFE_componentWillReceiveProps(nextProps: StateProps & DispatchProps & Props) {
    const {
      email,
      contact,
      getContact,
      collapsed,
      selectRightSidebarTab,
    } = nextProps;

    if (contact && email && email !== this.props.email
      && contact.type === ContactType.Default) {
      getContact(email);
    }

    if (nextProps.hotkeyActions !== this.props.hotkeyActions) {
      const newActions = nextProps.hotkeyActions;
      const oldActions = this.props.hotkeyActions;

      // RSB toggle
      if (checkNextHotkeyChange(HotkeyAction.ToggleRSB, newActions, oldActions)) {
        this.toggleCollapse(collapsed);
      }

      // Activity Feed
      if (checkNextHotkeyChange(HotkeyAction.ShowActivityFeed, newActions, oldActions)) {
        if (collapsed) {
          this.toggleCollapse(collapsed);
        }
        selectRightSidebarTab(RightSidebarTab.Activity);
      }

      // Contact Profile
      if (checkNextHotkeyChange(HotkeyAction.ShowContactProfile, newActions, oldActions)) {
        if (collapsed) {
          this.toggleCollapse(collapsed);
        }
        selectRightSidebarTab(RightSidebarTab.Contact);
      }

      // Salesforce
      if (checkNextHotkeyChange(HotkeyAction.ShowSalesforce, newActions, oldActions)) {
        if (collapsed) {
          this.toggleCollapse(collapsed);
        }
        selectRightSidebarTab(RightSidebarTab.Salesforce);
      }
    }
  }

  handleTabsPopoverRef = (ref: Popover) => {
    this.tabsPopover = ref;
  }

  onSelectTab = (e: React.MouseEvent<HTMLElement>) => {
    const {
      selectRightSidebarTab,
      collapsed,
    } = this.props;
    const selectedTab = e.currentTarget.id;

    // When show more tab is activated, open popover
    if (selectedTab === SHOW_MORE_ELEMENT_ID) {
      this.setState({ showTabsPopover: true });
      return;
    }

    if (collapsed) {
      this.toggleCollapse(collapsed);
    }

    selectRightSidebarTab(selectedTab as RightSidebarTab);
  }

  handlePopoverTabSelect = (e: RightSidebarTab) => {
    const {
      selectRightSidebarTab,
      collapsed,
    } = this.props;
    const selectedTab = e;

    if (collapsed) {
      this.toggleCollapse(collapsed);
    }

    selectRightSidebarTab(selectedTab);
  }

  toggleCollapse = (currentCollapse?: boolean) => {
    const { collapseRightSidebar } = this.props;
    collapseRightSidebar(!currentCollapse);
  }

  handleCollapse = () => {
    this.toggleCollapse(false);
  }

  handleContactUpdate = (contact: Contact) => {
    this.props.updateContact(contact);
  }

  generateShownTabs = (): { [key: string]: RightSidebarTabOrder } => {
    const {
      hideSalesforce,
      hideHubspot,
      collapsed,
    } = this.props;

    // Grab array of all tabs to show
    let allTabs = [
      RightSidebarTab.Activity,
      RightSidebarTab.Contact,
      RightSidebarTab.Onboarding,
      RightSidebarTab.LinkExtractor,
      RightSidebarTab.Calendar,
    ];

    if (!hideSalesforce) {
      allTabs = [
        ...allTabs,
        RightSidebarTab.Salesforce,
      ];
    }

    if (!hideHubspot) {
      allTabs = [
        ...allTabs,
        RightSidebarTab.Hubspot,
      ];
    }

    let overflowed = false;

    const shownTabs: { [key: string]: RightSidebarTabOrder } = allTabs.reduce(
      (dict, next) => {
        const len = Object.keys(dict).length;
        const nextLen = len + 1;
        if (nextLen >= MAX_TABS && !collapsed) {
          // If the tab is the last tab and equal to max tabs, don't overflow.
          const lastTabIsMax = nextLen === MAX_TABS && allTabs.length === MAX_TABS;

          // Otherwise, set overflow so we contain within dropdown
          if (!lastTabIsMax) {
            overflowed = true;
          }
        }
        dict[next] = {
          tab: next,
          shown: !overflowed ? true : false,
        };
        return dict;
      },
      {},
    );

    return shownTabs;
  }

  handleTabsPopoverHide = () => {
    this.setState({ showTabsPopover: false });
  }

  render() {
    const {
      contact,
      contactGroups,
      collapsed,
      selectedTab,
      createDraft,
      hideTopBar,
      hideSalesforce,
      hideHubspot,
    } = this.props;
    const { showTabsPopover } = this.state;

    const shownTabs = this.generateShownTabs();
    const overflowed = !!Object.values(shownTabs).filter(t => !t.shown).length;

    return (
      <div className={classnames('right-sidebar', { collapsed })}>
        {!hideTopBar && <div className="top-bar">
          <div className="left">
            <HoverTip
              top="35px"
              copy="Activity Feed"
            >
              <button
                id={RightSidebarTab.Activity}
                className={classnames({ selected: selectedTab === RightSidebarTab.Activity })}
                onClick={this.onSelectTab}
              >
                <Activity className="icon" width="16px" height="15px" />
              </button>
            </HoverTip>
            <HoverTip
              top="35px"
              copy="Contact Profile"
            >
              <button
                id={RightSidebarTab.Contact}
                className={classnames({ selected: selectedTab === RightSidebarTab.Contact })}
                onClick={this.onSelectTab}
              >
                <Bio className="icon" width="17px" height="17px" />
              </button>
            </HoverTip>
            <HoverTip
              top="35px"
              copy="Onboarding"
            >
              <button
                id={RightSidebarTab.Onboarding}
                className={classnames({ selected: selectedTab === RightSidebarTab.Onboarding })}
                onClick={this.onSelectTab}
              >
                <SilverHelp className="icon" width="17px" height="17px" />
              </button>
            </HoverTip>
            {!hideSalesforce && shownTabs[RightSidebarTab.Salesforce]?.shown &&
              <HoverTip
                top="35px"
                copy="Salesforce"
              >
                <button
                  id={RightSidebarTab.Salesforce}
                  className={classnames({ selected: selectedTab === RightSidebarTab.Salesforce })}
                  onClick={this.onSelectTab}
                >
                  <SalesforceLogo className="icon" width="23px" height="16px" />
                </button>
              </HoverTip>
            }
            {!hideHubspot && shownTabs[RightSidebarTab.Hubspot]?.shown &&
              <HoverTip
                top="35px"
                copy="Hubspot"
              >
                <button
                  id={RightSidebarTab.Hubspot}
                  className={classnames({ selected: selectedTab === RightSidebarTab.Hubspot })}
                  onClick={this.onSelectTab}
                >
                  <HubspotLogo className="icon" width="23px" height="16px" />
                </button>
              </HoverTip>
            }
            {shownTabs[RightSidebarTab.LinkExtractor]?.shown &&
              <HoverTip
                top="35px"
                copy="Link Extractor"
                hotkeyAction={HotkeyAction.ShowLinks}
                multiline
              >
                <button
                  id={RightSidebarTab.LinkExtractor}
                  className={classnames({
                    selected: selectedTab === RightSidebarTab.LinkExtractor,
                  })}
                  onClick={this.onSelectTab}
                >
                  <Hyperlink className="icon" width="23px" height="16px" />
                </button>
              </HoverTip>
            }
            {shownTabs[RightSidebarTab.Calendar]?.shown &&
              <HoverTip
                top="35px"
                copy="Calendar"
              >
                <button
                  id={RightSidebarTab.Calendar}
                  className={classnames({
                    selected: selectedTab === RightSidebarTab.Calendar,
                  })}
                  onClick={this.onSelectTab}
                >
                  <CalendarIcon className="icon" width="15px" height="15px" />
                </button>
              </HoverTip>
            }
            {overflowed && (
              <HoverTip
                top="35px"
                copy="Show more"
              >
                <button
                  id={SHOW_MORE_ELEMENT_ID}
                  onClick={this.onSelectTab}
                >
                  <ThreeDots className="icon" width="15px" height="15px" />
                </button>
              </HoverTip>
            )}
          </div>
          {!collapsed &&
            <button id="collapse" onClick={this.handleCollapse}>
              <RightArrow className="icon" />
            </button>}
        </div>}
        {!collapsed && showTabsPopover && (
          <TabsPopover
            shownTabs={shownTabs}
            onTabSelect={this.handlePopoverTabSelect}
            onHide={this.handleTabsPopoverHide}
            selectedTab={selectedTab}
          />
        )}
        {!collapsed && selectedTab === RightSidebarTab.LinkExtractor && (
          <LinkExtractor />
        )}
        {!collapsed && selectedTab === RightSidebarTab.Activity && <ActivityFeed />}
        {!collapsed && selectedTab === RightSidebarTab.Contact &&
          <ContactInfo
            contact={contact}
            contactGroups={contactGroups}
            createDraft={createDraft}
            updateContact={this.handleContactUpdate}
          />
        }
        {!collapsed && !hideSalesforce &&
          selectedTab === RightSidebarTab.Salesforce &&
          <Salesforce />}
        {!collapsed && !hideHubspot && selectedTab === RightSidebarTab.Hubspot &&
          <Hubspot />}
        {!collapsed && selectedTab === RightSidebarTab.KeyboardShortcut && <KeyboardShortcuts />}
        {!collapsed &&
          selectedTab === RightSidebarTab.Onboarding && <Onboarding />}
        {!collapsed &&
          selectedTab === RightSidebarTab.Calendar && <Calendar />}
      </div>
    );
  }
}

export default RightSidebar;
