import {
  ActionStatus, Action, StoreState,
} from '@src/types';
import FileSaver from 'file-saver';
import {
  Sequence, SequenceTab, SequenceRecipient, SequenceRecipientFilter,
} from '@src/types/sequences';
import { Epic } from 'redux-observable';
import { Deps } from '@src/lib/epic';
import {
  SEQUENCE_LIST, sequenceList,
  SEQUENCE_CREATE, sequenceCreate,
  SEQUENCE_UPDATE, sequenceUpdate,
  SEQUENCE_DELETE, sequenceDelete,
  sequenceSelect,
  sequenceTabSelect,
  SEQUENCE_SELECT,
  SEQUENCE_CSV_UPLOAD,
  SequenceCsvUploadPayload,
  sequenceCsvUpload,
  SEQUENCE_DUPLICATE,
  sequenceDuplicate,
  SEQUENCE_EXPORT,
  sequenceExport,
  SEQUENCE_COUNT,
  sequenceCount,
  SEQUENCE_SHARE,
  sequenceShare,
} from '@src/ducks/sequences';
import { Observable } from 'rxjs';
import { LocationChangeAction, LOCATION_CHANGE, push, RouterAction } from 'react-router-redux';
import { toastSet } from '@src/ducks/toasts';
import { ApiError } from '@src/api/base';
import { sequenceStageList, sequenceStageCreate } from '@src/ducks/sequenceStages';
import {
  sequenceRecipientList, SequenceRecipientListMeta,
} from '@src/ducks/sequenceRecipients';
import { getQueryVariable } from '@src/lib/query';

const locationChangeUpdateSequences: Epic<Action<any, any>, StoreState, Deps> =
  (action$, store, deps) =>
    action$.ofType(LOCATION_CHANGE)
      .filter((action) => {
        const path = ((action as any) as LocationChangeAction).payload.pathname.toLowerCase();

        return path === '/sequences' || path === '/sequences/archive';
      })
      .debounceTime(200, deps.scheduler)
      .flatMap((action) => {
        const path = ((action as any) as LocationChangeAction).payload.pathname.toLowerCase();

        if (path.startsWith('/sequences/archive')) {
          return Observable.merge(
            Observable.of(sequenceList(true)),
            Observable.of(sequenceCount(null)),
          );
        }

        return Observable.of(sequenceList(false));
      });

const locationChangeSelectSequence: Epic<Action<any, any>, StoreState, Deps> =
  (action$, store, deps) =>
    action$.ofType(LOCATION_CHANGE)
      .filter((action) => {
        const path = ((action as any) as LocationChangeAction).payload.pathname.toLowerCase();
        const split = path.split('/');

        return path.startsWith('/sequences')
          && !path.startsWith('/sequences/archive')
          && !path.startsWith('/sequences/campaigns')
          && split.length > 2;
      })
      .debounceTime(200, deps.scheduler)
      .flatMap((action) => {
        const path = ((action as any) as LocationChangeAction).payload.pathname.toLowerCase();
        const split = path.split('/');
        const filter = getQueryVariable(
          'filter',
          ((action as any) as LocationChangeAction).payload.search,
        );

        let tab = SequenceTab.Stages;
        if (split[3] === 'recipients') {
          tab = SequenceTab.Recipients;
        } else if (split[3] === 'settings') {
          tab = SequenceTab.Settings;
        }

        return Observable.concat(
          Observable.of(sequenceTabSelect(tab)),
          Observable.of(sequenceStageList(split[2])),
          Observable.of(sequenceSelect(split[2])),
          Observable.of(sequenceRecipientList({
            sequence: split[2],
            filter: filter ? filter as SequenceRecipientFilter : SequenceRecipientFilter.All,
          })),
        );
      });

const sequenceListEpic: Epic<Action<boolean, Sequence[]>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_LIST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.list(action.meta)
          .map(result => sequenceList(action.meta, result))
          .catch((e: Error) => Observable.of(sequenceList(action.meta, e))),
    );

const sequenceCreateEpic: Epic<Action<any, any>|RouterAction, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_CREATE)
      .filter(action => (action as Action<any, any>).status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.create((action as Action<any, any>).meta)
          .flatMap((result) => {
            return Observable.concat(
              Observable.of(sequenceCreate((action as Action<any, any>).meta, result)),
              Observable.of(sequenceSelect(result.id)),
              Observable.of(sequenceStageCreate({
                stage: {
                  sequence: result.id,
                  intervalDays: 3,
                  newThread: true,
                },
                navigateTo: false,
              })),
              Observable.of(push(`/sequences/${result.id}`)),
            );
          })
          .catch(error => Observable.of(toastSet(error))),
      );

const sequenceUpdateEpic: Epic<Action<Sequence|Error|null, Sequence|null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_UPDATE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.update(action.meta as Sequence)
          .map(result => sequenceUpdate(action.meta as Sequence, result))
          .catch((e: Error | ApiError) => {
            if ((e as ApiError).errors && (e as ApiError).errors!.DaysOfWeek &&
              (e as ApiError).errors!.DaysOfWeek === 'DaysOfWeek is required.') {
              (e as ApiError).errors!.DaysOfWeek =
                'You must select at least one day to send this Sequence on.';
            }
            return Observable.of(toastSet(e));
          }),
      );

const sequenceShareEpic: Epic<Action<Sequence | Error | null, Sequence | null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_SHARE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.share(action.meta as Sequence)
          .map(result => sequenceShare(action.meta as Sequence, result))
          .catch((e: Error | ApiError) => {
            if ((e as ApiError).errors && (e as ApiError).errors!.DaysOfWeek &&
              (e as ApiError).errors!.DaysOfWeek === 'DaysOfWeek is required.') {
              (e as ApiError).errors!.DaysOfWeek =
                'You must select at least one day to send this Sequence on.';
            }
            return Observable.of(toastSet(e));
          }),
      );

const sequenceDeleteEpic: Epic<Action<Sequence, null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_DELETE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.delete(action.meta)
          .map(result => sequenceDelete(action.meta, null))
          .catch((e) => {
            // handle 404 as completion
            if (e as ApiError && e.code === 404) {
              return Observable.of(sequenceDelete(action.meta, null));
            }

            throw e;
          })
          .catch((e: Error) => Observable.of(sequenceDelete(action.meta, e))),
      );

const sequenceSelectEpic: Epic<Action<
  string|SequenceRecipientListMeta, Sequence|SequenceRecipient[]
>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_SELECT)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.get(action.meta as string)
          .map(result =>
            sequenceSelect(action.meta as string, result),
          ),
      );

const sequenceCSVUploadEpic: Epic<
  Action<File|Error|null, SequenceCsvUploadPayload|null>,
  StoreState,
  Deps
> = (action$, store, deps) =>
  action$.ofType(SEQUENCE_CSV_UPLOAD)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action<File, SequenceCsvUploadPayload>) => {
      let cloudFile = '';
      return deps.cloudFile.upload(action.meta)
        .flatMap((response) => {
          cloudFile = response.cloudFile;
          return deps.sequences.parseCsv(response.cloudFile);
        })
        .map(response =>
          sequenceCsvUpload(action.meta, {
            cloudFile,
            headers: response.headers,
            emailHeader: response.emailHeader,
            familyNameHeader: response.familyNameHeader,
            givenNameHeader: response.givenNameHeader,
          }),
        )
        .catch(error => Observable.merge(
          Observable.of(toastSet(error)),
          Observable.of(sequenceCsvUpload(action.meta, error)),
        ));
    });

const sequenceDuplicateEpic: Epic<
  Action<string | Error | null, Sequence | null> | RouterAction, StoreState, Deps
>
= (action$, store, deps) =>
  action$.ofType(SEQUENCE_DUPLICATE)
    .filter((
      action: Action<string | Error | null, Sequence | null>,
    ) => action.status === ActionStatus.Start)
    .flatMap((action: Action<string | Error | null, Sequence | null>) =>
      deps.sequences.duplicate(action.meta as string)
        .flatMap(result =>
          Observable.concat(
            Observable.of(sequenceDuplicate(action.meta as string, result)),
            Observable.of(push(`/sequences/${result.id}`)),
          ),
        )
        .catch(error => Observable.of(toastSet(error))),
    );

const sequenceExportEpic: Epic<Action<Sequence|Error|null, null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_EXPORT)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.export((action.meta as Sequence).id)
          .map((file) => {
            FileSaver.saveAs(file, `sequence-${(action.meta as Sequence).name}.csv`);
            return sequenceExport(action.meta as Sequence, null);
          })
          .catch(error => Observable.of(toastSet(error))),
      );

const sequenceCountEpic: Epic<Action<null, Sequence[]>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SEQUENCE_COUNT)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.sequences.list(false)
          .map(result => sequenceCount(action.meta, result)),
      );

export default [
  locationChangeUpdateSequences,
  locationChangeSelectSequence,
  sequenceCreateEpic,
  sequenceListEpic,
  sequenceUpdateEpic,
  sequenceDeleteEpic,
  sequenceSelectEpic,
  sequenceCSVUploadEpic,
  sequenceDuplicateEpic,
  sequenceExportEpic,
  sequenceCountEpic,
  sequenceShareEpic,
];
