import React from 'react';
import {
  Resource,
  Thread,
  Me,
  TeamMembers,
  KeyCode,
  Onboarding,
  HotkeyActions,
} from '@src/types';
import {
  SharedThread,
  SharedThreadFilter,
  SharedThreadsMode,
  SharedThreadFilterMode,
} from '@src/types/sharedThreads';
import SharedThreadCell from './SharedThreadCell';
import './SharedThreadList.scss';
import Resizable, { NumberSize } from 're-resizable';
import browserHistory from '@src/lib/browserHistory';
import SearchInput from '@src/components/Form/SearchInput/SearchInput';
import Disclosure from '@src/components/Icons/Disclosure.svg';
import FilterPopover from '@src/routes/Threads/components/ThreadList/components/FilterPopover';
import { checkNextHotkeyChange, getHotkey, HotkeyAction } from '@src/lib/hotkeys';
import { OnboardingPatchMeta } from '@src/ducks/me';

export interface StateProps {
  sharedThreads: SharedThread[];
  threads: {
    [id: string]: Resource<Thread>;
  };
  me?: Me;
  width?: number;
  selectedSharedThread?: string;
  teamMembers: TeamMembers;
  filter: SharedThreadFilter;
  sharedThreadsFetched: boolean;
  isEmpty: boolean;
  onboarding?: Onboarding;
  hotkeyActions: HotkeyActions;
}

export interface OwnProps {
  mode: SharedThreadsMode;
}

export interface DispatchProps {
  setWidth: (width: number) => void;
  filterSharedThreads: (filter: SharedThreadFilter) => void;
  filterUserSharedThreads: (filter: SharedThreadFilter) => void;
  selectSharedThread: (id: string) => void;
  updateSharedThread: (s: SharedThread) => void;
  updateOnboarding: (m: OnboardingPatchMeta) => void;
}

interface State {
  sharedThreads: SharedThread[];
}

const FILTERS = ['New', 'Unread', 'Done'];
const USER_FILTERS = ['New', 'Comments', 'Done', 'All'];
const HEADER_HEIGHT = 62;
const SEARCH_HEIGHT = 30;

// TODO(SHIN): Handle empty state deletion where shared thread reappears when list is called again
class SharedThreadList extends React.PureComponent<OwnProps & StateProps & DispatchProps, State> {
  private filterPopover: FilterPopover;
  private timeout: NodeJS.Timer;
  private searchInput: SearchInput;

  constructor(props: DispatchProps & StateProps & OwnProps) {
    super(props);

    const { sharedThreads, sharedThreadsFetched } = props;

    this.state = {
      sharedThreads: sharedThreadsFetched ? sharedThreads : [],
    };
  }

  componentDidMount() {
    const { mode, updateOnboarding, onboarding } = this.props;
    this.handleFocus();

    document.body.addEventListener('focus', this.handleFocus);

    // Update onboarding
    if (onboarding && !onboarding.sharedThreadsTabGem && mode === SharedThreadsMode.User) {
      updateOnboarding({
        skipped: false,
        type: {
          sharedThreadsTabGem: true,
        },
      });
    }
  }

  componentWillUnmount() {
    document.body.removeEventListener('focus', this.handleFocus);
    if (this.timeout) {
      clearTimeout(this.timeout);
    }
  }

  UNSAFE_componentWillReceiveProps(nextProps: DispatchProps & StateProps) {
    if (nextProps.sharedThreads !== this.props.sharedThreads && nextProps.sharedThreadsFetched) {
      this.setState({
        sharedThreads: nextProps.sharedThreads,
      });
    }
  }

  // HACK(SHIN): Update focus behavior to not rely on resetting ThreadList on componentDidUpdate
  componentDidUpdate(oldProps: StateProps & DispatchProps) {
    const { hotkeyActions } = this.props;

    // Handles search hotkey
    const newActions = hotkeyActions;
    const oldActions = oldProps.hotkeyActions;
    if (newActions !== oldActions) {
      if (checkNextHotkeyChange(HotkeyAction.Search, newActions, oldActions)) {
        this.searchInput.focus(true);
        return;
      }
    }
  }

  handleSearchInputRef = (ref: SearchInput) => {
    this.searchInput = ref;
  }

  computeFilters = (mode: SharedThreadsMode) => {
    switch (mode) {
      case SharedThreadsMode.Mentions:
        return FILTERS;
      case SharedThreadsMode.User:
        return USER_FILTERS;
      default:
        return FILTERS;
    }
  }

  handleFocus = () => {
    const node = document.getElementsByClassName('shared-thread-list-main');
    if (node.length) {
      (node[0] as HTMLElement).focus();
    }
  }

  handleResizeStop = (
    event: MouseEvent,
    direction: string,
    refToElement: HTMLElement,
    delta: NumberSize,
  ) => {
    const { setWidth } = this.props;

    if (refToElement.clientWidth) {
      setWidth(refToElement.clientWidth);
    }
  }

  handleCellClick = (id: string) => {
    const pathname = browserHistory.location.pathname;
    if (pathname && pathname.startsWith('/mentions')) {
      browserHistory.push(`/mentions/${id}`);
    } else {
      browserHistory.push(`/sharedThreads/${id}`);
    }
  }

  // Handle Switched handling of mode update
  handleFilterUpdate = (filter: SharedThreadFilter) => {
    const { filterUserSharedThreads, filterSharedThreads, mode } = this.props;

    if (mode === SharedThreadsMode.User) {
      filterUserSharedThreads(filter);
      return;
    }

    filterSharedThreads(filter);
  }

  handleSearch = (query: string, complete: boolean) => {
    const { filter } = this.props;
    const updatedFilter = { ...filter, query };
    this.handleFilterUpdate(updatedFilter);
  }

  handleSearchClear = () => {
    const { filter } = this.props;
    const updatedFilter = { ...filter, query: undefined };
    this.handleFilterUpdate(updatedFilter);
  }

  showFilterPopover = () => {
    this.filterPopover.show();
  }

  handleFilterPopoverRef = (ref: FilterPopover) => {
    this.filterPopover = ref;
  }

  handleFilterSelect = (newFilter: string) => {
    const { filter } = this.props;
    const lowercaseFilter = newFilter.toLowerCase();

    // Reset to default states
    let updatedFilter: SharedThreadFilter = {
      ...filter,
      mode: SharedThreadFilterMode.New,
    };

    // Handle Filter Cases for Done
    if (lowercaseFilter === 'done') {
      updatedFilter = { ...filter, mode: SharedThreadFilterMode.Archive };
    }

    // Handle Filter Cases for 'comments
    if (lowercaseFilter === 'comments') {
      updatedFilter = { ...filter, mode: SharedThreadFilterMode.Comments };
    }

    // Handle Filter Case for All
    if (lowercaseFilter === 'all') {
      updatedFilter = { ...filter, mode: SharedThreadFilterMode.All };
    }

    // Handle Filter Case for Unread
    if (lowercaseFilter === 'unread') {
      updatedFilter = { ...filter, mode: SharedThreadFilterMode.Unread };
    }

    // Emit Filter update
    this.handleFilterUpdate(updatedFilter);
  }

  // TODO(SHIN): See if we should change browserHistory and implications of immediate select
  handleKeyDown = (e: React.KeyboardEvent<HTMLElement>) => {
    const {
      selectSharedThread,
      selectedSharedThread,
      updateSharedThread,
    } = this.props;
    const { sharedThreads } = this.state;
    let handled = false;

    if (document.activeElement && (document.activeElement.tagName === 'INPUT' ||
      document.activeElement.tagName === 'TEXTAREA')) {
      return;
    }

    const hotkey = getHotkey(e);
    switch (hotkey && hotkey.name || e.keyCode) {
      case HotkeyAction.NextThread:
      case KeyCode.J:
        if (selectedSharedThread && sharedThreads && sharedThreads.length) {
          const index = sharedThreads.findIndex(st => st.id === selectedSharedThread);
          if (index < sharedThreads.length - 1 && index > -1) {
            selectSharedThread(sharedThreads[index + 1].id);
            this.handleCellClick(sharedThreads[index + 1].id);
          }
          handled = true;
        }
        break;
      case HotkeyAction.PreviousThread:
      case KeyCode.K:
        if (selectedSharedThread && sharedThreads && sharedThreads.length) {
          const index = sharedThreads.findIndex(st => st.id === selectedSharedThread);
          if (index > 0) {
            selectSharedThread(sharedThreads[index - 1].id);
            this.handleCellClick(sharedThreads[index - 1].id);
          }
          handled = true;
        }
        break;
      case HotkeyAction.Archive:
        if (selectedSharedThread && sharedThreads) {
          const selected = sharedThreads.find(s => s.id === selectedSharedThread);
          const index = sharedThreads.findIndex(st => st.id === selectedSharedThread);
          const cell = document.getElementById(`shared-thread-cell-${selectedSharedThread}`);
          if (selected && cell) {
            cell.classList.add('unmounting');
            this.timeout = global.setTimeout(
              () => {
                if (index < sharedThreads.length - 1 && index > -1) {
                  selectSharedThread(sharedThreads[index + 1].id);
                  browserHistory.push(`/mentions/${sharedThreads[index + 1].id}`);
                }
                updateSharedThread({
                  ...selected,
                  archived: !selected.archived,
                });
              },
              600,
            );
            handled = true;
          }
        }
        break;
      default:
        break;
    }

    if (handled) {
      e.preventDefault();
      e.stopPropagation();
    }
  }

  renderEmpty = () => {
    const {
      filter,
      isEmpty,
      mode,
    } = this.props;
    const search = filter && filter.query;
    let emoji = 'ðŸ’¬';
    let description = !!search
      ? 'No search results for:'
      : 'Conversations that you\'ve been mentioned in will show up here';

    let learnMoreCopy = 'Learn more about mentions';
    let learnMoreHref = 'http://help.polymail.io/features/comments-and-mentions';
    if (mode === SharedThreadsMode.User) {
      description = 'Conversations that you\'ve shared will show up here';
      learnMoreCopy = 'Learn more about shared conversations';
      learnMoreHref = 'https://help.polymail.io/articles/2156127-shared-conversations';
    }

    if (!search && !isEmpty) {
      switch (mode) {
        case SharedThreadsMode.Mentions:
          // Handle alternative empty states when search is not active
          if (filter.mode === SharedThreadFilterMode.Archive) {
            emoji = 'âœ…';
            description = 'Conversations you\'ve marked as Done will show up here.';
          } else if (filter.mode === SharedThreadFilterMode.Unread) {
            emoji = 'âœ¨';
            description = 'No new unread Conversations. You\'re all caught up!';
          } else {
            emoji = 'ðŸŽ‰';
            description = 'Nice! You\'re up to date with your Conversations.';
          }
          break;
        case SharedThreadsMode.User:
        // Handle alternative empty states when search is not active
          if (filter.mode === SharedThreadFilterMode.Archive) {
            emoji = 'âœ…';
            description = 'Conversations you\'ve marked as Done will show up here.';
          } else if (filter.mode === SharedThreadFilterMode.Unread) {
            emoji = 'âœ¨';
            description = 'No new unread Conversations. You\'re all caught up!';
          } else {
            emoji = 'ðŸŽ‰';
            description = 'Conversations that you\'ve shared will show up here.';
          }
          break;
        default:
          break;
      }
    }

    return (
      <div className="empty-state">
        <div className="emoji">
          {emoji}
        </div>
        <div className="description">
          {description}
          {!!search &&
            <br/>
          }
          {!!search &&
            `"${search}"`
          }
        </div>
        {!search &&
          <a
            className="learn-more"
            href={learnMoreHref}
            target="_blank"
          >
            {learnMoreCopy}
          </a>
        }
      </div>
    );
  }

  render() {
    const {
      me,
      width,
      selectedSharedThread,
      teamMembers,
      filter,
      sharedThreadsFetched,
      mode,
    } = this.props;
    const { sharedThreads } = this.state;
    let filterText = 'New';
    const { query } = filter;

    // Handle Filter text
    switch (filter.mode) {
      case SharedThreadFilterMode.Archive:
        filterText = 'Done';
        break;
      case SharedThreadFilterMode.Comments:
        filterText = 'Comments';
        break;
      case SharedThreadFilterMode.New:
        filterText = 'New';
        break;
      case SharedThreadFilterMode.Unread:
        filterText = 'Unread';
        break;
      case SharedThreadFilterMode.All:
        filterText = 'All';
        break;
      default:
        break;
    }

    // TODO(SHIN): Handle Shared Thread Filter
    let headerTitle = 'Mentions';
    if (mode === SharedThreadsMode.User) {
      headerTitle = 'Shared by Me';
    }

    return (
      <Resizable
        className="shared-thread-list"
        enable={{
          top: false,
          right: true,
          bottom: false,
          left: false,
          topRight: false,
          bottomRight: false,
          bottomLeft: false,
          topLeft: false,
        }}
        defaultSize={{
          width: width || 300,
          height: 'auto',
        }}
        onResizeStop={this.handleResizeStop}
      >
        <div
          className="shared-thread-list-main"
          onKeyDown={this.handleKeyDown}
          tabIndex={-1}
        >
          <SearchInput
            ref={this.handleSearchInputRef}
            onSearch={this.handleSearch}
            search={query}
            key="search-input"
            height={SEARCH_HEIGHT}
            defaultValue={query}
            onClear={this.handleSearchClear}
            placeholder="Search Mentions"
          />
          <div className="header" style={{ height: HEADER_HEIGHT }}>
            <h4>{headerTitle}</h4>
            <button onClick={this.showFilterPopover}>
              {filterText}<Disclosure className="disclosure"/>
            </button>
          </div>
          <FilterPopover
            ref={this.handleFilterPopoverRef}
            filters={this.computeFilters(mode)}
            onFilterSelect={this.handleFilterSelect}
            selectedFilter={filterText}
          />
          <div className="main">
            {me && sharedThreadsFetched && (!sharedThreads || sharedThreads.length === 0) &&
              this.renderEmpty()
            }
            {me && sharedThreads
              .map((st: SharedThread) => {
                return (
                  <SharedThreadCell
                    key={st.id}
                    sharedThreadId={st.id}
                    me={me}
                    onClick={this.handleCellClick}
                    selected={selectedSharedThread === st.id}
                    member={st.lastAuthor ? teamMembers[st.lastAuthor] : undefined}
                  />
                );
              })}
            </div>
        </div>
      </Resizable>
    );
  }
}

export default SharedThreadList;
