import React from 'react';
import './ContactSupportPopup.scss';
import { Account, Draft, Me } from '@src/types';
import Composer from '../ThreadDetail/components/Composer';
import Popover from '@src/components/Popover/Popover';

export interface StateProps {
  me?: Me;
  accounts: Account[];
  popupCollapsed: boolean;
  draft: Draft;
}

export interface DispatchProps {
  toggleContactSupportPopup: (closed: boolean) => void;
}

const ContactSupport: React.SFC<StateProps & DispatchProps> = ({
  me,
  accounts,
  draft,
  popupCollapsed,
  toggleContactSupportPopup,
}) => {
  const title = 'Email Us';
  const content = `What is the issue? If you're reporting a bug, what are the steps you took so we can reproduce the behavior?`;

  let popoverRef: Popover;

  const handleDismiss = () => {
    toggleContactSupportPopup(!popupCollapsed);
    popoverRef?.hide();
  };

  const updateRef = (ref: Popover) => {
    popoverRef = ref;
  };

  return (
    <>
      {me && accounts && draft && !popupCollapsed && (
        <Popover
          ref={updateRef}
          contactSupport
          darkOverlay
          showOnMount
          onHide={handleDismiss}
          className="contact-support-popup"
          overlay
          fadesOut
          persistent
          allowClickPropagation={false}
        >
          <h3 className="popup-title">{title}</h3>
          <p>{content}</p>
            <Composer
              me={me}
              accounts={accounts}
              hideCommentEditor
              onSend={handleDismiss}
              className={'message-composer'}
              draft={draft}
              showFullHeight
              hideActions
            />
        </Popover>
      )}
    </>
  );
};

export default ContactSupport;
