import React from 'react';
import classnames from 'classnames';
import { Spring, animated } from 'react-spring';
import RoundCheckmark from '@src/components/Icons/RoundCheckmark.svg';
import HollowCircle from '@src/components/Icons/HollowCircle.svg';
import {
  OnboardingSection as OnboardingSectionType,
  onboardingTypesForSection, titleForOnboardingStep,
  sectionForOnboardingType, onboardingStepCompleted,
} from '@src/lib/onboarding';

import './OnboardingLegendSection.scss';
import { Onboarding, OnboardingType } from '@src/types';

export interface Props {
  onClick: (section: OnboardingSectionType) => void;
  onStepClick?: (step: OnboardingType) => void;
  section: OnboardingSectionType;
  styles: any;
  expanded?: boolean;
  completed?: boolean;
}

export interface StateProps {
  onboarding: Onboarding;
  current?: OnboardingType;
}

const OnboardingLegendSection = (props: Props & StateProps) => {
  const {
    section, styles, onClick, expanded, current, onStepClick, completed, onboarding,
  } = props;

  const handleClick = () => {
    onClick(section);
  };

  const handleStepClick = (step: OnboardingType) => {
    if (onStepClick) {
      onStepClick(step);
    }
  };

  const currentSection = current && sectionForOnboardingType(current) === section;
  const currentDisabled = currentSection && expanded;
  const blockedDisable = !currentSection && !completed;

  return (
    <animated.div
      key={section}
      style={styles}
      className={classnames('onboarding-legend-section', { blocked: !completed })}
    >
      <div className="top">
        <div className="icon">
          {completed && (
            <RoundCheckmark height="16px" width="16px" />
          )}
          {!completed && (
            <HollowCircle height="16px" width="16px" />
          )}
        </div>
        <button
          className={classnames('title', { expanded })}
          disabled={currentDisabled || blockedDisable}
          onClick={handleClick}
        >
          {section}
        </button>
      </div>
      <Spring
        from={{
          height: 1,
          opacity: 0,
        }}
        to={{
          height: expanded ? 30 : 0,
          opacity: expanded ? 1 : 0,
        }}
      >
        {sp => (
          <div className="section-items">
            {onboardingTypesForSection(section).map(si => (
              <OnboardingStepItem
                key={si}
                step={si}
                styles={sp}
                shown={expanded}
                onClick={handleStepClick}
                current={si === current}
                completed={onboardingStepCompleted(onboarding, si)}
              />
            ))}
          </div>
        )}
      </Spring>
    </animated.div>
  );
};

interface StepProps {
  step: OnboardingType;
  styles: any;
  current: boolean;
  completed?: boolean;
  shown?: boolean;
  onClick: (step: OnboardingType) => void;
}

export const OnboardingStepItem = (props: StepProps) => {
  const { step, styles, shown, current, onClick, completed } = props;

  const handleClick = (e: React.MouseEvent<any>) => {
    onClick(step);
  };

  return (
    <animated.button
      key={step}
      style={styles}
      className={classnames('onboarding-section-item', {
        shown,
        current,
        disabled: !current && !completed,
      })}
      onClick={handleClick}
      disabled={!current && !completed}
    >
      {titleForOnboardingStep(step)}
    </animated.button>
  );
};

export default OnboardingLegendSection;
