import { Observable } from 'rxjs';
import { Epic } from 'redux-observable';

import { ActionStatus, Action, StoreState } from '@src/types';

import { Deps } from '@src/lib/epic';

import {
  SALESFORCE_SUMMARY_FETCH, salesforceSummaryFetch,
  SALESFORCE_SUMMARY_CREATE, SalesforceSummaryCreateMeta,
  SalesforceSummaryMetaType, salesforceSummaryCreate,
  SALESFORCE_TASK_CREATE, salesforceTaskCreate, SalesforceTaskCreateMeta,
  SALESFORCE_LEAD_CONVERT, salesforceLeadConvert,
  SALESFORCE_SUMMARY_UPDATE, SalesforceSummaryUpdateMeta, salesforceSummaryUpdate,
  SALESFORCE_LISTVIEW,
  salesforceListView,
  SALESFORCE_CONTACT_LIST,
  salesforceContactList,
  salesforceLeadsList,
  SALESFORCE_LEADS_LIST,
  salesforceSchemaGet,
  SALESFORCE_SCHEMA_GET,
  salesforcePendingClear,
} from '@src/ducks/salesforce';

import {
  SFDCListView,
  SObject,
  SObjectType,
  SalesforceSummary,
  SObjectSchema,
} from '@src/types/salesforce';
import { toastSet } from '@src/ducks/toasts';
import { SalesforceAPI } from '@src/api/salesforce';

const fetchSummary: Epic<Action<string, SalesforceSummary>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SALESFORCE_SUMMARY_FETCH)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.salesforce.get(action.meta)
          .map(result => salesforceSummaryFetch(action.meta, result))
          .catch((e: Error) => Observable.of(salesforceSummaryFetch(action.meta, e))),
      );

const createSummary: Epic<
  Action<SalesforceSummaryCreateMeta, SalesforceSummary|null>, StoreState, Deps
> = (action$, store, deps) =>
    action$.ofType(SALESFORCE_SUMMARY_CREATE)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action) => {
        switch (action.meta.type) {
          case SalesforceSummaryMetaType.Contact:
            return deps.salesforce.createContact(action.meta)
              .flatMap(result =>
                Observable.merge(
                  Observable.of(salesforceSummaryCreate(action.meta, result)),
                  Observable.of(salesforcePendingClear(action.meta)),
                ))
              .catch((e: Error) => Observable.of(salesforceSummaryCreate(action.meta, e)));
          case SalesforceSummaryMetaType.Lead:
            return deps.salesforce.createLead(action.meta)
              .flatMap(result =>
                Observable.merge(
                  Observable.of(salesforceSummaryCreate(action.meta, result)),
                  Observable.of(salesforcePendingClear(action.meta)),
                ))
              .catch((e: Error) => Observable.of(salesforceSummaryCreate(action.meta, e)));
          default:
            return Observable.of(salesforceSummaryCreate(
                action.meta,
                new Error('Unknown Salesforce Type: ' + action.meta.type,
            )));
        }
      });

const createTask: Epic<Action<SalesforceTaskCreateMeta|Error|null, SObject|null>, StoreState, Deps>
    = (action$, store, deps) =>
      action$.ofType(SALESFORCE_TASK_CREATE)
        .filter(action => action.status === ActionStatus.Start)
        .flatMap((action: Action<SalesforceTaskCreateMeta, SObject>) =>
          deps.salesforce.createTask(action.meta)
            .map(result => salesforceTaskCreate(action.meta, result))
            .catch((e: Error) => Observable.of(toastSet(e))),
        );

const leadConvert: Epic<Action<string|Error|null, SalesforceSummary|null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SALESFORCE_LEAD_CONVERT)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<string, SalesforceSummary>) =>
        deps.salesforce.convertLeadToContact(action.meta)
          .map(result => salesforceLeadConvert(action.meta, result))
          .catch((e: Error) => Observable.of(toastSet(e))),
      );

const updateFieldRequest = (
  api: SalesforceAPI, meta: SalesforceSummaryUpdateMeta,
): Observable<SObject> => {
  switch (meta.type) {
    case SalesforceSummaryMetaType.Account:
      return api.updateAccount(meta.id, meta.name, meta.value);

    case SalesforceSummaryMetaType.Contact:
      return api.updateContact(meta.email, meta.name, meta.value);

    case SalesforceSummaryMetaType.Lead:
      return api.updateLead(meta.email, meta.name, meta.value);

    default:
      throw new Error(`unknown field type: ${meta.type}`);
  }
};

const updateSummary: Epic<
  Action<SalesforceSummaryUpdateMeta|Error|null, SObject|null>, StoreState, Deps
> = (action$, store, deps) =>
  action$.ofType(SALESFORCE_SUMMARY_UPDATE)
    .filter(action => action.status === ActionStatus.Start)
    .flatMap((action: Action <SalesforceSummaryUpdateMeta, SObject>) =>
      updateFieldRequest(deps.salesforce, action.meta)
        .map(object => salesforceSummaryUpdate(action.meta, object))
        .catch(e => Observable.of(toastSet(e))),
    );

const listView: Epic<Action<SObjectType, SFDCListView[]>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SALESFORCE_LISTVIEW)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap(action =>
        deps.salesforce.listViews(action.meta)
          .map(result => salesforceListView(action.meta, result))
          .catch((e: Error) => Observable.of(salesforceListView(action.meta, e))),
    );

const contactList: Epic<Action<string|Error|null, SObject[]|null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SALESFORCE_CONTACT_LIST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<string, SObject[]>) =>
        deps.salesforce.contacts(action.meta)
          .map(result => salesforceContactList(action.meta, result))
          .catch((e: Error) => Observable.merge(
            Observable.of(toastSet(e)),
            Observable.of(salesforceContactList(action.meta, e)),
          )),
      );

const leadsList: Epic<Action<string|Error|null, SObject[]|null>, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SALESFORCE_LEADS_LIST)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<string, SObject[]>) =>
        deps.salesforce.leads(action.meta)
          .map(result => salesforceLeadsList(action.meta, result))
          .catch((e: Error) => Observable.merge(
            Observable.of(toastSet(e)),
            Observable.of(salesforceLeadsList(action.meta, e)),
          )),
    );

export const getSchemas: Epic<Action<
  SObjectType|Error|null, SObjectSchema|null
  >, StoreState, Deps>
  = (action$, store, deps) =>
    action$.ofType(SALESFORCE_SCHEMA_GET)
      .filter(action => action.status === ActionStatus.Start)
      .flatMap((action: Action<SObjectType, SObjectSchema>) =>
        deps.salesforce.getSchema(action.meta)
          .map(result => salesforceSchemaGet(action.meta, {
            fields: result.filter(field => field.creatable),
          }))
          .catch((e: Error) => Observable.merge(
            Observable.of(toastSet(e)),
            Observable.of(salesforceSchemaGet(action.meta, e)),
          )),
    );

export default [
  fetchSummary,
  createSummary,
  updateSummary,
  createTask,
  leadConvert,
  listView,
  contactList,
  leadsList,
  getSchemas,
];
