import { keyBy } from 'lodash';
import { create, createAction } from '@src/lib/redux';
import update from 'immutability-helper';

import { TeamMember, StoreState, TeamMembers, Me, TeamInvite, Team, Resource } from '@src/types';
import { defaultTeamMember } from '@src/lib/teamMember';

import emptyPixel from '@src/assets/emptyPixel.png';
import { resourceOk, resourceLoading, resourceFailed, resourcePending } from '@src/lib/resource';
import { TeamInviteValidateResponse } from '@src/api/team';

export const {
  type: TEAM_MEMBER_FETCH,
  action: teamMemberFetch,
  reducer: teamMemberFetchReducer,
} = create<null, TeamMember[]>('TEAM_MEMBER_FETCH', {
  success: (state, meta, payload) => update(state, {
    teamMembers: { $set: keyBy(payload, 'id') },
  }),
});

export const {
  type: TEAM_MEMBER_REMOVE,
  action: teamMemberRemove,
  reducer: teamMemberRemoveReducer,
} = create<string, null>('TEAM_MEMBER_REMOVE', {
  success: (state, meta, payload) =>
    update(state, {
      teamMembers: { $unset: [meta] },
    }),
});

export const TEAM_MEMBER_INVITE = 'TEAM_MEMBER_INVITE';
export const teamMemberInvite = createAction<string, null>(TEAM_MEMBER_INVITE);

export const {
  type: TEAM_INVITE_LIST,
  action: teamInviteList,
  reducer: teamInviteListReducer,
} = create<null, TeamInvite[]>('TEAM_INVITE_LIST', {
  success: (state, meta, payload) => ({
    ...state,
    teamInvites: resourceOk(payload),
  }),
});

export const {
  type: TEAM_ADMIN_ADD,
  action: teamAdminAdd,
  reducer: teamAdminAddReducer,
} = create<string, Team>('TEAM_ADMIN_ADD', {
  success: (state, meta, payload) =>
    update(state, {
      team: { $set: resourceOk(payload) },
    }),
});

export const {
  type: TEAM_ADMIN_REMOVE,
  action: teamAdminRemove,
  reducer: teamAdminRemoveReducer,
} = create<string, Team>('TEAM_ADMIN_REMOVE', {
  success: (state, meta, payload) =>
    update(state, {
      team: { $set: resourceOk(payload) },
    }),
});

export interface TeamInviteValidateMeta {
  nonce: string;
  payload: string;
}

export const {
  type: TEAM_INVITE_VALIDATE,
  action: teamInviteValidate,
  reducer: teamInviteValidateReducer,
} = create<TeamInviteValidateMeta, TeamInviteValidateResponse>('TEAM_INVITE_VALIDATE', {
  start: (state, meta) =>
    update(state, {
      teamInvitesValidation: {
        [teamInvitesValidationKey(meta)]: {
          $set: resourceLoading(),
        },
      },
    }),
  success: (state, meta, payload) =>
    update(state, {
      teamInvitesValidation: {
        [teamInvitesValidationKey(meta)]: {
          $set: resourceOk(payload),
        },
      },
    }),
  failure: (state, meta, error) =>
    update(state, {
      teamInvitesValidation: {
        [teamInvitesValidationKey(meta)]: {
          $set: resourceFailed(error),
        },
      },
    }),
});

export const teamInvitesValidationKey = (meta: TeamInviteValidateMeta) =>
  `${meta.nonce}${meta.payload}`;

export const selectTeamInvitesValidation = (state: StoreState, meta: TeamInviteValidateMeta):
  Resource<TeamInviteValidateResponse> =>
    state.teamInvitesValidation[teamInvitesValidationKey(meta)] || resourcePending();

export const TEAM_INVITE_RESEND = 'TEAM_INVITE_RESEND';
export const teamInviteResend =
  createAction<string, null>(TEAM_INVITE_RESEND);

export const TEAM_INVITE_REMOVE = 'TEAM_INVITE_REMOVE';
export const teamInviteRemove =
  createAction<string, null>(TEAM_INVITE_REMOVE);

export const selectTeamMember = (state: StoreState, userId: string) => {
  return state.teamMembers[userId] || state.publicMembers[userId] ||
    defaultTeamMember(userId);
};

export const selectFirstAdminTeamMember = (state: StoreState): TeamMember|undefined => {
  const team = state.team.data;
  const teamAdminId = team && !!team.admins.length && team.admins[0] || '';
  return state.teamMembers[teamAdminId];
};

// Returns photo url of team member
export const selectPhotoUrl = (members: TeamMembers, me: Me, id: string) => {
  // handle self case
  if (id === me.id) {
    return me.photoUrl;
  }

  // handle team members
  if (members[id]) {
    return members[id].photoUrl;
  }

  // default to empty pixel
  return emptyPixel;
};

export default [
  teamMemberFetchReducer,
  teamMemberRemoveReducer,
  teamInviteListReducer,
  teamInviteValidateReducer,
  teamAdminAddReducer,
  teamAdminRemoveReducer,
];
