import React from 'react';
import classnames from 'classnames';

import Disclosure from '@src/components/Icons/Disclosure.svg';

import './SequenceListHeader.scss';
import { SortDirection } from '../../SequenceList';
import Checkbox from '@src/components/Form/Checkbox/Checkbox';

export interface Props {
  sortValue: string;
  sortDirection: SortDirection;
  allChecked: boolean;
  padName?: boolean;
  onSortClick?: (key: string) => void;
  onCheckToggle: () => void;
}

export enum SequenceListHeaderSortKey {
  Name = 'slh-name',
  Delivered = 'slh-delivered',
  Opened = 'slh-opened',
  Clicked = 'slh-clicked',
  Replied = 'slh-replied',
  Date = 'slh-date',
}

const SequenceListHeader = (props: Props) => {
  const {
    sortValue, onSortClick,
    sortDirection, allChecked,
    onCheckToggle, padName,
  } = props;
  const onClick = (el: React.MouseEvent<HTMLButtonElement>) => {
    const target = el.currentTarget;
    if (!target.id || !onSortClick) {
      return;
    }

    switch (target.id) {
      case SequenceListHeaderSortKey.Name:
        onSortClick(SequenceListHeaderSortKey.Name);
        return;
      case SequenceListHeaderSortKey.Delivered:
        onSortClick(SequenceListHeaderSortKey.Delivered);
        return;
      case SequenceListHeaderSortKey.Opened:
        onSortClick(SequenceListHeaderSortKey.Opened);
        return;
      case SequenceListHeaderSortKey.Clicked:
        onSortClick(SequenceListHeaderSortKey.Clicked);
        return;
      case SequenceListHeaderSortKey.Replied:
        onSortClick(SequenceListHeaderSortKey.Replied);
        return;
      case SequenceListHeaderSortKey.Date:
        onSortClick(SequenceListHeaderSortKey.Date);
        return;
      default:
        return;
    }
  };

  return (
    <div className="sequence-list-header">
        <Checkbox
          onChange={onCheckToggle}
          checked={allChecked}
        />
      <div className="container">
        <div className="sequence-info">
          <button
            id={SequenceListHeaderSortKey.Name}
            className={classnames(
              'sequence-name',
              'header-button',
              { selected: sortValue === SequenceListHeaderSortKey.Name },
              { padded: !!padName },
            )}
            onClick={onClick}
          >
            <h5 className="info">
              Sequence Name
            </h5>
            {sortValue === SequenceListHeaderSortKey.Name && (
              <Disclosure
                className={classnames('sort-disclosure', sortDirection)}
              />
            )}
          </button>
        </div>
        <div className="sequence-analytics">
          <button
            id={SequenceListHeaderSortKey.Delivered}
            className={classnames(
              'analytic delivered-container',
              'header-button',
              { selected: sortValue === SequenceListHeaderSortKey.Delivered })
            }
            onClick={onClick}
          >
            <h5>Delivered</h5>
            {sortValue === SequenceListHeaderSortKey.Delivered && (
              <Disclosure
                className={classnames('sort-disclosure', sortDirection)}
              />
            )}
          </button>
          <button
            id={SequenceListHeaderSortKey.Opened}
            className={classnames(
              'analytic opened-container',
              'header-button',
              { selected: sortValue === SequenceListHeaderSortKey.Opened })
            }
            onClick={onClick}
          >
            <h5>Opened</h5>
            {sortValue === SequenceListHeaderSortKey.Opened && (
              <Disclosure
                className={classnames('sort-disclosure', sortDirection)}
              />
            )}
          </button>
          <button
            id={SequenceListHeaderSortKey.Clicked}
            className={classnames(
              'analytic clicked-container',
              'header-button',
              { selected: sortValue === SequenceListHeaderSortKey.Clicked })
            }
            onClick={onClick}
          >
            <h5>Clicked</h5>
            {sortValue === SequenceListHeaderSortKey.Clicked && (
              <Disclosure
                className={classnames('sort-disclosure', sortDirection)}
              />
            )}
          </button>
          <button
            id={SequenceListHeaderSortKey.Replied}
            className={classnames(
              'analytic replied-container',
              'header-button',
              { selected: sortValue === SequenceListHeaderSortKey.Replied })
            }
            onClick={onClick}
          >
            <h5>Replied</h5>
            {sortValue === SequenceListHeaderSortKey.Replied && (
              <Disclosure
                className={classnames('sort-disclosure', sortDirection)}
              />
            )}
          </button>
        </div>
        <button
          id={SequenceListHeaderSortKey.Date}
          className={classnames(
            'date',
            'header-button',
            { selected: sortValue === SequenceListHeaderSortKey.Date })
          }
          onClick={onClick}
        >
          Created
          {sortValue === SequenceListHeaderSortKey.Date && (
              <Disclosure
                className={classnames('sort-disclosure', sortDirection)}
              />
            )}
        </button>
      </div>
    </div>
  );
};

export default SequenceListHeader;
