import React from 'react';
import moment from 'moment';
import ReactDataGrid from 'react-data-grid';
import { Resource } from '@src/types';
import {
  SequenceStageRecipient,
  SequenceStage,
  SequenceRecipientStatus,
  SequenceRecipientsState,
  SequenceStageRecipientFilter,
  SequenceStageRecipientSort,
} from '@src/types/sequences';

import SearchInput from '@src/components/Form/SearchInput/SearchInput';
import { cloneDeep, values, findIndex, capitalize, debounce } from 'lodash';
import RecipientNameCell from '@src/components/Cells/RecipientNameCell/RecipientNameCell';
import RecipientPausedCell from '@src/components/Cells/RecipientPausedCell/RecipientPausedCell';
import RecipientRepliedCell from '@src/components/Cells/RecipientRepliedCell/RecipientRepliedCell';

import './SequenceStageRecipientsList.scss';
import {
  SequenceRecipientDeleteMeta,
} from '@src/ducks/sequenceRecipients';
import Dropdown from '@src/components/Form/Dropdown';
import RecipientStatusCell from '@src/components/Cells/RecipientStatusCell/RecipientStatusCell';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import { SequenceStageRecipientMeta } from '@src/ducks/sequenceStageRecipients';
import { RouteComponentProps } from 'react-router';

export interface RecipientCount {
  sent: number;
  scheduled: number;
  paused: number;
  bounced: number;
}

export interface StateProps {
  stageRecipients?: Resource<SequenceStageRecipient[]>;
  recipientsState?: SequenceRecipientsState;
}

export interface DispatchProps {
  updateRecipient: (recipient: SequenceStageRecipient) => void;
  deleteRecipient: (recipient: SequenceRecipientDeleteMeta) => void;
  listRecipients: (meta: SequenceStageRecipientMeta) => void;
}

export interface Props {
  stage: SequenceStage;
  recipientCounts: RecipientCount;
}

export interface Row {
  activeProps: {
    recipientId: string;
    active: boolean | null,
    onPauseToggle: (id: string) => void,
  };
  name: { email: string, fullName: string };
  opens: number | string;
  clicks: number | string;
  downloads: number | string;
  replied: boolean;
  status: string;
  isSelected: boolean;
  index: number;
}

const EMPTY_TEXT = {
  [SequenceStageRecipientFilter.Sent]: 'No recipients have been sent a message this stage yet.',
  [SequenceStageRecipientFilter.Scheduled]:
    'No recipients are scheduled to receive this stage yet.',
  [SequenceStageRecipientFilter.Paused]: 'No recipients are paused in this stage.',
  [SequenceStageRecipientFilter.Bounced]: 'No recipients have bounced in this stage.',
  [SequenceStageRecipientFilter.Delivered]:
    'No recipients have been delivered a message on this stage yet.',
  [SequenceStageRecipientFilter.Opened]: 'No recipients have opened a message on this stage yet.',
  [SequenceStageRecipientFilter.Clicked]:
    'No recipients have clicked on a link in a message on this stage yet.',
  [SequenceStageRecipientFilter.Downloaded]:
    'No recipients have downloaded any attachments on this stage yet.',
  [SequenceStageRecipientFilter.Replied]:
    'No recipients have replied to a message on this stage yet.',
  [SequenceStageRecipientFilter.Unsubscribed]: 'No recipients have unsubscribed on this stage.',
};

interface State {
  rows: Row[];
  counts: { [filter: string]: number };
  confirmProps?: ConfirmProps;
}

const columns = [
  {
    key: 'activeProps',
    name: 'Active',
    width: 60,
    resizable: false,
    sortable: true,
    formatter: RecipientPausedCell,
  },
  {
    key: 'name',
    name: 'Recipient',
    resizable: true,
    width: 200,
    sortable: true,
    formatter: RecipientNameCell,
  },
  {
    key: 'sent',
    name: 'Sent',
    width: 150,
    resizeable: true,
    sortable: true,
  },
  {
    key: 'willSend',
    name: 'Will Send',
    width: 150,
    resizeable: true,
    sortable: true,
  },
  {
    key: 'opens',
    name: 'Opens',
    cellClass: 'opens',
    resizable: true,
    sortable: true,
  },
  {
    key: 'clicks',
    name: 'Clicks',
    cellClass: 'clicks',
    resizable: true,
    sortable: true,
  },
  {
    key: 'downloads',
    name: 'Downloads',
    cellClass: 'download',
    resizable: true,
    sortable: true,
  },
  {
    key: 'replied',
    name: 'Replied',
    resizable: true,
    sortable: true,
    formatter: RecipientRepliedCell,
  },
  {
    key: 'status',
    name: 'Status',
    resizable: true,
    sortable: true,
    formatter: RecipientStatusCell,
  },
];

class SequenceRecipientsList extends React.PureComponent<
  StateProps & DispatchProps & Props & RouteComponentProps<any>, State
> {
  listRecipientsDebounce: any;

  constructor(props: StateProps & DispatchProps & Props & RouteComponentProps<any>) {
    super();

    const counts = this.getCounts(props.stage, props.recipientCounts);

    this.listRecipientsDebounce = debounce(props.listRecipients, 200);
    this.state = {
      rows: this.createRows(props.stageRecipients),
      counts,
    };
  }

  UNSAFE_componentWillReceiveProps(
    nextProps: StateProps & DispatchProps & Props & RouteComponentProps<any>,
  ) {
    if (nextProps.stageRecipients !== this.props.stageRecipients) {
      this.setState({
        rows: this.createRows(nextProps.stageRecipients),
        counts: this.getCounts(nextProps.stage, nextProps.recipientCounts),
      });
    }
  }

  getCounts = (stage: SequenceStage, recipientCounts: RecipientCount) => {
    if (!stage) {
      return {
        [SequenceStageRecipientFilter.Scheduled]: 0,
        [SequenceStageRecipientFilter.Sent]: 0,
        [SequenceStageRecipientFilter.Paused]: 0,
        [SequenceStageRecipientFilter.Bounced]: 0,
        [SequenceStageRecipientFilter.Delivered]: 0,
        [SequenceStageRecipientFilter.Opened]: 0,
        [SequenceStageRecipientFilter.Clicked]: 0,
        [SequenceStageRecipientFilter.Downloaded]: 0,
        [SequenceStageRecipientFilter.Replied]: 0,
        [SequenceStageRecipientFilter.Unsubscribed]: 0,
      };
    }

    return {
      [SequenceStageRecipientFilter.Scheduled]: stage.recipients.scheduled,
      [SequenceStageRecipientFilter.Sent]: recipientCounts.sent,
      [SequenceStageRecipientFilter.Paused]: recipientCounts.paused,
      [SequenceStageRecipientFilter.Bounced]: recipientCounts.bounced,
      [SequenceStageRecipientFilter.Delivered]: stage.analytics.delivered,
      [SequenceStageRecipientFilter.Opened]: stage.analytics.opened,
      [SequenceStageRecipientFilter.Clicked]: stage.analytics.clicked,
      [SequenceStageRecipientFilter.Downloaded]: stage.analytics.downloaded,
      [SequenceStageRecipientFilter.Replied]: stage.analytics.replied,
      [SequenceStageRecipientFilter.Unsubscribed]: stage.analytics.unsubscribed,
    };
  }

  componentWillUnmount() {
    if (this.listRecipientsDebounce) {
      this.listRecipientsDebounce.cancel();
    }
  }

  handleRecipientPauseToggle = (id: string) => {
    const { updateRecipient, stageRecipients } = this.props;
    const recipient = stageRecipients && stageRecipients.data &&
      stageRecipients.data.find(r => r.id === id);

    if (recipient) {
      updateRecipient({
        ...recipient,
        paused: !recipient.paused,
      });
    }
  }

  createRows = (
    stageRecipients?: Resource<SequenceStageRecipient[]>,
  ): Row[] => {
    if (!stageRecipients || !stageRecipients.data || !stageRecipients.data.length) {
      return [];
    }

    return stageRecipients.data
      .map((r, index) => {
        let status = capitalize(r.status);
        const unsubscribed = r.sequenceStatus === SequenceRecipientStatus.Unsubscribed
          || !!r.analytics.unsubscribed;
        if (unsubscribed) {
          status = 'Unsub';
        } else if (r.paused) {
          status = 'Paused';
        }

        let willSend = '-';
        if (r.willSend && !r.paused && !unsubscribed
          && moment().isBefore(r.willSend)) {
          willSend = moment(r.willSend).format('MMM D - h:mma');
        } else if (r.willSend && !r.paused && !unsubscribed) {
          willSend = 'Sending';
        }

        const oldRow = this.getRowForRecipient(r.id);

        return {
          activeProps: {
            recipientId: r.id,
            active: r.sequenceStatus === SequenceRecipientStatus.Active
              ? !r.paused : null,
            onPauseToggle: this.handleRecipientPauseToggle,
          },
          name: {
            email: r.email,
            fullName: r.fullName,
          },
          sent: r.sent && moment(r.sent).format('MMM D - h:mma'),
          willSend,
          opens: r.analytics.opened || '-',
          clicks: r.analytics.clicked || '-',
          downloads: r.analytics.downloaded || '-',
          replied: !!r.analytics.replied,
          status,
          title: r.fullName,
          isSelected: oldRow && oldRow.isSelected || false,
          index,
        };
      });
  }

  handleSearch = (query: string, complete: boolean) => {
    const { stage, recipientsState } = this.props;

    if (recipientsState) {
      this.listRecipientsDebounce({
        stage: stage.id,
        sequence: stage.sequence,
        filter: recipientsState.filter,
        sort: recipientsState.sort,
        query,
        desc: recipientsState.desc,
      });
    }
  }

  getColumns = () => {
    const { recipientsState } = this.props;
    const recipientFilter = recipientsState && recipientsState.filter;
    let clonedColumns = columns.slice();

    if (recipientFilter === SequenceStageRecipientFilter.Sent ||
      recipientFilter === SequenceStageRecipientFilter.Delivered ||
      recipientFilter === SequenceStageRecipientFilter.Opened ||
      recipientFilter === SequenceStageRecipientFilter.Clicked ||
      recipientFilter === SequenceStageRecipientFilter.Downloaded ||
      recipientFilter === SequenceStageRecipientFilter.Replied ||
      recipientFilter === SequenceStageRecipientFilter.Paused) {
      clonedColumns = clonedColumns.filter(c => c.key !== 'willSend');
    }

    if (recipientFilter === SequenceStageRecipientFilter.Scheduled ||
      recipientFilter === SequenceStageRecipientFilter.Paused ||
      recipientFilter === SequenceStageRecipientFilter.Unsubscribed ||
      recipientFilter === SequenceStageRecipientFilter.Bounced) {
      clonedColumns = clonedColumns.filter(c => c.key !== 'sent');
    }

    return clonedColumns;
  }

  getRowAt = (index: number): object => {
    const { recipientsState, stage } = this.props;
    const { rows } = this.state;

    if (index === rows.length - 1 && stage && recipientsState
      && recipientsState.offset !== rows.length) {
      this.listRecipientsDebounce({
        filter: recipientsState.filter,
        sequence: stage.sequence,
        stage: stage.id,
        sort: recipientsState.sort,
        query: recipientsState.query,
        offset: rows.length,
        desc: recipientsState.desc,
      });
    }

    return rows[index];
  }

  getRowForRecipient = (recipientId: string): Row | undefined => {
    // Special handle case since state could be undefined when first setting rows
    if (!this.state) {
      return;
    }

    const { rows } = this.state;
    const index = findIndex(
      rows,
      row => row.activeProps.recipientId === recipientId,
    );
    return rows[index];
  }

  handleRowSelect = (selected: Array<{ rowIdx: number, row: Row }>) => {
    const { rows } = this.state;

    if (selected.length) {
      const newRows = cloneDeep(rows);
      selected.forEach(s => newRows[s.rowIdx].isSelected = true);
      this.setState({ rows: newRows });
    }
  }

  handleRowDeselect = (deselected: Array<{ rowIdx: number, row: Row }>) => {
    const { rows } = this.state;

    if (deselected.length) {
      const newRows = cloneDeep(rows);
      deselected.forEach(s => newRows[s.rowIdx].isSelected = false);
      this.setState({ rows: newRows });
    }
  }

  handleCheckedDelete = () => {
    const { deleteRecipient, stage } = this.props;
    const { rows } = this.state;

    // Grab all recipients
    const checkedRecipients = rows
      .filter(row => row.isSelected)
      .map(row => ({
        id: row.activeProps.recipientId,
        sequence: stage.sequence,
      }));

    // Handle deletion of all checked recipients
    const deleteHandler = () => {
      checkedRecipients.forEach((recipient) => {
        deleteRecipient(recipient);
      });
      this.setState({
        confirmProps: undefined,
      });
    };

    // Confirm dialogue for deleting recipients
    if (checkedRecipients.length > 0) {
      const title = checkedRecipients.length > 1
        ? `Remove ${checkedRecipients.length} Recipients?`
        : 'Remove Recipient?';
      const recipientDescriptor = checkedRecipients.length > 1
        ? `these recipients`
        : 'this recipient';
      const message = `Are you sure you want to remove ${recipientDescriptor} from this sequence?`
        + ' Any analytics and recipients data will be lost forever';
      const confirmProps: ConfirmProps = {
        title,
        message,
        confirmCopy: 'Remove',
        cancelCopy: 'Cancel',
        onConfirm: () => deleteHandler(),
        onCancel: () => this.setState({
          confirmProps: undefined,
        }),
      };

      // Show confirm dialogue
      this.setState({ confirmProps });
    }
  }

  handleCheckedPause = () => {
    const { stage, updateRecipient, stageRecipients } = this.props;
    const { rows } = this.state;

    // Grab all recipients
    const checkedRecipients = rows
      .filter(row => row.isSelected && row.activeProps.active)
      .map(row => ({
        id: row.activeProps.recipientId,
        sequence: stage.sequence,
        index: row.index,
      }));

    checkedRecipients.forEach((recipient) => {
      const stageRecipient = stageRecipients && stageRecipients.data &&
        stageRecipients.data[recipient.index];
      if (stageRecipient) {
        updateRecipient({
          ...stageRecipient,
          paused: true,
        });
      }
    });
  }

  handleCheckedUnpause = () => {
    const { stage, updateRecipient, stageRecipients } = this.props;
    const { rows } = this.state;

    // Grab all recipients
    const checkedRecipients = rows
      .filter(row => row.isSelected && !row.activeProps.active)
      .map(row => ({
        id: row.activeProps.recipientId,
        sequence: stage.sequence,
        index: row.index,
      }));

    checkedRecipients.forEach((recipient) => {
      const stageRecipient = stageRecipients && stageRecipients.data &&
        stageRecipients.data[recipient.index];
      if (stageRecipient) {
        updateRecipient({
          ...stageRecipient,
          paused: false,
        });
      }
    });
  }

  handleGridSort = (col: string, sortBy: string) => {
    const { recipientsState, stage } = this.props;
    const mapping = {
      activeProps: SequenceStageRecipientSort.Paused,
      sent: SequenceStageRecipientSort.Sent,
      willSend: SequenceStageRecipientSort.Scheduled,
      opens: SequenceStageRecipientSort.Opened,
      clicks: SequenceStageRecipientSort.Clicked,
      downloads: SequenceStageRecipientSort.Downloaded,
      replied: SequenceStageRecipientSort.Replied,
      status: SequenceStageRecipientSort.Status,
    };

    if (recipientsState) {
      this.listRecipientsDebounce({
        query: recipientsState.query,
        sequence: stage.sequence,
        stage: stage.id,
        filter: recipientsState.filter,
        sort: mapping[col],
        desc: sortBy === 'DESC',
      });
    }
  }

  handleFilterSelect = (value: string, index: number, id?: string) => {
    const { stage, recipientsState } = this.props;

    if (recipientsState) {
      this.listRecipientsDebounce({
        stage: stage.id,
        sequence: stage.sequence,
        filter: value.slice(0, value.indexOf('(') - 1).toLowerCase(),
        sort: recipientsState.sort,
        query: recipientsState.query,
        desc: recipientsState.desc,
      });
    }
  }

  render() {
    const { recipientsState } = this.props;
    const { rows, counts, confirmProps } = this.state;
    const numChecked = rows.filter(row => row.isSelected).length;

    if (!recipientsState) {
      return <div />;
    }

    return (
      <div className="sequence-stage-recipients-list">
        {confirmProps &&
          <Confirm
            {...confirmProps}
          />
        }
        <div className="header">
          <div className="filter">
            <Dropdown
              values={values(SequenceStageRecipientFilter)
                .map(d => `${capitalize(d)} (${counts[d]})`)}
              onSelect={this.handleFilterSelect}
              startIndex={values(SequenceStageRecipientFilter)
                // TODO: Need to fix how filters are used in Sequences UI
                .indexOf(recipientsState.filter as any)}
              value={`${capitalize(recipientsState.filter)} (${counts[recipientsState.filter]})`}
            />
          </div>
          {!!numChecked && <div className="action-bar">
            <span className="selected-num">
              {`${numChecked} selected`}
            </span>
            <button className="pause" onClick={this.handleCheckedPause}>Pause</button>
            <button className="unpause" onClick={this.handleCheckedUnpause}>Activate</button>
            <button className="delete" onClick={this.handleCheckedDelete}>Delete</button>
          </div>}
          <div className="search">
            <SearchInput
              onSearch={this.handleSearch}
              placeholder="Search recipients"
              defaultValue=""
            />
          </div>
        </div>
        <div className="table">
          {rows && !!rows.length && <ReactDataGrid
            enableCellSelect={true}
            columns={this.getColumns()}
            rowGetter={this.getRowAt}
            rowsCount={rows.length}
            enableRowSelect={true}
            rowHeight={60}
            minHeight={60 * 11}
            rowScrollTimeout={200}
            rowSelection={{
              selectBy: { isSelectedKey: 'isSelected' },
              showCheckbox: true,
              onRowsSelected: this.handleRowSelect,
              onRowsDeselected: this.handleRowDeselect,
            }}
            onGridSort={this.handleGridSort}
          />}
          {rows && !rows.length && <div className="empty">
            <span>{EMPTY_TEXT[recipientsState.filter]}</span>
          </div>}
        </div>
      </div>
    );
  }
}

export default SequenceRecipientsList;
