import React from 'react';

import {
  Address,
  CommandControlType,
  ThreadBox,
  FilterBox,
  Me,
  Thread,
  List,
  TeamMember,
  HotkeyActions,
  ShareLink,
  IntegrationType,
} from '@src/types';
import { participantsToString } from '@src/lib/address';
import ThreadDetailHeaderItem from './ThreadDetailHeaderItem';
import ListIcon from '@src/components/Icons/Label.svg';
import Close from '@src/components/Icons/Close.svg';

import './ThreadDetailHeader.scss';
import { Link } from 'react-router-dom';
import { SharedThread, SharedThreadPermissionScope } from '@src/types/sharedThreads';

// tslint:disable-next-line:max-line-length
import ShareThreadPopover from '@src/components/ThreadDetail/components/ShareThreadPopover';
import { SharedThreadCreateMeta } from '@src/ducks/sharedThreads';
import { checkNextHotkeyChange, HotkeyAction } from '@src/lib/hotkeys';
import BestowButton from './BestowButton/BestowButton';
import ViewingPill from '../ViewingPill';

export interface Props {
  subject: string;
  participants: Address[];
  onActionClick: (action: ThreadBox | FilterBox) => void;
  commandControlShow: (type: CommandControlType) => void;
  showUnsubscribe: boolean;
  hideActions?: boolean;

  thread?: Thread;
  me?: Me;
  lists?: List[];
  sharedThread?: SharedThread;
}

export interface StateProps {
  sharedTeamMember?: TeamMember;
  sharedMemberPhotoUrl?: string;
  isSharedThreadOwner?: boolean;
  commentsEnabled?: boolean;
  hotkeyActions?: HotkeyActions;
  shareLink?: ShareLink;
  asanaEnabled: boolean;
  slackEnabled: boolean;
  isBlocked?: boolean;
}

export interface DispatchProps {
  createSharedThread: (meta: SharedThreadCreateMeta) => void;
  updateSharedThread: (meta: SharedThread) => void;
  deleteSharedThread: (meta: string) => void;
  setShareLink: (id: string) => void;
}

interface State {
  showSharePopover: boolean;
  showShareLoading: boolean;
  initialIntegrationView?: IntegrationType;
}

class ThreadDetailHeader extends React.PureComponent<Props & StateProps & DispatchProps, State> {
  constructor() {
    super();
    this.state = {
      showSharePopover: false,
      showShareLoading: false,
    };
  }

  componentDidUpdate(oldProps: StateProps) {
    const oldActions = oldProps.hotkeyActions;
    const newActions = this.props.hotkeyActions;
    if (oldActions  && newActions && oldActions !== newActions) {
      this.handleHotkeyActionsChange(oldActions, newActions);
    }
  }

  handleSharePopoverShow = (integration?: IntegrationType) => {
    const { sharedThread, thread } = this.props;
    this.setState({ showSharePopover: true, showShareLoading: true });
    if (!sharedThread && thread) {
      this.props.createSharedThread({
        thread: thread.id,
        permissions: {
          scope: SharedThreadPermissionScope.Public,
        },
      });
    }

    if (integration) {
      this.setState({ initialIntegrationView: integration });
    }
  }

  // Handle Shared Thread deletion
  handleSharedThreadDelete = () => {
    const { sharedThread, deleteSharedThread } = this.props;
    if (!sharedThread) {
      return;
    }

    deleteSharedThread(sharedThread.id);
  }

  handleSharePopoverShown = () => {
    this.setState({
      initialIntegrationView: undefined,
      showShareLoading: false,
    });
  }

  handleHotkeyActionsChange = (
    oldActions: { [action: string]: number },
    newActions: { [action: string]: number },
  ) => {
    // Handle CC toggle
    if (checkNextHotkeyChange(HotkeyAction.CreateShareLink, newActions, oldActions)) {
      this.setState({ showSharePopover: true, showShareLoading: true });
    }

    if (checkNextHotkeyChange(HotkeyAction.ShareToAsana, newActions, oldActions)) {
      this.setState({ showSharePopover: true, showShareLoading: true });
      this.setState({ initialIntegrationView: IntegrationType.Asana });
    }

    if (checkNextHotkeyChange(HotkeyAction.ShareToSlack, newActions, oldActions)) {
      this.setState({ showSharePopover: true, showShareLoading: true });
      this.setState({ initialIntegrationView: IntegrationType.Slack });
    }
  }

  handleSharePopoverDismiss = () => {
    const { setShareLink, sharedThread } = this.props;

    this.setState({ showSharePopover: false, showShareLoading: false }, () => {
      // set back to thread share link instead of message link
      if (sharedThread) {
        setShareLink(sharedThread.id);
      }
    });
  }

  handleSharedThreadUpdate = (meta: SharedThread) => {
    this.props.updateSharedThread(meta);
  }

  render() {
    const {
      sharedThread,
      onActionClick,
      sharedMemberPhotoUrl,
      isSharedThreadOwner,
      sharedTeamMember,
      shareLink,
      asanaEnabled,
      slackEnabled,
      isBlocked,
      thread,
    } = this.props;
    const { showSharePopover, showShareLoading } = this.state;
    const options = [
      ThreadBox.Inbox,
      ThreadBox.Later,
      ThreadBox.Archive,
      ThreadBox.Trash,
      FilterBox.Starred,
      ThreadBox.List,
      FilterBox.Unread,
      'Mute Conversation',
      'Block Sender',
    ];
    const label = this.props.lists && this.props.thread && this.props.thread.list
      ? this.props.lists
        .find(l => !!this.props.thread && l.id === this.props.thread.list)
      : undefined;
    const handleClose = (e: React.SyntheticEvent<HTMLButtonElement>) => {
      e.preventDefault();
      e.stopPropagation();

      onActionClick(ThreadBox.Archive);
    };

    // Labels for block/mute properties
    const threadMuted = this.props.thread && this.props.thread.muted || false;
    const senderBlocked = this.props.isBlocked || false;
    let blockedCopy: string = '';
    if (threadMuted) {
      // tslint:disable-next-line:max-line-length
      blockedCopy = 'ðŸ””This conversation is muted. You will not receive notifications for new messages.';
    }

    if (senderBlocked) {
      blockedCopy = 'ðŸ™…Messages from this sender are blocked and immediately moved to trash';
    }

    if (threadMuted && senderBlocked) {
      blockedCopy = 'This conversation is blocked and muted';
    }

    // Need to add avatar for team member name
    return (
      <div className="thread-detail-header">
        {sharedThread && sharedTeamMember &&
          !isSharedThreadOwner &&
          <div className="shared-thread-heading">
            <div className="shared-label">
              {sharedMemberPhotoUrl &&
                <img className="member-avatar" src={sharedMemberPhotoUrl} />
              }
              <span className="name-label">
                {`${sharedTeamMember.firstName} ${sharedTeamMember.lastName} `}
              </span> shared
            </div>
            <div className="viewing-wrapper"><ViewingPill /></div>
          </div>
        }
        {!this.props.hideActions && <div className="action-buttons">
          {options.map(option =>
            <ThreadDetailHeaderItem
              key={option}
              box={option}
              onClick={onActionClick}
              thread={thread}
              isBlocked={isBlocked}
            />,
          )}
          <div className="viewing-wrapper">
            <ViewingPill />
          </div>
          <BestowButton
            onShareClick={this.handleSharePopoverShow}
            loading={showShareLoading}
            asanaEnabled={asanaEnabled}
            slackEnabled={slackEnabled}
            isShared={!!sharedThread}
          />
          {showSharePopover && sharedThread &&
            <ShareThreadPopover
              sharedThread={sharedThread}
              onDismiss={this.handleSharePopoverDismiss}
              onSharedThreadUpdate={this.handleSharedThreadUpdate}
              shareLink={shareLink}
              asanaEnabled={asanaEnabled}
              initialIntegrationView={this.state.initialIntegrationView}
              onShow={this.handleSharePopoverShown}
              deleteSharedThread={this.handleSharedThreadDelete}
            />
          }
        </div>}
        {(threadMuted || senderBlocked) &&
          <div className="blocked-label">
            {blockedCopy}
          </div>
        }
        {label && this.props.thread && <div className="labels">
          <div className="icon">
            <ListIcon />
          </div>
          <Link className="label" to={`/mail/all/lists/${label.id}/${this.props.thread.id}`}>
            {label.name}
            <button className="close" onClick={handleClose}>
              <Close width="6px" height="6px" />
            </button>
          </Link>
        </div>}
        <div className="subject">
          {this.props.subject}
        </div>
        <div className="participants">
          {participantsToString(this.props.participants, this.props.me)}
        </div>
      </div>
    );
  }
}

export default ThreadDetailHeader;
