import React from 'react';
import moment from 'moment-timezone';
import ClipboardJS from 'clipboard';
import {
  Calendar as CalendarType,
  Features,
  Me,
  Action,
  Account,
  AccountType,
  Integration,
  IntegrationStatus,
} from '@src/types';
import { ConferenceType, Scheduling, TimeWindow } from '@src/types/scheduling';
import ToggleCell from '@src/routes/Settings/components/ToggleCell';
import DropdownCell from '@src/routes/Settings/components/DropdownCell';

import './ManageSchedulingDetail.scss';
import SettingTextInput from '@src/components/Form/SettingTextInput';
import Checkbox from '@src/components/Form/Checkbox/Checkbox';
import Dropdown from '@src/components/Form/Dropdown';
import { cloneDeep, flatten, values, findIndex, find } from 'lodash';
import { SchedulingCreateMeta } from '@src/ducks/scheduling';
import Confirm, { Props as ConfirmProps } from '@src/components/Base/Confirm';
import ToolTip from '@src/components/Base/Tooltip';
import UpgradePrompt from '@src/components/Base/UpgradePrompt/UpgradePrompt';
import browserHistory from '@src/lib/browserHistory';
import { OnboardingPatchMeta } from '@src/ducks/me';
import NavigationHeader from '@src/routes/Settings/components/NavigationHeader';
import { conferenceTypeDisplay } from '@src/lib/scheduling';
import { AmplitudeEvents, logAmplitudeEvent } from '@src/lib/amplitude-analytics';

export interface Props {
  scheduling?: Scheduling;
  calendars: { [account: string]: CalendarType[] };
  userSlug: string;
  features: Features;
  accounts: Account[];
  confirmProps?: ConfirmProps;
  schedulingUrl?: string;
  me?: Me;
  schedulings?: Scheduling[];
  isNew?: boolean;
  zoomIntegration?: Integration;
  writableCalendars: {
    [account: string]: CalendarType[];
  };
}

export interface DispatchProps {
  createScheduling: (meta: SchedulingCreateMeta) => void;
  updateScheduling: (scheduling: Scheduling) => void;
  listCalendars: (account?: string) => void;
  listScheduling: () => void;
  dispatchAction: (action: Action<any, any>) => void;
  updateOnboarding: (meta: OnboardingPatchMeta) => void;
  deleteScheduling: (meta: Scheduling) => void;
}

interface State {
  calendarEnabled: boolean;
  showBranding: boolean;
  eventName: string;
  eventLocation: string;
  calendarUrl: string;
  calendarDaysOfWeek: {
    [day: number]: boolean;
  };
  calendarDoubleBookChecks: {
    [id: string]: boolean;
  };
  minimumNotice: number;
  durationMinutes: number;
  bufferDuration: number;
  timeValues: moment.Moment[];
  selectedTimeZone: string;
  conferenceType: ConferenceType;

  // Scheduling Values
  afterSecondsSinceMidnight: number;
  beforeSecondsSinceMidnight: number;
  showSave: boolean;
  selectedCalendar: string;

  // UI States
  showCopiedToolTip?: boolean;
  confirmProps?: ConfirmProps;

  // Upgrade Prompts
  showSchedulingUpgrade?: boolean;
  showBrandingUpgrade?: boolean;
  showDoubleBookingUpgrade?: boolean;

}

// TODO(SHIN): Handle redirecting zoom if integration is not installed
// TODO(SHIN): Handle showing Zoom for all calendar cases
class ManageSchedulingDetail extends React.PureComponent<
  Props & DispatchProps, State
> {
  ref: HTMLDivElement;

  clipboard = new ClipboardJS('#copy-link');
  WEEKDAYS = [
    { name: 'Mon', index: 1 },
    { name: 'Tue', index: 2 },
    { name: 'Wed', index: 3 },
    { name: 'Thu', index: 4 },
    { name: 'Fri', index: 5 },
    { name: 'Sat', index: 6 },
    { name: 'Sun', index: 0 },
  ];

  DURATION_MINUTES = [
    15,
    20,
    30,
    45,
    60,
  ];

  DURATION_PADDING_MINUTES = [
    0,
    5,
    10,
    15,
    30,
    45,
    60,
  ];

  MINIMUM_NOTICE_MINUTES = [
    0,
    120,
    240,
    480,
    720,
    1440,
  ];

  CONFERENCE_OPTIONS = [
    ConferenceType.None,
    ConferenceType.Google,
    ConferenceType.Zoom,
  ];

  DEFAULT_TIME_WINDOWS: TimeWindow[] = [
    {
      dayOfWeek: 1,
      afterSecondsSinceMidnight: 32400,
      beforeSecondsSinceMidnight: 61200,
    },
    {
      dayOfWeek: 2,
      afterSecondsSinceMidnight: 32400,
      beforeSecondsSinceMidnight: 61200,
    },
    {
      dayOfWeek: 3,
      afterSecondsSinceMidnight: 32400,
      beforeSecondsSinceMidnight: 61200,
    },
    {
      dayOfWeek: 4,
      afterSecondsSinceMidnight: 32400,
      beforeSecondsSinceMidnight: 61200,
    },
    {
      dayOfWeek: 5,
      afterSecondsSinceMidnight: 32400,
      beforeSecondsSinceMidnight: 61200,
    },
  ];

  TIMEZONES = moment.tz.names()
    .filter(tz => !tz.toLowerCase().startsWith('etc'));

  constructor(props: Props & DispatchProps) {
    super();
    const { scheduling, isNew, features } = props;
    const schedulingCount = values(props.schedulings).length;
    const schedulingAllowed = features.scheduling;
    const calendarDoubleBooks = scheduling && scheduling.blockingCalendars &&
        scheduling.blockingCalendars.reduce(
        (a, b) => {
          a[b] = true;
          return a;
        },
        {},
      ) || {};

    const calendarEnabled = !!schedulingCount && scheduling && !scheduling.disabled || false;

     // Need to default for 0 in the case where the property does not exist
    const defaultMinimumNotice = isNew ? 480 : 0;

    this.state = {
      calendarEnabled: schedulingAllowed && (calendarEnabled || (isNew || false)),
      showBranding: scheduling && !scheduling.hideBranding !== undefined ?
        !scheduling.hideBranding : true,
      eventName: scheduling && scheduling.name || 'New Meeting Times',
      eventLocation: scheduling && scheduling.location || '',
      calendarUrl: scheduling && scheduling.slug || '',
      calendarDaysOfWeek: this.calendarDaysOfWeek(
        scheduling && scheduling.timeWindows || this.DEFAULT_TIME_WINDOWS,
      ),
      timeValues: this.getTimeValues(),
      durationMinutes: scheduling && scheduling.durationMinutes || 30,
      bufferDuration: scheduling && scheduling.paddingMinutes || 0,
      afterSecondsSinceMidnight: scheduling && scheduling.timeWindows[0] &&
        scheduling.timeWindows[0].afterSecondsSinceMidnight || 32400,
      beforeSecondsSinceMidnight: scheduling && scheduling.timeWindows[0] &&
        scheduling.timeWindows[0].beforeSecondsSinceMidnight || 61200,
      showSave: false,
      selectedTimeZone: scheduling && scheduling.timeZone || moment.tz.guess(),
      calendarDoubleBookChecks: calendarDoubleBooks,
      selectedCalendar: scheduling && scheduling.calendar || '',
      minimumNotice: scheduling && scheduling.minimumNotice || defaultMinimumNotice,
      conferenceType: scheduling && scheduling.conferenceType || ConferenceType.None,
    };
  }

  componentDidMount() {
    const {
      listCalendars,
    } = this.props;

    // Always fetch calendar on mount
    listCalendars();

    if (this.ref && document.activeElement === document.body) {
      this.ref.focus();
    }
  }

  componentDidUpdate(oldProps: Props) {
    const { scheduling } = this.props;

    // Reset state if scheduling model has not been set on component yet
    if (scheduling && scheduling !== oldProps.scheduling) {
      const calendarDoubleBooks = scheduling && scheduling.blockingCalendars &&
        scheduling.blockingCalendars.reduce(
          (a, b) => {
            a[b] = true;
            return a;
          },
          {},
        ) || {};

      this.setState({
        calendarEnabled: !scheduling.disabled,
        showBranding: !scheduling.hideBranding,
        eventName: scheduling.name || '',
        eventLocation: scheduling.location || '',
        calendarUrl: scheduling.slug || '',
        calendarDaysOfWeek: this.calendarDaysOfWeek(scheduling.timeWindows),
        timeValues: this.getTimeValues(),
        durationMinutes: scheduling.durationMinutes || 30,
        bufferDuration: scheduling.paddingMinutes,
        afterSecondsSinceMidnight: scheduling.timeWindows[0] &&
          scheduling.timeWindows[0].afterSecondsSinceMidnight || 32400,
        beforeSecondsSinceMidnight: scheduling.timeWindows[0] &&
          scheduling.timeWindows[0].beforeSecondsSinceMidnight || 61200,
        showSave: false,
        selectedTimeZone: scheduling.timeZone || moment.tz.guess(),
        calendarDoubleBookChecks: calendarDoubleBooks,
        selectedCalendar: scheduling.calendar || '',
        conferenceType: scheduling.conferenceType,
      });
    }

    if (!scheduling && scheduling !== oldProps.scheduling) {
      this.setState({
        calendarEnabled: true,
        showBranding: true,
        eventName: 'New Meeting Times',
        eventLocation: '',
        calendarUrl: '',
        calendarDaysOfWeek: this.calendarDaysOfWeek(this.DEFAULT_TIME_WINDOWS),
        timeValues: this.getTimeValues(),
        durationMinutes: 30,
        bufferDuration: 10,
        afterSecondsSinceMidnight: 32400,
        beforeSecondsSinceMidnight: 61200,
        showSave: false,
        selectedTimeZone: moment.tz.guess(),
        calendarDoubleBookChecks: {},
        selectedCalendar: '',
        conferenceType: ConferenceType.None,
      });
    }
  }

  handleRef = (ref: HTMLDivElement) => {
    this.ref = ref;
  }

  calendarDaysOfWeek(windows: TimeWindow[]) {
    const days: {
      [day: number]: boolean;
    }  = {
      0: false,
      1: false,
      2: false,
      3: false,
      4: false,
      5: false,
      6: false,
    };

    windows.forEach((win) => {
      days[win.dayOfWeek] = true;
    });

    return days;
  }

  getTimeValues = () => {
    const start = moment().startOf('day');
    const timeValues = [];

    while (start.isBefore(moment().add(1, 'day').startOf('day'))) {
      timeValues.push(start.clone());
      start.add(1, 'hour');
    }

    return timeValues;
  }

  handleCalendarToggle = () => {
    const { features } = this.props;

    // Show Upgrade prompt if feature is not set
    if (!features.scheduling) {
      this.showSchedulingUpgrade();
      return;
    }

    this.setState({ calendarEnabled: !this.state.calendarEnabled, showSave: true });
  }

  handleBrandingToggle = () => {
    const { features } = this.props;

    // Show Upgrade prompt if feature not enabled
    if (!features.schedulingNoBranding) {
      this.showBrandingUpgrade();
      return;
    }

    this.setState({
      showBranding: !this.state.showBranding,
      showSave: true,
    });
  }

  handleDurationSelect = (value: string, index: number) => {
    const minutes = this.DURATION_MINUTES[index];
    this.setState({ durationMinutes: minutes, showSave: true });
  }

  handleBufferDurationSelect = (value: string, index: number) => {
    const minutes = this.DURATION_PADDING_MINUTES[index];
    this.setState({ bufferDuration: minutes, showSave: true });
  }

  handleMinimumNoticeSelect = (value: string, index: number) => {
    const minutes = this.MINIMUM_NOTICE_MINUTES[index];
    this.setState({ minimumNotice: minutes, showSave: true });
  }

  handleEventNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    this.setState({ eventName: e.currentTarget.value, showSave: true });
  }

  handleCalendarUrlChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    this.setState({ calendarUrl: e.currentTarget.value, showSave: true });
  }

  handleEventLocationChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    this.setState({ eventLocation: e.currentTarget.value, showSave: true });
  }

  handleCalendarDaysChange = (e: React.SyntheticEvent<HTMLInputElement>) => {
    const dayIndex = parseInt(e.currentTarget.name, 10);
    const currentDays = { ...this.state.calendarDaysOfWeek };
    currentDays[dayIndex] = !currentDays[dayIndex];
    this.setState({
      calendarDaysOfWeek: currentDays,
      showSave: true,
    });
  }

  handleDoubleBookChange = (e: React.SyntheticEvent<HTMLInputElement>) => {
    // Show upgrade prompt if user does not have schedule double booking teamFeatures enabled
    const { features } = this.props;
    if (!features.schedulingDoubleBooking) {
      this.showDoubleBookingUpgrade();
      e.stopPropagation();
      e.preventDefault();
      return;
    }

    const calendarId = e.currentTarget.name;
    const doubleBookChecks = { ...this.state.calendarDoubleBookChecks  };
    doubleBookChecks[calendarId] = !doubleBookChecks[calendarId];
    this.setState({
      calendarDoubleBookChecks: doubleBookChecks,
      showSave: true,
    });
  }

  // TODO(SHIN): Handle double booking selection (include if on team, select single if on pro)
  handleCalendarSelect = (option: string, index: number) => {
    const { writableCalendars, features, accounts } = this.props;
    const { conferenceType } = this.state;
    const allCalendars = flatten(values(writableCalendars));
    const selectedCalendarId = allCalendars[index] && allCalendars[index].id || '';
    const doubleBookChecks = { ...this.state.calendarDoubleBookChecks };

    // Reset double booking calendars if feature does not allow for multiple double booking
    if (!features.schedulingDoubleBooking) {
      Object.keys(doubleBookChecks).forEach((key) => {
        doubleBookChecks[key] = key === selectedCalendarId;
      });
      doubleBookChecks[selectedCalendarId] = true;
      this.setState({
        selectedCalendar: selectedCalendarId,
        calendarDoubleBookChecks: doubleBookChecks,
        showSave: true,
      });
      return;
    }

    // Unsets Google Meet Conference type if selected calendar is not Google.
    let selectedConferenceType = conferenceType;
    const calendarAccountId = allCalendars[index] && allCalendars[index].account;
    const calendarAccount = accounts.filter(a => a.id === calendarAccountId)[0];
    if (calendarAccount && calendarAccount.type !== AccountType.Google) {
      if (selectedConferenceType === ConferenceType.Google) {
        selectedConferenceType = ConferenceType.None;
      }
    }

    // Set selected calendar id
    doubleBookChecks[selectedCalendarId] = true;
    this.setState({
      calendarDoubleBookChecks: doubleBookChecks,
      selectedCalendar: selectedCalendarId,
      conferenceType: selectedConferenceType,
      showSave: true,
    });
  }

  // Handle Conference Select chooses a conference option
  handleConferenceSelect = (option: string, index: number) => {
    const { zoomIntegration } = this.props;
    const conferenceType = this.getConferenceOptions()[index];

    // Navigate to integration page if integration is not valid
    if (conferenceType === ConferenceType.Zoom &&
      (!zoomIntegration || zoomIntegration.status !== IntegrationStatus.Ok)) {

      // Show confirm prompt with redirect in order to enable zoom integration
      const blockProps: ConfirmProps = {
        title: 'Enable Zoom Integration',
        message: 'You must connect your Zoom account in order to create conference urls for your scheduling pages. Continue to the integrations page?',
        confirmCopy: 'Continue',
        cancelCopy: 'Cancel',
        onConfirm: () => {
          this.setState({ confirmProps: undefined }, () => {
            this.handleZoomRedirect();
          });
        },
        onCancel: () => this.setState({ confirmProps: undefined }),
      };

      this.setState({
        confirmProps: blockProps,
      });
      return;
    }

    // Otherwise set zoom integration
    this.setState({ conferenceType, showSave: true });
  }

  handleTimeZoneSelect = (option: string, index: number) => {
    this.setState({ selectedTimeZone: this.TIMEZONES[index], showSave: true });
  }

  // Calculates new upper bound for time zone
  handleUpperBoundSelect = (value: string, index: number) => {
    const { timeValues } = this.state;
    const time = timeValues[index];

    const newBefore = time.diff(moment().startOf('day'), 'seconds');
    let afterSecondsSinceMidnight = this.state.afterSecondsSinceMidnight;

    if (newBefore < afterSecondsSinceMidnight) {
      afterSecondsSinceMidnight = 0;
    }

    this.setState({
      beforeSecondsSinceMidnight: newBefore,
      afterSecondsSinceMidnight,
      showSave: true,
    });
  }

  handleLowerBoundSelect = (value: string, index: number) => {
    const { timeValues } = this.state;
    const time = timeValues[index];
    const newAfter = time.diff(moment().startOf('day'), 'seconds');
    let beforeSecondsSinceMidnight = this.state.beforeSecondsSinceMidnight;

    if (newAfter > beforeSecondsSinceMidnight) {
      beforeSecondsSinceMidnight = (60 * 60 * 24) - 1;
    }
    this.setState({
      afterSecondsSinceMidnight: newAfter,
      beforeSecondsSinceMidnight,
      showSave: true,
    });
  }

  handleSave = () => {
    const { updateScheduling, createScheduling } = this.props;
    const existing = cloneDeep(this.props.scheduling);

    // Calculate blocking calendars
    const blockingCalendars: string[] = [];
    Object.keys(this.state.calendarDoubleBookChecks).forEach((key) => {
      if (this.state.calendarDoubleBookChecks[key]) {
        blockingCalendars.push(key);
      }
    });

    // Converts time windows
    const timeWindows: TimeWindow[] = this.WEEKDAYS
      .filter(d => this.state.calendarDaysOfWeek[d.index])
      .map(d => ({
        dayOfWeek: d.index,
        afterSecondsSinceMidnight: this.state.afterSecondsSinceMidnight,
        beforeSecondsSinceMidnight: this.state.beforeSecondsSinceMidnight,
      }));

    // Handle creation if scheduling model does not exist
    if (!existing) {
      const createMeta: SchedulingCreateMeta = {
        name: this.state.eventName,
        disabled: !this.state.calendarEnabled,
        eventName: this.state.eventName,
        location: this.state.eventLocation,
        durationMinutes: this.state.durationMinutes,
        timeZone: this.state.selectedTimeZone,
        calendar: this.state.selectedCalendar,
        slug: this.state.calendarUrl,
        paddingMinutes: this.state.bufferDuration,
        minimumNotice: this.state.minimumNotice,
        conferenceType: this.state.conferenceType,
        blockingCalendars,
        timeWindows,
      };

      createScheduling(createMeta);
      this.setState({ showSave: false });
      return;
    }

    const updateMeta: Scheduling = {
      ...existing,
      disabled: !this.state.calendarEnabled,
      name: this.state.eventName,
      eventName: this.state.eventName,
      location: this.state.eventLocation,
      durationMinutes: this.state.durationMinutes,
      timeZone: this.state.selectedTimeZone,
      calendar: this.state.selectedCalendar,
      slug: this.state.calendarUrl,
      hideBranding: !this.state.showBranding,
      paddingMinutes: this.state.bufferDuration,
      minimumNotice: this.state.minimumNotice,
      conferenceType: this.state.conferenceType,
      blockingCalendars,
      timeWindows,
    };

    updateScheduling(updateMeta);
    this.setState({ showSave: false });
  }

  handleCopy = (e: React.MouseEvent<HTMLButtonElement>) => {
    this.setState({ showCopiedToolTip: true });
  }

  dismissToolTip = () => {
    this.setState({ showCopiedToolTip: undefined });
  }

  getConferenceOptions = () => {
    const { calendars, accounts } = this.props;
    const { selectedCalendar } = this.state;

    // Return nothing if no calendar is selected
    if (selectedCalendar === '') {
      return [];
    }

    const allCalendars = flatten(values(calendars));
    const calendarAccountId = allCalendars.filter(c => c.id === selectedCalendar)[0]?.account;
    const calendarAccountIndex = findIndex(accounts, { id: calendarAccountId });

    // Return nothing if cannot match account
    if (calendarAccountIndex === -1) {
      return [];
    }

    if (calendarAccountIndex > -1) {
      const calendarAccountType = accounts[calendarAccountIndex]?.type;

      // Filter out Google Meet if calendar type is not google
      if (calendarAccountType !== AccountType.Google) {
        return this.CONFERENCE_OPTIONS.filter(option => option !== ConferenceType.Google);
      }
    }

    // Otherwise return all options
    return this.CONFERENCE_OPTIONS;
  }

  handleConfirm = () => {
    const { scheduling } = this.props;
    const oldCalendarUrl = scheduling && scheduling.slug;
    const newCalendarUrl = this.state.calendarUrl;
    const slugChanged = oldCalendarUrl && newCalendarUrl !== oldCalendarUrl;
    const calendarToggledOff = !this.state.calendarEnabled && scheduling && !scheduling.disabled;

    // Block any saves if url, name or calendar has not been selected
    if (!this.state.calendarUrl || !this.state.eventName || !this.state.selectedCalendar) {
      let message = 'Please fill out missing information';
      if (!this.state.calendarUrl) {
        message = 'Complete the scheduling url and try again.';
      }

      if (!this.state.eventName) {
        message = 'Fill out the calendar event name and try again.';
      }

      if (!this.state.selectedCalendar) {
        message = 'Please select the calendar we should schedule meetings in and try again.';
      }
      const blockProps: ConfirmProps = {
        title: 'Please fill out missing information before saving',
        message,
        confirmCopy: 'Ok',
        onConfirm: () => this.setState({ confirmProps: undefined }),
      };
      this.setState({ confirmProps: blockProps });
      return;
    }

    // Callback when confirmed ok
    const saveCallback = () => {
      this.setState({ confirmProps: undefined });
      this.handleSave();
      logAmplitudeEvent(AmplitudeEvents.CreateNewCalendarMeetingTime);
    };

    // If no breaking changes allow immediate save
    if (!slugChanged && !calendarToggledOff) {
      logAmplitudeEvent(AmplitudeEvents.CreateNewCalendarMeetingTime);
      this.handleSave();
      return;
    }

    // Otherwise show confirm dialogue before saving
    const confirmProps: ConfirmProps = {
      title: 'Are you sure you want to make these changes?',
      message: 'Any scheduling links you have sent will not be accessible anymore.',
      confirmCopy: 'Ok',
      cancelCopy: 'Cancel',
      onConfirm: () => saveCallback(),
      onCancel: () => this.setState({ confirmProps: undefined }),
    };

    this.setState({ confirmProps });
  }

  // Upgrade Prompts
  showBrandingUpgrade = () => {
    this.setState({ showBrandingUpgrade: true });
  }

  hideBrandingUpgrade = () => {
    this.setState({ showBrandingUpgrade: undefined });
  }

  showSchedulingUpgrade = () => {
    this.setState({ showSchedulingUpgrade: true });
  }

  hideSchedulingUpgrade = () => {
    this.setState({ showSchedulingUpgrade: undefined });
  }

  showDoubleBookingUpgrade = () => {
    const { schedulingDoubleBooking } = this.props.features;
    if (schedulingDoubleBooking) {
      return;
    }

    this.setState({ showDoubleBookingUpgrade: true });
  }

  hideDoubleBookingUpgrade = () => {
    this.setState({ showDoubleBookingUpgrade: undefined });
  }

  handleOpenSchedulingUrl = () => {
    const { scheduling, me } = this.props;
    if (scheduling && me) {
      const url = `${process.env.SCHEDULING_URL}/${me.slug}/${scheduling.slug}`;
      window.open(url, '_blank');
    }
  }

  handleDelete = () => {
    const { scheduling, deleteScheduling } = this.props;

    const message = 'Any links pointing to this scheduling page will no longer work. Are you sure you want to continue?';
    const deleteProps: ConfirmProps = {
      title: 'Delete this meeting times availability?',
      message,
      confirmCopy: 'Delete',
      onConfirm: () => {
        // Delete and redirect
        if (scheduling) {
          deleteScheduling(scheduling);
          browserHistory.push('/calendar');
        }
        this.setState({ confirmProps: undefined });
      },
      cancelCopy: 'Cancel',
      onCancel: () => {
        this.setState({ confirmProps: undefined });
      },
    };
    this.setState({ confirmProps: deleteProps });
  }

  handleZoomRedirect = () => {
    browserHistory.push('/settings/integrations/zoom');
  }

  // TODO(SHIN): Prevent toggling of double booking if not on Teams+
  render() {
    const {
      calendars,
      userSlug,
      features,
      scheduling,
      zoomIntegration,
      accounts,
      writableCalendars,
    } = this.props;
    const {
      timeValues, selectedCalendar,
      calendarUrl, confirmProps,
      showSave, conferenceType,
     } = this.state;

    const allCalendars = flatten(values(calendars));
    const allWritableCalendars = flatten(values(writableCalendars));
    const calendarIndex = allWritableCalendars.findIndex(cal => cal.id === selectedCalendar);

    const { afterSecondsSinceMidnight, beforeSecondsSinceMidnight } = this.state;
    const lowerIndex = timeValues.findIndex(
      t => t.diff(moment().startOf('day'), 'seconds') === afterSecondsSinceMidnight,
    );
    const upperIndex = timeValues.findIndex(
      t =>
        t.diff(moment().startOf('day'), 'seconds') ===
        (beforeSecondsSinceMidnight) % (60 * 60 * 24),
    );

    const timezones = this.TIMEZONES;
    const timezoneIndex = this.TIMEZONES.indexOf(this.state.selectedTimeZone);
    const durationIndex = this.DURATION_MINUTES.indexOf(this.state.durationMinutes);
    const durationPaddingIndex = this.DURATION_PADDING_MINUTES.indexOf(this.state.bufferDuration);
    const minimumNoticeIndex = this.MINIMUM_NOTICE_MINUTES.indexOf(this.state.minimumNotice);
    const conferenceIndex = this.getConferenceOptions().indexOf(this.state.conferenceType);

    // Navigate to integration page if integration is not valid
    const zoomError = conferenceType === ConferenceType.Zoom && (
      !zoomIntegration || zoomIntegration.status !== IntegrationStatus.Ok
    );

    const reducedCalendars: { [key: string]: CalendarType[] } = allCalendars.reduce(
      (dict: { [key: string]: CalendarType[] }, cal: CalendarType) => {

        if (cal.readOnly) {
          const readOnlyVals = dict.readOnly || [];
          dict.readOnly = [...readOnlyVals, cal];
          return dict;
        }
        const existingVals = dict[cal.account] || [];
        dict[cal.account] = [...existingVals, cal];
        return dict;
      },
      {},
    );

    const accountOrder: { [key: string]: number } = {};
    accounts.forEach((a, n) => {
      accountOrder[a.id] = n + 1;
    });

    // TODO(SHIN): Disable Checkboxes for Double Double Booking when feature bool not present
    return (
      <div
        className="manage-scheduling-detail"
        tabIndex={-1}
        ref={this.handleRef}
      >
        <NavigationHeader
          key={scheduling?.id}
          title={this.state.eventName || 'New Meeting Times'}
          onSave={showSave && this.handleConfirm || undefined}
          contextButton={!showSave && !!scheduling && !scheduling?.disabled &&
            (<button
              className="view-calendar-link"
              onClick={this.handleOpenSchedulingUrl}
            >
              View Polymail calendar page 
            </button>
          ) || undefined
          }
        />
        <div className="calendar-container">
          <div className="preferences-calendar">
            <div className="calendar-main">
              {confirmProps &&
                <Confirm {...confirmProps} />
              }
              <div
                className="enable-section"
                onMouseLeave={this.hideSchedulingUpgrade}
              >
                {this.state.showSchedulingUpgrade &&
                  <UpgradePrompt
                    title="Polymail Scheduling"
                    description={'Upgrade your Polymail Subscription to '
                      + 'get access to Calendar Scheduling!'}
                  />
                }
                <ToggleCell
                  title="Enable this Polymail calendar page"
                  subtitle="Allows people to book meeting on your public Polymail calendar page."
                  subtitleLink={
                    <a
                      href="https://help.polymail.io/features/calendar-scheduling"
                      target="_blank"
                    >
                      {' Learn More.'}
                    </a>
                  }
                  onToggle={this.handleCalendarToggle}
                  on={this.state.calendarEnabled}
                />
              </div>
              {this.state.calendarEnabled &&
                <div className="all-options">
                  {/* Information Header */}
                  <div className="calendar-section-header">
                    MEETING INFORMATION
                  </div>
                  <div className="calendar-link">
                    <SettingTextInput
                      title="Your Polymail calendar link"
                      description={'Share this link with invitees '
                        + 'to allow them to access and book meetings to your calendar'}
                      prefix={`${process.env.SCHEDULING_URL}/${userSlug}/`}
                      placeholder="(e.g. your-url)"
                      name="calendarUrl"
                      value={calendarUrl}
                      onChange={this.handleCalendarUrlChange}
                      type="input"
                    />
                    <div className="copy-area">
                      <button
                        // tslint:disable-next-line:max-line-length
                        data-clipboard-text={`${process.env.SCHEDULING_URL}/${userSlug}/${calendarUrl}`}
                        id="copy-link"
                        onClick={this.handleCopy}
                        className="copy-link"
                        onMouseLeave={this.dismissToolTip}
                      >
                        Copy Url
                      </button>
                      {this.state.showCopiedToolTip &&
                        <ToolTip
                          className="tooltip"
                          top="34px"
                          left="14px"
                          copy="Copied!"
                        />
                      }
                    </div>
                  </div>
                  <SettingTextInput
                    className="event-name"
                    title="Event Name"
                    description="Name of the event that invitees can book on your Polymail calendar"
                    placeholder="30 min meeting"
                    name="eventName"
                    value={this.state.eventName}
                    onChange={this.handleEventNameChange}
                    type="input"
                  />
                  <SettingTextInput
                    className="location"
                    title="Event Location"
                    // tslint:disable-next-line:max-line-length
                    description="Location of the event that invitees can book on your Polymail calendar"
                    placeholder="e.g. Google Hangouts, Zoom Conference"
                    name="eventLocation"
                    value={this.state.eventLocation}
                    onChange={this.handleEventLocationChange}
                    type="input"
                  />
                  {/* Time Windows Header */}
                  <div className="calendar-section-header">
                    TIME WINDOWS
                  </div>
                  <DropdownCell
                    className="duration"
                    values={this.DURATION_MINUTES
                      .map(d => `${d} minutes`)
                    }
                    onSelect={this.handleDurationSelect}
                    title="Event duration"
                    subtitle="Events booked through Polymail will be for this duration"
                    dropdownId="dockCount"
                    startIndex={durationIndex}
                  />
                  <div className="time-window">
                    <div className="title">
                      Available Meeting times
                  </div>
                    <div className="subtitle">
                      Only allow invitees to book meetings within these times
                  </div>
                    <div className="weekdays">
                      {this.WEEKDAYS.map(d => (
                        <div className="day" key={d.index}>
                          <Checkbox
                            inputName={`${d.index}`}
                            checked={this.state.calendarDaysOfWeek[d.index]}
                            onChange={this.handleCalendarDaysChange}
                          />
                          <span className="label">
                            {d.name}
                          </span>
                        </div>
                      ))}
                    </div>
                    <div className="times">
                      <Dropdown
                        values={timeValues.map(m => m.format('h:mm a'))}
                        onSelect={this.handleLowerBoundSelect}
                        startIndex={lowerIndex}
                      />
                      <div className="to-separator">
                        to
                    </div>
                      <Dropdown
                        values={timeValues.map(m => m.format('h:mm a'))}
                        onSelect={this.handleUpperBoundSelect}
                        startIndex={upperIndex}
                      />
                      <Dropdown
                        values={timezones}
                        onSelect={this.handleTimeZoneSelect}
                        startIndex={timezoneIndex}
                        readOnly={false}
                        typeahead
                      />
                    </div>
                  </div>
                  {/* Information Header */}
                  <div className="calendar-section-header">
                    CALENDARS
                  </div>
                  <DropdownCell
                    values={allWritableCalendars
                      .map(calendar => calendar.name)}
                    onSelect={this.handleCalendarSelect}
                    title="Add new events to calendar"
                    subtitle="Meetings booked through Polymail will be added to this calendar"
                    dropdownId="dockCount"
                    startIndex={calendarIndex}
                    readOnly={false}
                    typeahead
                  />
                  <DropdownCell
                    className="calendar-conference-dropdown"
                    values={this.getConferenceOptions().map(t => conferenceTypeDisplay(t))}
                    onSelect={this.handleConferenceSelect}
                    title="Add conference link to meetings"
                    subtitle="We can set up a meeting url for you through your calendar provider"
                    dropdownId="conference"
                    startIndex={conferenceIndex}
                    error={!!zoomError && 'Please authorize your Zoom integration to enable this feature.' || undefined }
                    errorHandler={!!zoomError && this.handleZoomRedirect || undefined}
                    errorCopy={'Fix Zoom'}
                  />
                  <div
                    onMouseLeave={this.hideDoubleBookingUpgrade}
                    className="double-booking-section"
                  >
                    <div className="title">
                      Prevent double bookings
                    </div>
                    <div className="subtitle">
                      {'Check these calendars for conflicts before ' +
                        'making time slots available to invitees'}
                    </div>
                    {this.state.showDoubleBookingUpgrade &&
                      <UpgradePrompt
                        title="Prevent Double Booking"
                        description={'To prevent double bookings against multiple calendars, ' +
                          'upgrade to Teams or Enterprise'
                        }
                      />
                    }
                  </div>
                  <div className="calendar-section">
                    {Object.keys(reducedCalendars)
                    .sort((a, b) => {
                      const lastItem = accounts.length + 1;
                      return (accountOrder[a] || lastItem) - (accountOrder[b] || lastItem);
                    })
                    .map(k => (
                      <div
                        className="calendar-section-cell"
                        key={`section-cell-${k}`}
                      >
                        <div className="section-header">
                          {find(accounts, { id: k })?.description || 'Read-only Calendars'}
                        </div>
                        <div className="section-cells">
                          {reducedCalendars[k].map(calendar => (
                            <div
                              className="calendar-cell"
                              onMouseOver={this.showDoubleBookingUpgrade}
                              key={calendar.id}
                            >
                              <Checkbox
                                inputName={`${calendar.id}`}
                                checked={this.state.calendarDoubleBookChecks[calendar.id]}
                                onChange={this.handleDoubleBookChange}
                                disabled={!features.schedulingDoubleBooking}
                              />
                              <span className="label">
                                {calendar.name}
                              </span>
                            </div>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                  {/* Information Header */}
                  <div className="calendar-section-header">
                    ADVANCED
                  </div>
                  <DropdownCell
                    className="duration"
                    values={this.DURATION_PADDING_MINUTES
                      .map(d => `${d} minutes`)
                    }
                    onSelect={this.handleBufferDurationSelect}
                    title="Buffer window"
                    subtitle="Make sure that there is at least this amount of time available between existing meetings"
                    dropdownId="bufferDuration"
                    startIndex={durationPaddingIndex}
                  />
                  <DropdownCell
                    className="duration"
                    values={this.MINIMUM_NOTICE_MINUTES
                      .map(d => `${d / 60} hours`)
                    }
                    onSelect={this.handleMinimumNoticeSelect}
                    title="Prevent Last Minute Bookings"
                    subtitle="Set the minimum amount of time before a meeting can be scheduled."
                    dropdownId="minimumNotice"
                    startIndex={minimumNoticeIndex}
                  />
                  <div
                    className="branding-section"
                    onMouseLeave={this.hideBrandingUpgrade}
                  >
                    <ToggleCell
                      title="Show Polymail branding on my calendar page"
                      subtitle="Displays Polymail's logo and branding on your calendar page"
                      onToggle={this.handleBrandingToggle}
                      on={this.state.showBranding}
                    />
                    {this.state.showBrandingUpgrade &&
                      <UpgradePrompt
                        title="Polymail Branding"
                        description={'Upgrade to Teams or Enterprise'
                          + ' to remove Polymail branding from your calendar page'}
                      />
                    }
                  </div>
                  {!!scheduling && (
                      <div className="delete-section">
                        <button
                          className="delete-button"
                          onClick={this.handleDelete}
                        >
                          Delete this Availability
                        </button>
                      </div>
                    )}
                </div>
              }
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ManageSchedulingDetail;
