import React from 'react';
import ReactDOM from 'react-dom';
import Mentions from '@src/components/Icons/Mentions.svg';
// import Plus from '@src/components/Icons/Plus.svg';
// import Template from '@src/components/Icons/Template.svg';
// import Calendar from '@src/components/Icons/Calendar.svg';
// import Unsubscribe from '@src/components/Icons/Unsubscribe.svg';
// import Share from '@src/components/Icons/Share.svg';
import { Team, OnboardingType, Features, Onboarding } from '@src/types';
import { CollapsedSectionItem } from '../../Item/CollapsedSectionItem/CollapsedSectionItem';
import InviteTeamPopover from '../InviteTeamPopover';
import InviteTeamPopoverComponent from '../InviteTeamPopover/InviteTeamPopover';
import TeamDisclosurePopover from '../TeamDisclosurePopover/TeamDisclosurePopover';
import { NavbarTab } from '@src/types/leftSidebar';

import './CollapsedTeamSection.scss';
import { checkNextHotkeyChange, HotkeyAction } from '@src/lib/hotkeys';

export interface StateProps {
  collapsed: boolean;
  team?: Team;
  selectedTab: NavbarTab;
  features: Features;
  mentionsCount?: number;
  userMentionsCount?: number;

  hotkeyActions?: { [key: string]: number };
  showMentionsOnboarding?: boolean;
  showTeamInviteOnboarding?: boolean;
  showUnsubscriberOnboarding?: boolean;
  onboarding: Onboarding;
}

export interface DispatchProps {
  collapse: (collapsedState: boolean) => void;
  updateOnboarding: (type: OnboardingType) => void;
}

interface State {
  showPopover: boolean;
}

class CollapsedTeamSection extends React.PureComponent<StateProps & DispatchProps, State> {
  popover: InviteTeamPopoverComponent;
  disclosurePopover: TeamDisclosurePopover;

  constructor() {
    super();
    this.state = {
      showPopover: false,
    };
  }

  componentDidUpdate(oldProps: StateProps & DispatchProps) {
    const oldActions = oldProps.hotkeyActions || {};
    const newActions = this.props.hotkeyActions || {};
    this.handleHotkeyActionsChange(oldActions, newActions);
  }

  handleHotkeyActionsChange = (
    oldActions: { [action: string]: number },
    newActions: { [action: string]: number },
  ) => {
    // Handle CC toggle
    if (checkNextHotkeyChange(HotkeyAction.InviteTeamMembers, newActions, oldActions)) {
      const teamElement = ReactDOM.findDOMNode(this);
      const top = teamElement && (teamElement as Element).getBoundingClientRect().top + 100 || 0;
      this.handleShowPopover(top);
    }
  }

  toggleCollapse = (e: React.SyntheticEvent<HTMLButtonElement>) => {
    const { collapse, collapsed } = this.props;

    e.preventDefault();
    e.stopPropagation();

    collapse(!collapsed);
  }

  handleRef = (ref: any) => {
    this.popover = ref && ref.getWrappedInstance();
  }

  handleShowPopoverClick = (e: React.MouseEvent<HTMLElement>) => {
    const target = e.currentTarget;
    const boundingBox = target && target.getBoundingClientRect && target.getBoundingClientRect();

    if (boundingBox) {
      this.handleShowPopover(boundingBox.bottom);
    }
  }

  handleShowPopover = (top: number) => {
    this.setState({ showPopover: true }, () => {
      this.popover.show(top);
    });
  }

  handleTeamPopoverHide = () => {
    const { showTeamInviteOnboarding, updateOnboarding } = this.props;
    // Dismiss team invite onboarding if clicked
    if (showTeamInviteOnboarding) {
      updateOnboarding(OnboardingType.InviteTeamMembers);
    }

    this.setState({ showPopover: false });
  }

  handleMentionsClick = () => {
    const { showMentionsOnboarding, updateOnboarding } = this.props;
    if (showMentionsOnboarding) {
      updateOnboarding(OnboardingType.MentionsTab);
    }
  }

  handleDisclosurePopover = (ref: TeamDisclosurePopover) => {
    this.disclosurePopover = ref;
  }

  showDisclosurePopover = (e: React.MouseEvent<HTMLElement>) => {
    const target = e.currentTarget;
    const boundingBox = target && target.getBoundingClientRect && target.getBoundingClientRect();

    if (boundingBox) {
      e.stopPropagation();
      e.preventDefault();
      this.disclosurePopover.show(boundingBox.bottom);
    }
  }

  render() {
    const {
      collapsed,
      team,
      selectedTab,
      mentionsCount,
      features,
      showMentionsOnboarding,
    } = this.props;

    interface ItemFormat {
      key: string;
      name: string;
      link?: string;
      onClick: any;
      icon: JSX.Element;
      hidden: boolean;
      count: number;
      isOnboarding: boolean|undefined;
      notificationCount: boolean;
    }

    // TOOD(SHIN): Implement OnboardingGem for Shared by Me
    // TODO(SHIN): Determine if we want these in collapsed mode
    let items: ItemFormat[] = [];

    // Add Mentions if comments is enabled
    if (features.comments) {
      const commentItem: ItemFormat[] = [{
        key: 'mentions',
        name: 'Mentions',
        link: '/mentions',
        icon: <Mentions />,
        onClick: this.handleMentionsClick,
        hidden: false,
        count: mentionsCount || 0,
        isOnboarding: showMentionsOnboarding,
        notificationCount: true,
      }];

      items = commentItem.concat(items);
    }

    if (!items.length) {
      return null;
    }

    return (
      <div className="collapsed-team-section">
        {collapsed && items
          .filter(i => !i.hidden)
          .map(i => (
            <CollapsedSectionItem
              key={i.key}
              name={i.name}
              count={i.count}
              className={i.key.toLowerCase()}
              icon={i.icon}
              selected={selectedTab.toLowerCase() === i.key.toLowerCase()}
              link={i.link}
              onClick={i.onClick}
              isOnboarding={i.isOnboarding}
              notificationCount={i.notificationCount}
            />
          ))}
        {this.state.showPopover &&
          <InviteTeamPopover
            ref={this.handleRef}
            team={team}
            onHide={this.handleTeamPopoverHide}
          />
        }
      </div>
    );
  }
}

export default CollapsedTeamSection;
