import React from 'react';
import moment from 'moment-timezone';
import { DraftCalendarInvite, Contact } from '@src/types';

import './CalendarInvite.scss';

interface Props {
  event: DraftCalendarInvite;
  noBranding: boolean;
  contacts: Contact[];
}

const CalendarInvite: React.SFC<Props> = ({ event, noBranding, contacts }) => {
  const timezone = moment.tz.guess();
  const startString = moment(event.start).format('ddd, MMM D [at] h:mma');
  const endString = moment(event.end).format('h:mma');
  const timezoneString = moment.tz.zone(timezone)!.abbr(moment().valueOf());
  const timeString = `${startString} (${timezoneString}) to ${endString} (${timezoneString})`;

  return (
    <div
      className="pm-calendar-invite"
      contentEditable={false}
      style={{
        width: '100%',
        maxWidth: '310px',
        fontFamily:
          '-apple-system, "Helvetica Neue", Helvetica,' +
          ' "Lucida Grande", -sans-serif !important',
        fontSize: '12px !important',
        boxSizing: 'border-box',
        padding: '0px',
        margin: '0px',
        background: 'white',
      }}
    >
      <div
        style={{
          boxSizing: 'border-box',
          padding: '12px 12px',
          borderRadius: '5px 5px 0px 0px',
          background: '#00aaff',
          color: 'white',
          width: '100%',
        }}
      >
        <div>
          <div
            style={{
              width: '90%',
              display: 'inline-block',
            }}
          >
            <div
              style={{
                fontSize: '16px',
                marginBottom: '4px',
                fontWeight: 700,
                lineHeight: '1.25',
              }}
            >
              <b>{event.name}</b>
            </div>
          </div>
          <div
            className="pm-cal-invite-close-container"
            style={{ verticalAlign: 'top', float: 'right' }}
          />
        </div>
        <div>
          <div
            style={{
              display: 'block',
              whiteSpace: 'nowrap',
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              wordWrap: 'break-word',
              fontSize: '13px',
              fontWeight: 500,
              lineHeight: '1.23',
            }}
          >
            {timeString}
          </div>
        </div>
      </div>
      <div
        style={{
          boxSizing: 'border-box',
          padding: '12px 18px',
          borderLeft: '1px solid #EEEEEE',
          borderRight: '1px solid #EEEEEE',
          color: '#7D8FA0',
          lineHeight: '1.2',
          borderBottom: noBranding ? '1px solid #EEEEEE' : 'none',
          borderRadius: noBranding ? '0px 0px 5px 5px' : '0px 0px 0px 0px',
        }}
      >
        <table style={{ fontSize: '13px !important' }}>
          <tbody>
            <tr>
              <td
                style={{
                  width: '20px',
                  margin: '0px',
                  padding: '0px',
                  verticalAlign: 'top',
                }}
              >
                <img
                  src={`${process.env.API_URL}/v2/img/calendar-invite/location@3x.png`}
                  alt="Location Icon"
                  style={{ width: '12px', height: '16px' }}
                />
              </td>
              <td style={{ margin: '0px', padding: '0px 0px 0px 9px', wordBreak: 'break-all' }}>
                {event.location}
              </td>
            </tr>
            <tr>
              <td style={{ width: '20px', padding: '10px 0px 0px 0px', verticalAlign: 'top' }}>
                <img
                  src={`${process.env.API_URL}/v2/img/calendar-invite/recipients@3x.png`}
                  alt="Attendees Icon"
                  style={{ width: '16px', height: '13px' }}
                />
              </td>
              <td style={{ margin: '0px', padding: '5px 0px 0px 9px' }}>
                <div
                  style={{
                    fontSize: '13px',
                    lineHeight: '1.45',
                    color: '#7e8f9f',
                    padding: '5px 0',
                  }}
                >
                  {`${event.recipients.length} guest${event.recipients.length > 1 ? 's' : ''}
                   to invite`}
                </div>
                {!contacts.length &&
                  event.recipients.map(r => (
                    <div
                      key={r}
                      style={{
                        fontSize: '14px',
                        color: '#444444',
                      }}
                    >
                      {r}
                    </div>
                  ))}
                {!!contacts.length &&
                  contacts.map(c => (
                    <div
                      key={c.email}
                      style={{
                        fontSize: '14px',
                        color: '#444444',
                        margin: '5px 0',
                      }}
                    >
                      <img
                        src={c.photoUrl}
                        style={{
                          width: '15px',
                          height: '15px',
                          borderRadius: '50%',
                          marginRight: '7px',
                        }}
                      />
                      <div
                        style={{
                          lineHeight: '15px',
                          verticalAlign: 'top',
                          display: 'inline-block',
                        }}
                      >
                        {c.fullName ? c.fullName : c.email}
                      </div>
                    </div>
                  ))}
              </td>
            </tr>
            {event.notes && (
              <tr>
                <td
                  style={{
                    width: '20px',
                    height: '20px',
                    margin: '0px',
                    padding: '10px 0px 0px 0px',
                    verticalAlign: 'top',
                  }}
                >
                  <img
                    src={`${process.env.API_URL}/v2/img/calendar-invite/note@3x.png`}
                    alt="Note Icon"
                    style={{ width: '16px', height: '16px' }}
                  />
                </td>
                <td
                  style={{
                    margin: '0px',
                    padding: '5px 0px 0px 9px',
                    fontSize: '13px',
                    whiteSpace: 'pre-line',
                  }}
                >
                  {event.notes}
                </td>
              </tr>
            )}
          </tbody>
        </table>
        <div style={{ textAlign: 'center', padding: '10px 0px 0px 0px', fontSize: '12px' }}>
          You'll receive an invite for this event shortly.
        </div>
      </div>
      {!noBranding && (
        <div
          style={{
            boxSizing: 'border-box',
            padding: '0px 10px 10px 10px',
            borderLeft: '1px solid #EEEEEE',
            borderBottom: '1px solid #EEEEEE',
            borderRight: '1px solid #EEEEEE',
            borderRadius: '0px 0px 5px 5px',
            textAlign: 'right',
            fontSize: '10px',
            color: '#7D8FA0',
            verticalAlign: 'top',
          }}
        >
          <a
            href={
              'https://polymail.io/?utm_source=polymail&' +
              'utm_medium=referral&utm_campaign=cal-invite'
            }
            style={{ textDecoration: 'none', color: '#7D8FA0' }}
          >
            Scheduled with Polymail
          </a>
          <a
            href={
              'https://polymail.io/?utm_source=polymail&' +
              'utm_medium=referral&utm_campaign=cal-invite'
            }
          >
            <img
              src={`${process.env.API_URL}/v1/img/calendar-invite/logo@3x.png`}
              alt="Polymail Logo"
              style={{
                width: '16px',
                height: '16px',
                marginLeft: '9px',
              }}
            />
          </a>
        </div>
      )}
    </div>
  );
};

export default CalendarInvite;
